INSERT INTO `article` (`id_article`, `title`, `image`, `brief_content`, `content`, `created_date`, `modified_date`, `id_category`, `id_author`) VALUES
(47, 'Mikel Arteta được tiến cử làm HLV của Arsenal', 'skysports-arteta-manchester-ci-4327-6118-1524525909.jpg', 'Giám đốc điều hành Ivan Gazidis tin trợ lý của Pep Guardiola tại Man City là phương án lý tưởng thay Arsene Wenger.', '<h2>Gi&aacute;m đốc điều h&agrave;nh&nbsp;Ivan Gazidis tin trợ l&yacute; của Pep Guardiola tại Man City l&agrave; phương &aacute;n l&yacute; tưởng thay Arsene Wenger.</h2>\n\n<p><a href=\"https://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/wenger-tu-choi-tiet-lo-ly-do-roi-arsenal-3740275.html?ctr=related_news_click\">Wenger từ chối tiết lộ l&yacute; do rời Arsenal</a>&nbsp;/&nbsp;<a href=\"https://thethao.vnexpress.net/tin-tuc/giai-ngoai-hang-anh/patrick-vieira-chua-nghi-toi-chuyen-ke-nhiem-wenger-3740037.html?ctr=related_news_click\">Patrick Vieira chưa nghĩ tới chuyện kế nhiệm Wenger</a></p>\n\n<p><em>Sky Sports</em>&nbsp;cho biết, Ivan Gazidis đ&atilde; nhấn mạnh đến t&iacute;nh nhất qu&aacute;n v&agrave; việc giữ g&igrave;n truyền thống Arsenal trong việc t&igrave;m người thay Arsene Wenger. Gi&aacute;m đốc điều h&agrave;nh Arsenal đ&aacute;nh gi&aacute; cao Mikel Arteta, cựu đội trưởng Arsenal, d&ugrave; trợ l&yacute; của Pep Guardiola chưa c&oacute; kinh nghiệm huấn luyện.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Arteta được Giám đốc điều hành Gazidis đánh giá cao. Ảnh: PA.\" src=\"https://i-thethao.vnecdn.net/2018/04/24/skysports-arteta-manchester-ci-4327-6118-1524525909.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Arteta được Gi&aacute;m đốc điều h&agrave;nh Gazidis đ&aacute;nh gi&aacute; cao. Ảnh:&nbsp;<em>PA.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Quan điểm của Gazidis được đưa ra&nbsp;trong buổi họp với 200 nh&acirc;n vi&ecirc;n Arsenal h&ocirc;m 23/4. &Ocirc;ng đ&atilde; tham khảo &yacute; kiến của &ocirc;ng chủ Stan Kroenke, v&agrave; th&ocirc;ng b&aacute;o rằng c&ocirc;ng cuộc t&igrave;m người thay Arsene Wenger từ m&ugrave;a 2018-2019 đ&atilde; được k&iacute;ch hoạt.&nbsp;</p>\n\n<p>Gazidis c&ugrave;ng những nh&acirc;n vật đứng đầu ban huấn luyện, gồm Gi&aacute;m đốc thể thao Raul Sanllehi v&agrave; Trưởng bộ phận tuyển trạch Sven Mislintat sẽ bắt đầu thảo luận về c&aacute;c ứng vi&ecirc;n trong tuần n&agrave;y.&nbsp;Tuy nhi&ecirc;n, Arsenal tr&aacute;nh tiếp cận c&aacute;c ứng vi&ecirc;n tiềm năng cho đến trước khi Wenger th&ocirc;ng b&aacute;o rời CLB v&agrave;o h&ocirc;m thứ S&aacute;u 20/4, nhằm giữ sự t&ocirc;n trọng cho nh&agrave; cầm qu&acirc;n người Ph&aacute;p.&nbsp;</p>\n\n<p>Arteta c&oacute; 5 năm cống hiến cho Arsenal, v&agrave; được Wenger chỉ định l&agrave;m đội trưởng trong hai m&ugrave;a cuối c&ugrave;ng ở s&acirc;n Emirates.&nbsp;Cựu danh thủ người T&acirc;y Ban Nha gia nhập ban huấn luyện Man City, c&ugrave;ng với Brian Kidd v&agrave; Domenec Torrent từ h&egrave; 2016, khi Guardiola đến s&acirc;n Etihad.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Enrique cũng là ứng viên sáng giá thay Wenger. Ảnh: Reuters.\" src=\"https://i-thethao.vnecdn.net/2018/04/24/skysports-luis-enrique-footbal-4824-4222-1524525909.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Enrique cũng l&agrave; ứng vi&ecirc;n s&aacute;ng gi&aacute; thay Wenger. Ảnh:&nbsp;<em>Reuters.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Gi&aacute;m đốc thể thao của Arsenal,&nbsp;Sanllehi, từng giữ chức vụ tương tự ở Barca th&igrave; đ&aacute;nh gi&aacute; cao Luis Enrique sau khi c&oacute; thời gian l&agrave;m việc chung ở s&acirc;n Nou Camp. Trong c&aacute;c ứng vi&ecirc;n, bảng th&agrave;nh t&iacute;ch của Enrique cũng ấn tượng nhất, với một chức v&ocirc; địch Champions League v&agrave; hai danh hiệu La Liga.</p>\n\n<p>Trưởng bộ phận tuyển trạch Mislintat lại đề cử HLV của Hoffenheim, Julian Nagelsmann v&agrave; người dẫn dắt Schalke, Domenico Tedesco. Thomas Tuchel, người từng được nhắc đến trong danh s&aacute;ch tiềm năng c&aacute;ch đ&acirc;y v&agrave;i th&aacute;ng, c&oacute; vẻ như sẽ gia nhập PSG thay Unai Emery.</p>\n\n<p>Cũng theo&nbsp;<em>Sky Sports</em>, Brendan Rodgers v&agrave; Rafael Benitez kh&ocirc;ng lọt v&agrave;o mắt xanh của giới ch&oacute;p bu tại Arsenal.</p>\n\n<p><strong>Thắng Nguyễn</strong></p>\n', '1524530544', '', 4, 0),
(48, 'Modric: \'Nếu muốn ở lại Real, cầu thủ phải có đẳng cấp cao\'', 'modric2-5559-1524469089.jpg', 'Tiền vệ người Croatia đánh giá về độ khó khi muốn trở thành một cầu thủ của Real Madrid.', '<h2>Tiền vệ người Croatia đ&aacute;nh gi&aacute; về độ kh&oacute; khi muốn trở th&agrave;nh một cầu thủ của Real Madrid.</h2>\n\n<p><a href=\"https://thethao.vnexpress.net/tin-tuc/champions-league/chu-tich-perez-bayern-rat-manh-nhung-real-se-vo-dich-mot-lan-nua-3740382.html?ctr=related_news_click\">Chủ tịch Perez: &#39;Bayern rất mạnh, nhưng Real sẽ v&ocirc; địch một lần nữa&#39;</a>&nbsp;/&nbsp;<a href=\"https://thethao.vnexpress.net/tin-tuc/la-liga/bild-lewandowski-tiet-lo-voi-dong-doi-se-sang-real-trong-he-2018-3739556.html?ctr=related_news_click\">Bild: &#39;Lewandowski tiết lộ với đồng đội sẽ sang Real trong h&egrave; 2018&#39;</a></p>\n\n<p>L&agrave; đội v&ocirc; địch Champions League hai năm gần nhất, Real lu&ocirc;n đ&ograve;i hỏi phong độ tốt từ c&aacute;c cầu thủ. Ngay cả Luka Modric, c&ocirc;ng thần chơi cho đội từ năm 2012, cũng kh&ocirc;ng phải ngoại lệ.&nbsp;&quot;Tại Real, bạn phải chơi ở đẳng cấp cao trong mọi trận đấu. Nếu kh&ocirc;ng th&igrave; sẽ rất kh&oacute; ở lại&quot;, tiền vệ 32 tuổi cho biết.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Modric đang thi đấu rất hiệu quả trong màu áo Real. Ảnh: Marca\" src=\"https://i-thethao.vnecdn.net/2018/04/23/modric2-5559-1524469089.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Modric đang&nbsp;thi đấu rất hiệu quả trong m&agrave;u &aacute;o Real. Ảnh:&nbsp;<em>Marca</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Chuẩn bị thi đấu b&aacute;n kết Champions League với Bayern, Real đang c&oacute; cơ hội đăng quang năm thứ ba li&ecirc;n tiếp. Đ&acirc;y cũng l&agrave; mục ti&ecirc;u m&agrave; họ dồn to&agrave;n lực sau khi thất bại ở La Liga v&agrave; Cup Nh&agrave; vua.&nbsp;&quot;Ch&uacute;ng t&ocirc;i l&agrave; đội đầu ti&ecirc;n bảo vệ được Champions League. Điều đ&oacute; thực sự tuyệt vời. Ch&uacute;ng t&ocirc;i đ&atilde; đ&aacute;nh bại những ứng cử vi&ecirc;n v&ocirc; địch. Ch&uacute;ng t&ocirc;i đang c&oacute; cảm gi&aacute;c tốt v&agrave; cũng chơi tốt&quot;,&nbsp;Modric tự tin.</p>\n\n<p>Đang ở giai đoạn cuối của sự nghiệp, Modric c&oacute; thể phải t&iacute;nh phương &aacute;n ra đi nếu kh&ocirc;ng c&ograve;n giữ được phong độ. Tiền vệ người Croatia cũng kh&ocirc;ng loại trừ khả năng chuyển tới Mỹ thi đấu như Zlatan Ibrahimovic. Anh n&oacute;i:&nbsp;&quot;Bạn kh&ocirc;ng bao giờ biết được điều g&igrave; sẽ xảy ra. T&ocirc;i vẫn c&ograve;n hai năm hợp đồng với Real v&agrave; t&ocirc;i đang hạnh ph&uacute;c ở đ&acirc;y. T&ocirc;i cũng muốn kết th&uacute;c sự nghiệp tại Real. Nhưng nếu kh&ocirc;ng thể, c&oacute; lẽ một ng&agrave;y n&agrave;o đ&oacute; t&ocirc;i sẽ chơi ở Mỹ. T&ocirc;i đang t&iacute;nh từng bước một&quot;.</p>\n\n<p>Kết th&uacute;c m&ugrave;a giải cấp CLB, Modric sẽ bắt tay ngay v&agrave;o việc c&ugrave;ng tuyển Croatia dự World Cup 2018. Croatia nằm ở bảng đấu c&ugrave;ng Argentina, Nigeria v&agrave; Iceland. Anh&nbsp;nhận x&eacute;t: &quot;Ch&uacute;ng t&ocirc;i đang c&oacute; một đội b&oacute;ng với rất nhiều t&agrave;i năng. Đối với một đất nước nhỏ b&eacute;, thật vui khi c&oacute; nhiều VĐV t&agrave;i năng trong nhiều m&ocirc;n thể thao. T&ocirc;i cũng cảm thấy vinh dự khi l&agrave; thủ qu&acirc;n của đội tuyển b&oacute;ng đ&aacute;&quot;.</p>\n\n<p><strong>Thanh Qu&yacute;</strong></p>\n', '1524531001', '', 4, 0),
(49, 'Ronaldo đánh gót ghi bàn, cứu thua cho Real tại Bernabeu', 'bilbao-5377-1524095339.jpg', 'Pha lập công muộn màng của CR7 giúp Real Madrid hòa 1-1 trong trận tiếp Bilbao ở vòng 33 La Liga, tối 18/4.', '<h2>Pha lập c&ocirc;ng muộn m&agrave;ng của CR7 gi&uacute;p Real Madrid h&ograve;a 1-1 trong trận tiếp Bilbao ở v&ograve;ng 33 La Liga, tối 18/4.</h2>\n\n<p>&nbsp;00:54| 03:20</p>\n\n<p><em>B&agrave;n thắng: Ronaldo 87&#39; - Inaki Williams 14&#39;</em></p>\n\n<p>Sau khi cho nhiều trụ cột nghỉ ở trận đấu với Malaga cuối tuần trước, HLV Zinedine Zidane tung ra s&acirc;n đội h&igrave;nh mạnh nhất khi trở về s&acirc;n nh&agrave; Bernabeu. Cristiano Ronaldo v&agrave; Karim Benzema l&agrave; hai cầu thủ đ&aacute; cặp tr&ecirc;n h&agrave;ng c&ocirc;ng, c&ograve;n Luka Modric, Toni Kroos, Marco Asensio v&agrave; Lucas Vazquez hỗ trợ ph&iacute;a sau.&nbsp;Real nhờ đ&oacute; nhanh ch&oacute;ng g&acirc;y được sức &eacute;p l&ecirc;n phần s&acirc;n của Bilbao. Ph&uacute;t thứ 9, Ronaldo c&oacute; pha bay người đ&aacute;nh đầu đập x&agrave;. Kh&ocirc;ng l&acirc;u sau, vẫn l&agrave; ng&ocirc;i sao người Bồ Đ&agrave;o Nha tạo ra t&igrave;nh huống nguy hiểm kh&aacute;c, khi đi b&oacute;ng rồi cứa l&ograve;ng chệch cột.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Williams lốp bóng mở tỷ số cho Bilbao.\" src=\"https://i-thethao.vnecdn.net/2018/04/19/bilbao-5377-1524095339.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Williams lốp b&oacute;ng mở tỷ số cho Bilbao.</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Tuy nhi&ecirc;n Bilbao cũng tỏ ra kh&ocirc;ng dễ bị bắt nạt. Sau một số pha l&ecirc;n b&oacute;ng đ&aacute;p trả, đội kh&aacute;ch thậm ch&iacute; c&ograve;n mở tỷ số ở ph&uacute;t 14. Từ pha chọc khe của đồng đội, Inaki Williams băng xuống lốp b&oacute;ng kỹ thuật đ&aacute;nh bại thủ m&ocirc;n Keylor Navas.</p>\n\n<p>B&agrave;n thua sớm khiến Real phải thi đấu tập trung hơn. D&ugrave; vậy, vượt qua h&agrave;ng thủ Bilbao v&agrave; thủ m&ocirc;n Kepa kh&ocirc;ng phải l&agrave; nhiệm vụ dễ d&agrave;ng. Cuối hiệp một, Kepa c&oacute; hai pha cứu thua xuất sắc khi đối mặt với c&uacute; s&uacute;t ch&igrave;m trong v&ograve;ng cấm của Ronaldo v&agrave; c&uacute; v&ocirc;-l&ecirc; từ xa của Marcelo.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Những pha tấn công của Real thường thiếu một chút chính xác trong khâu dứt điểm.\" src=\"https://i-thethao.vnecdn.net/2018/04/19/asen-8077-1524095339.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Những pha tấn c&ocirc;ng của Real thường thiếu một ch&uacute;t ch&iacute;nh x&aacute;c trong kh&acirc;u dứt điểm.</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Diễn biến trong hiệp hai kh&ocirc;ng kh&aacute;c nhiều. Real chơi tốt hơn nhưng kh&ocirc;ng l&agrave;m g&igrave; được h&agrave;ng thủ Bilbao. Thủ m&ocirc;n Kepa tiếp tục đứng vững trước những pha dứt điểm của Ronaldo, Modric v&agrave; Asensio.</p>\n\n<p>Đứng trước nguy cơ thua trận, HLV Zidane phải tung Isco v&agrave; Bale v&agrave;o từ ph&uacute;t 69. Sự c&oacute; mặt của hai chuy&ecirc;n gia đi b&oacute;ng gi&uacute;p c&aacute;c pha h&atilde;m th&agrave;nh của Real trở n&ecirc;n đa dạng hơn. V&agrave; cuối c&ugrave;ng, những&nbsp;nỗ lực của đội chủ nh&agrave; cũng tạo ra kết quả ở ph&uacute;t 87. Từ c&uacute; s&uacute;t ch&eacute;o g&oacute;c v&agrave;o v&ograve;ng cấm của Modric, Ronaldo đ&aacute;nh g&oacute;t khiến b&oacute;ng đổi hướng đi v&agrave;o lưới.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Ronaldo một lần nữa đóng vai cứu tinh cho Real. Ảnh: Reuters.\" src=\"https://i-thethao.vnecdn.net/2018/04/19/3-1165-1524104145.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Ronaldo một lần nữa đ&oacute;ng vai cứu tinh cho Real. Ảnh:<em>&nbsp;Reuters.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Kh&ocirc;ng để thua tr&ecirc;n s&acirc;n nh&agrave;, Real giữ vững vị tr&iacute; số ba La Liga với 68 điểm. Nh&agrave; đương kim v&ocirc; địch hiện hơn ba điểm so với đội xếp thứ tư Valencia, đồng thời k&eacute;m ba điểm so với đội xếp thứ hai Atletico - đội đang chơi &iacute;t hơn một trận.</p>\n\n<p>Do khoảng c&aacute;ch k&eacute;m Barca hiện tại l&agrave; 15 điểm (trước năm trận cuối), Real gần như trở th&agrave;nh cựu v&ocirc; địch. Điều n&agrave;y sẽ xảy ra khi Real mất th&ecirc;m điểm, hoặc Barca h&ograve;a hay thắng th&ecirc;m một trận.</p>\n\n<p><strong>Thanh Qu&yacute;</strong></p>\n', '1524531109', '', 4, 0),
(50, 'Ronaldo tái hiện chuỗi trận ghi bàn nhiều nhất ở Real', 'rona2-2047-1524130021.jpg', 'Với pha đánh gót vào lưới Bilbao tối qua 18/4, siêu sao của Real Madrid đang có chuỗi 12 trận ghi bàn liên tiếp trên mọi đấu trường.', '<h2>Với pha đ&aacute;nh g&oacute;t v&agrave;o lưới Bilbao tối qua 18/4, si&ecirc;u sao của Real Madrid đang c&oacute; chuỗi 12 trận ghi b&agrave;n li&ecirc;n tiếp tr&ecirc;n mọi đấu trường.</h2>\n\n<p><a href=\"https://thethao.vnexpress.net/tin-tuc/champions-league/chu-tich-uefa-var-cung-vo-dung-doi-voi-tran-real-juventus-3738799.html?ctr=related_news_click\">Chủ tịch UEFA: &#39;VAR cũng v&ocirc; dụng đối với trận Real - Juventus&#39;</a>&nbsp;/&nbsp;<a href=\"https://thethao.vnexpress.net/tin-tuc/la-liga/ronaldo-danh-got-ghi-ban-cuu-thua-cho-real-tai-bernabeu-3738692.html?ctr=related_news_click\">Ronaldo đ&aacute;nh g&oacute;t ghi b&agrave;n, cứu thua cho Real tại Bernabeu</a></p>\n\n<p>Trong những năm đầu chơi cho Real Madrid, Cristiano Ronaldo từng l&agrave;m n&ecirc;n th&agrave;nh t&iacute;ch tốt nhất l&agrave; chuỗi 12 trận ghi b&agrave;n li&ecirc;n tiếp. V&agrave; ở tuổi 33, theo tờ&nbsp;<em>Marca</em>, anh c&agrave;ng c&oacute; t&iacute;nh thuyết phục hơn khi lập lại phong độ đỉnh cao đ&oacute;.</p>\n\n<p>Trong 12 trận vừa qua, Ronaldo ghi tổng cộng 22 b&agrave;n. Cụ thể l&agrave; ở trận gặp Real Sociedad (3 b&agrave;n), PSG (2), Real Betis (1), Deportivo Alaves (2), Getafe (2), PSG (1), Eibar (2), Girona (4), Juventus (2), Atletico Madrid (1), Juventus (1) v&agrave; Bilbao (1).</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Ronaldo cứ vào sân là ghi bàn trong 12 trận vừa qua. Ảnh: Reuters\" src=\"https://i-thethao.vnecdn.net/2018/04/19/rona2-2047-1524130021.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Ronaldo cứ v&agrave;o s&acirc;n l&agrave; ghi b&agrave;n trong 12 trận vừa qua. Ảnh:&nbsp;<em>Reuters.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Chuỗi trận tr&ecirc;n kh&ocirc;ng liền mạch trong lịch đấu của Real. Do t&iacute;nh chất một số trận kh&ocirc;ng qu&aacute; quan trọng, hoặc để giữ sức, HLV Zinedine Zidane quyết định cho cậu học tr&ograve; cưng ngồi ngo&agrave;i.</p>\n\n<p>Ronaldo đ&atilde; c&oacute; 24 b&agrave;n từ đầu m&ugrave;a La Liga 2017-2018. Con số n&agrave;y chỉ k&eacute;m 29 b&agrave;n của Lionel Messi. Với việc giải c&ograve;n năm v&ograve;ng nữa, anh vẫn c&ograve;n kh&ocirc;ng &iacute;t cơ hội vượt l&ecirc;n.</p>\n\n<p>Nhờ Ronaldo đang c&oacute; phong độ cao nhất, Real đặt to&agrave;n bộ hy vọng v&agrave;o Champions League. Giữa tuần sau, nh&agrave; đương kim v&ocirc; địch sẽ l&agrave;m kh&aacute;ch tr&ecirc;n s&acirc;n của Bayern Munich trong trận b&aacute;n kết lượt đi.</p>\n\n<p><strong>Thanh Qu&yacute;</strong></p>\n', '1524531167', '', 4, 0),
(51, 'Nadal áp đảo Nishikori, lần thứ 11 vô địch Monte Carlo', 'tennis-1725-1524410131_500x300.jpg', 'Tay vợt Tây Ban Nha thắng 6-3, 6-2 trong trận chung kết chiều 22/4, qua đó giành danh hiệu Masters thứ 31.', '<h2>Tay vợt T&acirc;y Ban Nha thắng 6-3, 6-2 trong trận chung kết chiều 22/4, qua đ&oacute; gi&agrave;nh danh hiệu Masters thứ 31.</h2>\n\n<p><a href=\"https://thethao.vnexpress.net/tin-tuc/tennis/nadal-ha-dimitrov-lan-thu-12-vao-chung-ket-monte-carlo-3740005.html?ctr=related_news_click\">Nadal hạ Dimitrov, lần thứ 12 v&agrave;o chung kết Monte Carlo</a>&nbsp;/&nbsp;<a href=\"https://thethao.vnexpress.net/tin-tuc/tennis/nadal-than-toc-ha-thiem-trong-mot-gio-vao-ban-ket-monte-carlo-3739700.html?ctr=related_news_click\">Nadal thần tốc hạ Thiem trong một giờ, v&agrave;o b&aacute;n kết Monte Carlo</a></p>\n\n<p>Thắng lợi n&agrave;y gi&uacute;p Rafael Nadal củng cố vị tr&iacute; số một thế giới, đứng tr&ecirc;n Roger Federer, đồng thời trở th&agrave;nh tay vợt nam đầu ti&ecirc;n trong kỷ nguy&ecirc;n Mở v&ocirc; địch một giải đấu 11 lần.</p>\n\n<p>13 năm sau lần đăng quang đầu ti&ecirc;n, Nadal thể hiện anh vẫn sẵn s&agrave;ng đ&aacute;nh bại bất kỳ đối thủ n&agrave;o tr&ecirc;n mặt s&acirc;n đất nện tại Monaco.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Nadal tiếp tục thể hiện sự thống trị tại Monte Carlo. Ảnh: AFP.\" src=\"https://i-thethao.vnecdn.net/2018/04/22/Untitled-1725-1524410131.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Nadal tiếp tục thể hiện sự thống trị tại Monte Carlo. Ảnh:&nbsp;<em>AFP</em>.</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Kei Nishikori khởi đầu trận đấu một c&aacute;ch hứa hẹn, d&ugrave; phải mất 11 ph&uacute;t để thắng game giao b&oacute;ng đầu ti&ecirc;n. Anh sau đ&oacute; bẻ break để vượt l&ecirc;n dẫn 2-1, v&agrave; nhen nh&oacute;m hy vọng lần đầu đăng quang danh hiệu Masters 1000.</p>\n\n<p>Tuy nhi&ecirc;n, Nadal kh&ocirc;ng mất nhiều thời gian để chiếm lại thế thượng phong, bằng c&aacute;ch thắng bốn game li&ecirc;n tiếp. Ở game 7, c&aacute;c kh&aacute;n giả đ&atilde; d&agrave;nh cho Nishikori những tr&agrave;ng vỗ tay kh&iacute;ch lệ khi tay vợt Nhật Bản dẫn 30-15 trong game giao b&oacute;ng của Nadal. Điều n&agrave;y lặp lại khi Nishikori thực hiện một c&uacute; v&ocirc;-l&ecirc; tr&ecirc;n lưới để từ chối cơ hội ghi điểm set đầu ti&ecirc;n của đối thủ. Nhưng Nadal đ&atilde; kh&ocirc;ng mắc sai lầm ở cơ hội thứ hai.</p>\n\n<p>Tại Monte Carlo năm nay, Nadal tiếp tục thể hiện phong độ rất cao khi kh&ocirc;ng thua set n&agrave;o từ đầu giải. D&ugrave; v&agrave;o chung kết, giới chuy&ecirc;n m&ocirc;n đ&aacute;nh gi&aacute; Nishikori kh&oacute; c&oacute; cơ hội ngăn bước &quot;&Ocirc;ng vua s&acirc;n đất nện,&quot; v&agrave; diễn biến set 2 tiếp tục chứng tỏ điều đ&oacute;.</p>\n\n<p>&nbsp;00:03| 04:11</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>Sau khi vất vả thắng game giao b&oacute;ng đầu ti&ecirc;n, Nishikori hai lần li&ecirc;n tiếp bị bẻ break. Đ&acirc;y l&agrave; một set đấu dễ d&agrave;ng cho Nadal v&igrave; anh kh&ocirc;ng gặp bất cứ th&aacute;ch thức n&agrave;o khi cầm giao. Tay vợt người T&acirc;y Ban Nha &aacute;p đảo v&agrave; khiến đối thủ phải li&ecirc;n tục di chuyển để đ&oacute;n những c&uacute; đ&aacute;nh của anh.&nbsp;</p>\n\n<p>Sau khi để Nadal thắng 5 game li&ecirc;n tiếp, Nishikori cắt được chuỗi ghi điểm của đối thủ. Tuy nhi&ecirc;n, nhiệm vụ phải gi&agrave;nh break ở game tiếp theo để ngăn Nadal v&ocirc; địch trở n&ecirc;n qu&aacute; kh&oacute; với tay vợt Nhật Bản. Nadal thắng trắng game quyết định, v&agrave; kết th&uacute;c trận đấu.</p>\n\n<p>Với 31 chức v&ocirc; địch, Nadal một m&igrave;nh giữ kỷ lục tay vợt sở hữu danh hiệu Masters 1000 nhiều nhất - xếp ngay dưới l&agrave; Novak Djokovic (30). Chiến thắng n&agrave;y cũng n&acirc;ng tổng số danh hiệu của anh l&ecirc;n 76.&nbsp;</p>\n\n<p><strong>Vĩnh San</strong></p>\n', '1524531415', '', 4, 0),
(52, '10 đại học đẹp nhất khu vực Đông Á', '1-peking-university-1524452615_680x0.jpg', 'Đại học Bắc Kinh, trường Công nghệ Nanyang và Kyung Hee được THE Students bình chọn là trường có khuôn viên xanh và kiến trúc độc đáo.', '<h2>Đại học Bắc Kinh, trường C&ocirc;ng nghệ Nanyang v&agrave; Kyung Hee được THE Students b&igrave;nh chọn l&agrave; trường c&oacute; khu&ocirc;n vi&ecirc;n xanh v&agrave; kiến tr&uacute;c độc đ&aacute;o.</h2>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/1-peking-university-1524452615_680x0.jpg\" /></p>\n\n<p><strong>1. Đại học Bắc Kinh (Trung Quốc)</strong></p>\n\n<p>Th&agrave;nh lập năm 1898, Đại học Bắc Kinh lu&ocirc;n được đ&aacute;nh gi&aacute; cao về khu&ocirc;n vi&ecirc;n v&agrave; ng&agrave;y c&agrave;ng n&acirc;ng cao danh tiếng về mặt học thuật. Trường nằm ở khu vườn ngự uyển của nh&agrave; Thanh trước đ&acirc;y, gồm những cảnh quan v&agrave; t&ograve;a nh&agrave; được tạo dựng theo phong c&aacute;ch Trung Hoa truyền thống. Ảnh:&nbsp;<em>THE</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/2-nanyang-1524452616_680x0.jpg\" /></p>\n\n<p><strong>2. Đại học C&ocirc;ng nghệ Nanyang (Singapore)</strong></p>\n\n<p>Trường được v&iacute; như thi&ecirc;n đường cho những người nghiện kiến tr&uacute;c với những t&ograve;a nh&agrave; độc đ&aacute;o được thiết kế bởi c&aacute;c kiến tr&uacute;c sư h&agrave;ng đầu, nổi bật như t&ograve;a nh&agrave; trung t&acirc;m học tập. Ảnh:&nbsp;<em>Asian Scientist</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/3-kyung-hee-university-south-korea-1-1524452616_680x0.jpg\" /></p>\n\n<p><strong>3. Đại học Kyung Hee (H&agrave;n Quốc)</strong></p>\n\n<p>Được th&agrave;nh lập năm 1949 tại Seoul, Đại học Kyung Hee l&agrave; sự kết hợp ho&agrave;n hảo giữa kiến tr&uacute;c t&acirc;n cổ điển v&agrave; vẻ đẹp tự nhi&ecirc;n. H&agrave;ng trăm c&acirc;y hoa anh đ&agrave;o H&agrave;n Quốc được trồng từ cổng tới tận s&acirc;n trường l&agrave; điểm nhấn khiến Kyung Hee trở n&ecirc;n nổi bật hơn v&agrave; trở th&agrave;nh một điểm tham quan th&uacute; vị khi đến Seoul. Ảnh:&nbsp;<em>K-pop House&#39;s</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/4-university-of-tokyo-1524452616_680x0.jpg\" /></p>\n\n<p><strong>4. Đại học Tokyo (Nhật Bản)</strong></p>\n\n<p>Đ&acirc;y l&agrave; một trong những trường đại học uy t&iacute;n nhất Nhật Bản. Được th&agrave;nh lập năm 1877, hiện trường c&oacute; 5 cơ sở ở Hongo, Komaba, Kashiwa, Shirokane v&agrave; Nakano, trong đ&oacute; khu&ocirc;n vi&ecirc;n ở Hongo nổi tiếng bậc nhất với những c&acirc;y Gingko 120 tuổi, hồ c&aacute; ch&eacute;p Koi, giảng đường Yasuda v&agrave; Red Gate - cổng được được x&acirc;y trong thời Edo, chứa đựng cả một chiều d&agrave;i lịch sử Nhật Bản. Ảnh:&nbsp;<em>Boomsbeat</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/5-ewha-womens-university-1524452616_680x0.jpg\" /></p>\n\n<p><strong>5. Đại học Nữ sinh Ewha (H&agrave;n Quốc)</strong></p>\n\n<p>Được th&agrave;nh lập v&agrave;o năm 1886, Ewha trở th&agrave;nh viện gi&aacute;o dục đại học đầu ti&ecirc;n ở H&agrave;n Quốc d&agrave;nh cho nữ giới. Trường c&oacute; kh&ocirc;ng gian xanh rộng lớn c&ugrave;ng những t&ograve;a nh&agrave; với kiến tr&uacute;c nổi bật, trong đ&oacute; ti&ecirc;u biểu nhất khu phức hợp Ewha Campus - c&ocirc;ng tr&igrave;nh c&oacute; khu&ocirc;n vi&ecirc;n ngầm lớn nhất H&agrave;n Quốc. Ảnh:&nbsp;<em>Mimoa</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/6-tsinghua-university-1524452616_680x0.jpg\" /></p>\n\n<p><strong>6. Đại học Thanh Hoa (Trung Quốc)</strong></p>\n\n<p>Giống như Đại học Bắc Kinh, trường Thanh Hoa cũng nằm trong khu vườn ngự uyển của nh&agrave; Thanh xưa với đầy c&acirc;y xanh, ao sen c&ugrave;ng sự kết hợp giữa c&aacute;c t&ograve;a nh&agrave; mang kiến tr&uacute;c phương T&acirc;y v&agrave; truyền thống. Ảnh:&nbsp;<em>THE</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/7-cityu-hongkong-1524452616_680x0.jpg\" /></p>\n\n<p><strong>7. City University of Hong Kong (Hong Kong, Trung Quốc)</strong></p>\n\n<p>Khu&ocirc;n vi&ecirc;n trường gồm 14 t&ograve;a nh&agrave; ch&iacute;nh với những n&eacute;t kiến tr&uacute;c độc đ&aacute;o, hiện đại, nổi bật l&agrave; Trung t&acirc;m S&aacute;ng tạo truyền th&ocirc;ng Run Run Shaw. Ảnh:&nbsp;<em>City University of Hong Kong</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/8-tama-art-university-tokyo-1524452616_680x0.jpg\" /></p>\n\n<p><strong>8. Đại học Nghệ thuật Tama (Nhật Bản)</strong></p>\n\n<p>Thư viện của Đại học Nghệ thuật Tama được nhiều người biết đến với vẻ đẹp ho&agrave;n hảo. Kết cấu của c&ocirc;ng tr&igrave;nh n&agrave;y gồm nhiều cột, dầm h&igrave;nh v&ograve;ng cung đ&oacute;ng vai tr&ograve; chịu lực đồng thời tạo ra c&aacute;c g&oacute;c nh&igrave;n tự do v&agrave; độc lập, gi&uacute;p &iacute;ch cho sự s&aacute;ng tạo của sinh vi&ecirc;n ng&agrave;nh nghệ thuật. Ảnh:&nbsp;<em>Mimoa</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/9-xiamen-university-china-1524452617_680x0.jpg\" /></p>\n\n<p><strong>9. Đại học Hạ M&ocirc;n (Trung Quốc)</strong></p>\n\n<p>Trường tọa lạc ở th&agrave;nh phố Hạ M&ocirc;n, nơi được mệnh danh l&agrave; &quot;Hawaii của phương Đ&ocirc;ng&quot;. Nằm b&ecirc;n bờ biển v&agrave; dưới ch&acirc;n những ngọn đồi, khu&ocirc;n vi&ecirc;n trường giống như khu bảo tồn với hồ nước, những t&aacute;n c&acirc;y xanh tốt c&ugrave;ng những t&ograve;a nh&agrave; mang phong c&aacute;ch độc đ&aacute;o. Ảnh:&nbsp;<em>Pinterest</em></p>\n\n<p><img alt=\"10 đại học đẹp nhất khu vực Đông Á\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/universiti-teknologi-petronas-4-1524472531_680x0.jpg\" /></p>\n\n<p><strong>10. Đại học C&ocirc;ng nghệ Petronas (Malaysia)</strong></p>\n\n<p>Trường tọa lạc ở vị tr&iacute; đẹp, được bao quanh bởi những ngọn đồi v&agrave; rừng mưa nhiệt đới. Khu&ocirc;n vi&ecirc;n l&agrave; một tổ hợp kh&ocirc;ng gian mở với nhiều hồ nước v&agrave; c&aacute;c t&ograve;a nh&agrave; hiện đại. Ảnh:&nbsp;<em>Owned</em></p>\n\n<p>&nbsp;</p>\n\n<p><strong>Dương T&acirc;m</strong></p>\n', '1524531838', '', 6, 0),
(53, 'Bài thi mẫu đánh giá năng lực của Đại học Quốc gia TP HCM', 'daihocquocgia-1524115085_180x108.jpg', '120 câu hỏi được xây dựng theo định hướng đánh giá năng lực cần thiết để học đại học, không chú trọng đánh giá kiến thức.', '<h1>B&agrave;i thi mẫu đ&aacute;nh gi&aacute; năng lực của Đại học Quốc gia TP HCM</h1>\n\n<h2>120 c&acirc;u hỏi được x&acirc;y dựng theo định hướng đ&aacute;nh gi&aacute; năng lực cần thiết để học đại học, kh&ocirc;ng ch&uacute; trọng đ&aacute;nh gi&aacute; kiến thức.</h2>\n\n<p>S&aacute;ng 19/4, Đại học Quốc gia TP HCM c&ocirc;ng bố b&agrave;i thi mẫu đ&aacute;nh gi&aacute; năng lực lần đầu được sử dụng để x&eacute;t tuyển v&agrave;o c&aacute;c trường, khoa th&agrave;nh vi&ecirc;n. Kh&aacute;c với c&aacute;ch tiếp cận của kỳ thi THPT quốc gia, b&agrave;i thi n&agrave;y đ&aacute;nh gi&aacute; học sinh qua khả năng sử dụng ng&ocirc;n ngữ, tư duy logic, xử l&yacute; số liệu, giải quyết vấn đề.</p>\n\n<p>B&agrave;i thi gồm 120 c&acirc;u hỏi với thời gian l&agrave;m 150 ph&uacute;t. Kết quả được x&aacute;c định theo phương ph&aacute;p trắc nghiệm hiện đại, thang điểm 1.200.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Bài thi mẫu đánh giá năng lực của Đại học Quốc gia TP HCM\" src=\"https://i-vnexpress.vnecdn.net/2018/04/19/de-thi-mau-1-1800-1524115217.jpg\" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Bài thi mẫu đánh giá năng lực của Đại học Quốc gia TP HCM - 1\" src=\"https://i-vnexpress.vnecdn.net/2018/04/19/de-thi-mau-2-6409-1524115217.jpg\" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Bài thi mẫu đánh giá năng lực của Đại học Quốc gia TP HCM - 2\" src=\"https://i-vnexpress.vnecdn.net/2018/04/19/de-thi-mau-3-7973-1524115217.jpg\" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Bài thi mẫu đánh giá năng lực của Đại học Quốc gia TP HCM - 3\" src=\"https://i-vnexpress.vnecdn.net/2018/04/19/de-thi-mau-4-2346-1524115217.jpg\" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Bài thi mẫu đánh giá năng lực của Đại học Quốc gia TP HCM - 4\" src=\"https://i-vnexpress.vnecdn.net/2018/04/19/de-thi-mau-5-4893-1524115217.jpg\" /></td>\n		</tr>\n	</tbody>\n</table>\n', '1524532005', '', 6, 0),
(54, 'So sánh chi phí lăn bánh Vios và các đối thủ tại Việt Nam', 'so-sanh-chi-phi-lan-banh-hyundai-accent-va-cac-doi-thu-1524471997_500x300.jpg', 'Honda City Top và Vios 1.5G có chi phí lăn bánh cao hơn các đối thủ Accent và Mazda2.', '<h2>Honda City Top v&agrave; Vios 1.5G c&oacute; chi ph&iacute; lăn b&aacute;nh cao hơn c&aacute;c đối thủ Accent v&agrave; Mazda2.</h2>\n\n<p><img alt=\"So sánh chi phí lăn bánh Vios và các đối thủ tại Việt Nam\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/GiaLanBanh-3-1524473727.jpg\" /></p>\n\n<p><strong>Việt Chung - Ngọc Tuấn</strong></p>\n', '1524532119', '', 7, 0),
(55, 'Tên cướp kéo lê cô gái trên đường Sài Gòn bị bắt', 'cuop-sai-gon-5349-1524502642.jpg', 'Hàng chục trinh sát đã rà soát camera ở các tuyến đường trung tâm quận 1, tìm tung tích tên cướp suốt 5 ngày.', '<h2>H&agrave;ng chục trinh s&aacute;t đ&atilde; r&agrave; so&aacute;t camera ở c&aacute;c tuyến đường trung t&acirc;m quận 1, t&igrave;m tung t&iacute;ch t&ecirc;n cướp suốt 5 ng&agrave;y.</h2>\n\n<p><a href=\"https://vnexpress.net/tin-tuc/phap-luat/camera-ghi-canh-co-gai-bi-cuop-keo-le-o-sai-gon-3739088.html#ctr=related_news_click\">Camera ghi cảnh c&ocirc; g&aacute;i bị cướp k&eacute;o l&ecirc; ở S&agrave;i G&ograve;n</a></p>\n\n<p>Tối 23/4, C&ocirc;ng an quận 1 (TP HCM) bắt Phạm Hồng Th&aacute;i (23 tuổi) - nghi can giật điện thoại của c&ocirc; g&aacute;i ở trung t&acirc;m S&agrave;i G&ograve;n, k&eacute;o l&ecirc; nạn nh&acirc;n suốt 50 m.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Thái tại cơ quan điều tra. Ảnh: Quốc Thắng.\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/cuop-sai-gon-5349-1524502642.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Th&aacute;i tại cơ quan điều tra. Ảnh:&nbsp;<em>Quốc Thắng.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Th&aacute;i l&agrave;m việc cho cửa h&agrave;ng cung cấp nước suối tại quận 4. S&aacute;ng 19/4, anh ta chạy xe m&aacute;y đến giao lộ Nguyễn Th&aacute;i Học - Trần Hưng Đạo (phường Phạm Ngũ L&atilde;o, quận 1) ph&aacute;t hiện chị Trần Thị Kim Thoa (24 tuổi) dừng xe đứng nghe điện thoại n&ecirc;n nảy sinh &yacute; đồ xấu.</p>\n\n<p>Từ ph&iacute;a sau, Th&aacute;i chạy l&ecirc;n giật phăng chiếc điện thoại tr&ecirc;n tay c&ocirc; g&aacute;i rồi lao v&uacute;t đi. Theo phản xạ, chị Thoa d&ugrave;ng hai tay giằng lại điện thoại nhưng bị t&ecirc;n cướp k&eacute;o đi suốt đoạn đường d&agrave;i. Đau đớn v&igrave; bị nhiều vết thương, nạn nh&acirc;n phải bu&ocirc;ng tay ra, t&ecirc;n cướp tẩu tho&aacute;t.</p>\n\n<p>L&uacute;c xảy ra sự việc c&oacute; chiến sĩ CSGT trực chốt chạy bộ đuổi theo nhưng kh&ocirc;ng kịp. Nhiều người chứng kiến cũng kh&ocirc;ng kịp phản ứng v&igrave; sự việc diễn ra kh&aacute; nhanh.</p>\n\n<p><img alt=\"Camera ghi cảnh cô gái bị kéo cướp kéo lê trên đường Sài Gòn\" src=\"https://iv.vnecdn.net/vnexpress/images/web/2018/04/19/camera-ghi-canh-co-gai-bi-keo-cuop-keo-le-tren-duong-sai-gon-1524129510_500x300.jpg\" /></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>C&ocirc;ng an quận 1 đ&atilde; tung nhiều trinh s&aacute;t, phối hợp lực lượng của C&ocirc;ng an TP HCM truy bắt t&ecirc;n cướp. Họ r&agrave; so&aacute;t c&aacute;c camera an ninh xung quanh khu vực, tr&ecirc;n c&aacute;c tuyến đường gần đấy. Sau 5 ng&agrave;y truy l&ugrave;ng, Th&aacute;i vừa bị bắt khi đang uống c&agrave; ph&ecirc;.</p>\n\n<p>Bước đầu Th&aacute;i cho biết, khi giật được điện thoại hắn quay về xưởng l&agrave;m việc b&igrave;nh thường, đến chiều đem b&aacute;n được hơn 2 triệu đồng, ti&ecirc;u x&agrave;i hết.</p>\n\n<p><strong>Quốc Thắng</strong></p>\n', '1524534097', '', 8, 0),
(56, 'Chị em sinh đôi truy đuổi cướp trên đường Sài Gòn', 'q1batcuop-3467-1486780111.jpg', 'Thấy bà lão bị giật túi tiền, Mai cùng chị sinh đôi tăng ga truy đuổi từ quận 1 sang quận 4, ép hắn ngã rồi cùng người dân khống chế.', '<h1>Chị em sinh đ&ocirc;i truy đuổi cướp tr&ecirc;n đường S&agrave;i G&ograve;n</h1>\n\n<h2>Thấy b&agrave; l&atilde;o bị giật t&uacute;i tiền, Mai c&ugrave;ng chị sinh đ&ocirc;i tăng ga truy đuổi từ quận 1 sang quận 4, &eacute;p hắn ng&atilde; rồi c&ugrave;ng người d&acirc;n khống chế.</h2>\n\n<p>Ng&agrave;y 11/2, C&ocirc;ng an quận 1 (TP HCM) tạm giữ Ph&ugrave; Sinh (37 tuổi) về h&agrave;nh vi Cướp giật t&agrave;i sản.</p>\n\n<table align=\"left\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"chi-em-sinh-doi-truy-duoi-cuop-tren-duong-sai-gon\" src=\"https://i-vnexpress.vnecdn.net/2017/02/11/q1batcuop-3467-1486780111.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Sinh tại cơ quan điều tra. Ảnh:&nbsp;<em>Q.T</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Theo điều tra, Sinh nghiện nhiều năm nay. Tối bốn h&ocirc;m trước anh ta chạy l&ograve;ng v&ograve;ng ở trung t&acirc;m S&agrave;i G&ograve;n t&igrave;m người giật t&agrave;i sản, kiếm tiền mua ma t&uacute;y.</p>\n\n<p>Thấy b&agrave; l&atilde;o ngồi b&aacute;n hoa b&ecirc;n đường Trần Hưng Đạo (quận 1), để t&uacute;i nhựa đựng tiền b&ecirc;n cạnh, Sinh lại gần. Thừa l&uacute;c b&agrave; cụ kh&ocirc;ng để &yacute;, hắn giật phăng chiếc giỏ rồi th&aacute;o chạy.</p>\n\n<p>Chứng kiến vụ việc, Mai v&agrave; Liễu (26 tuổi, chị em sinh đ&ocirc;i) chở nhau đi ngang rất bất b&igrave;nh, r&uacute; ga đuổi theo kẻ cướp.&nbsp;</p>\n\n<p>Sinh thấy 2 c&ocirc; g&aacute;i đuổi theo, li&ecirc;n tục lạng l&aacute;ch tr&ecirc;n đường hướng về quận 4. Tuy nhi&ecirc;n, anh ta vẫn bị b&aacute;m riết qua nhiều tuyến đường.</p>\n\n<p>Nhiều người đi đường c&ugrave;ng hỗ trợ hai c&ocirc; g&aacute;i, truy đuổi Sinh gắt gao. Đến đường L&yacute; Quốc Hưng (quận 4), xe của Mai vượt l&ecirc;n, &eacute;p ng&atilde; xe t&ecirc;n cướp. Hắn v&ugrave;ng dậy bỏ chạy nhưng bị người d&acirc;n v&agrave; 2 chị em sinh đ&ocirc;i v&acirc;y bắt.</p>\n\n<p><strong>Quốc Thắng</strong></p>\n', '1524534159', '', 8, 0),
(57, 'Vương miện hoa hậu và vị trí vedette trên sàn diễn thời trang Việt', 'CT_ZING2.jpg', 'Cách đây 1-2 năm, H\'Hen Niê là chân dài không tiếng tăm trên sàn catwalk tuần lễ thời trang. Danh hiệu Hoa hậu Hoàn vũ Việt Nam ngay lập tức đưa cô lên hàng \"người mẫu ngôi sao\".', '<p>C&aacute;ch đ&acirc;y 1-2 năm, H&#39;Hen Ni&ecirc; l&agrave; ch&acirc;n d&agrave;i kh&ocirc;ng tiếng tăm tr&ecirc;n s&agrave;n catwalk tuần lễ thời trang. Danh hiệu Hoa hậu Ho&agrave;n vũ Việt Nam ngay lập tức đưa c&ocirc; l&ecirc;n h&agrave;ng &quot;người mẫu ng&ocirc;i sao&quot;.</p>\n\n<p>Tuần lễ Thời trang quốc tế Việt Nam vừa c&oacute; đ&ecirc;m diễn mở m&agrave;n h&ocirc;m 19/4 với bộ sưu tập của nh&agrave; thiết kế t&agrave;i năng C&ocirc;ng Tr&iacute;. H&igrave;nh ảnh H&#39;Hen Ni&ecirc; bước đi ch&iacute;nh giữa ở vị tr&iacute; nổi bật, Ho&agrave;ng Th&ugrave;y v&agrave; M&acirc;u Thủy catwalk ph&iacute;a sau khiến cư d&acirc;n mạng đặc biệt ch&uacute; &yacute;.</p>\n\n<p>Chưa b&agrave;n đến chuyện ai đi đẹp, thứ tự sắp xếp đ&atilde; l&agrave; một c&acirc;u chuyện th&uacute; vị.&nbsp;</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Vuong mien hoa hau va vi tri vedette tren san dien thoi trang Viet hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/OpluOAA/2018_04_20/CT_ZING2.jpg\" style=\"height:573px; width:860px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>H&#39;Hen Ni&ecirc; dẫn trước Ho&agrave;ng Th&ugrave;y - M&acirc;u Thủy tr&ecirc;n s&agrave;n diễn Tuần lễ thời trang 2018. Ảnh:&nbsp;<em>Nguyễn Th&agrave;nh.</em>&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<h3>Ho&agrave;ng Th&ugrave;y, M&acirc;u Thủy lần đầu đi sau H&#39;Hen Ni&ecirc;</h3>\n\n<p>Nhận x&eacute;t về m&agrave;n tr&igrave;nh diễn của d&agrave;n người đẹp&nbsp;<a href=\"https://news.zing.vn/hoa-hau-hoa-khoi-tieu-diem.html\">Hoa hậu</a>&nbsp;Ho&agrave;n vũ, một kh&aacute;n giả b&igrave;nh luận: &quot;Ba người đều xuất ph&aacute;t l&agrave; người mẫu n&ecirc;n tr&igrave;nh diễn đẹp, thần th&aacute;i chuy&ecirc;n nghiệp&quot;. Th&agrave;nh vi&ecirc;n mạng kh&aacute;c b&agrave;y tỏ: &quot;Ho&agrave;ng Th&ugrave;y bước đi uyển chuyển, cuốn h&uacute;t nhất. Nhưng H&#39;Hen Ni&ecirc; đi giữa, nh&igrave;n rất c&aacute; t&iacute;nh&quot;.</p>\n\n<p>Nếu nh&igrave;n ở g&oacute;c độ hoa hậu, vị tr&iacute; sắp xếp n&agrave;y l&agrave; điều hiển nhi&ecirc;n bởi cả ba bước ra từ một cuộc thi sắc đẹp - H&#39;Hen Ni&ecirc; l&agrave; Hoa hậu Ho&agrave;n vũ Việt Nam 2017, c&ograve;n Th&ugrave;y v&agrave; Thủy l&agrave; hai &aacute; hậu. Tuy nhi&ecirc;n, nh&igrave;n ở g&oacute;c độ thời trang, đ&oacute; l&agrave; cuộc lột x&aacute;c ngoạn mục của H&#39;Hen Ni&ecirc; - top 9 Vietnam&#39;s Next Top Model 2015.</p>\n\n<p>Quay ngược lại một hoặc hai năm trước đ&acirc;y, c&ocirc; g&aacute;i d&acirc;n tộc &Ecirc; Đ&ecirc; &quot;chẳng l&agrave; ai&quot; tr&ecirc;n s&agrave;n catwalk, c&ograve;n Ho&agrave;ng Th&ugrave;y đ&atilde; l&agrave; vedette, được chọn diễn ở những vị tr&iacute; quan trọng với mức c&aacute;t-x&ecirc; cao.&nbsp;</p>\n\n<table align=\"right\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Vuong mien hoa hau va vi tri vedette tren san dien thoi trang Viet hinh anh 2\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/OpluOAA/2018_04_20/IMG_52721068x1602.jpg\" style=\"height:1602px; width:1068px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>Ho&agrave;ng Th&ugrave;y trước đ&oacute; nhiều lần đảm nhận vị tr&iacute; vedette. Ảnh:&nbsp;<em>BTC</em>.</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>V&agrave; M&acirc;u Thủy, với danh hiệu qu&aacute;n qu&acirc;n Next Top Model 2013, cũng thường được nh&agrave; thiết kế tin tưởng chọn l&agrave;m first-face hoặc diễn kết m&agrave;n c&aacute;c bộ sưu tập.</p>\n\n<p>C&oacute; thể n&oacute;i danh hiệu Hoa hậu Ho&agrave;n vũ Việt Nam 2017 đ&atilde; gi&uacute;p H&#39;Hen Ni&ecirc; thăng hạng tr&ecirc;n s&agrave;n diễn thời trang. Từ một người mẫu diễn ở vị tr&iacute; b&igrave;nh thường, nhạt nh&ograve;a, c&ocirc; xuất hiện tr&ecirc;n s&acirc;n khấu mở m&agrave;n Tuần lễ thời trang quốc tế Việt Nam 2018, b&ecirc;n cạnh hai đ&agrave;n chị trong nghề Ho&agrave;ng Th&ugrave;y v&agrave; M&acirc;u Thủy.</p>\n\n<p>Rất c&oacute; thể, ở những show sắp tới, c&ocirc; g&aacute;i với l&agrave;n da b&aacute;nh mật v&agrave; m&aacute;i t&oacute;c t&eacute;m c&aacute; t&iacute;nh tiếp tục được c&aacute;c nh&agrave; thiết kế &quot;chọn mặt gửi v&agrave;ng&quot; cho bộ sưu tập của m&igrave;nh.&nbsp;</p>\n\n<p>Bản th&acirc;n H&#39;Hen Ni&ecirc; cũng thừa nhận sau khi đăng quang hoa hậu, vị tr&iacute; của c&ocirc; trong giới người mẫu c&oacute; sự thay đổi r&otilde; rệt, được ưu &aacute;i nhiều hơn v&agrave; trao cho những cơ hội tỏa s&aacute;ng.&nbsp;</p>\n\n<h3>Cứ đăng quang hoa hậu, &aacute; hậu l&agrave; trở th&agrave;nh vedette&nbsp;</h3>\n\n<p>Tất nhi&ecirc;n, H&#39;Hen Ni&ecirc; chỉ l&agrave; một trong những trường hợp cho thấy hoa hậu, người đẹp lu&ocirc;n được ưu &aacute;i đặc biệt tr&ecirc;n s&agrave;n diễn thời trang Việt. Trước đ&acirc;y, kh&aacute;n giả từng chứng kiến Phạm Hương, Kỳ Duy&ecirc;n, Huyền My, Đỗ Mỹ Linh... đảm nhận vai tr&ograve; vedette, d&ugrave; kh&ocirc;ng phải ai trong số họ cũng sở hữu gương mặt, v&oacute;c d&aacute;ng ph&ugrave; hợp l&agrave;m người mẫu.</p>\n\n<p>Nhưng &iacute;t nhất, tại thời điểm đ&oacute;, họ l&agrave; gương mặt được c&ocirc;ng ch&uacute;ng quan t&acirc;m, được nhắc đến nhiều tr&ecirc;n c&aacute;c phương tiện truyền th&ocirc;ng.</p>\n\n<p>Hay mới đ&acirc;y, ca sĩ Hương Giang, ngay sau khi trở về từ cuộc thi Hoa hậu Chuyển giới Quốc tế 2018, d&ugrave; kh&ocirc;ng c&oacute; kinh nghiệm catwalk, vẫn được chọn diễn kết m&agrave;n cho một bộ sưu tập thuộc khu&ocirc;n khổ Tuần lễ Thời trang quốc tế Việt Nam.</p>\n\n<p>Hoa hậu, c&ugrave;ng với sự y&ecirc;u mến cuồng nhiệt từ ph&iacute;a kh&aacute;n giả, dường như lu&ocirc;n c&oacute; một &quot;quyền lực&quot; ngầm, quyết định cả vị thế tr&ecirc;n s&acirc;n khấu thời trang chuy&ecirc;n nghiệp - nơi t&ocirc;n vinh vải v&oacute;c, quần &aacute;o, t&ocirc;n vinh những vẻ đẹp lạ v&agrave; những bước catwalk đi&ecirc;u luyện.&nbsp;</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Vuong mien hoa hau va vi tri vedette tren san dien thoi trang Viet hinh anh 3\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/OpluOAA/2018_04_20/DML_zing_7.jpg\" style=\"height:682px; width:1024px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>Hoa hậu Đỗ Mỹ Linh cũng từng l&agrave; vedette show thời trang. Ảnh:&nbsp;<em>B&aacute; Ngọc.</em></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Tuy nhi&ecirc;n, với s&agrave;n quốc tế - nhất l&agrave; bốn kinh đ&ocirc; thời trang lớn như Paris, Milan, New York, London - việc c&aacute;c người đẹp xuất hiện tr&ecirc;n s&agrave;n catwalk kh&ocirc;ng phổ biến. Hoặc nếu tham gia kh&ocirc;ng c&oacute; nghĩa nhất định họ phải được chọn l&agrave;m vedette.</p>\n\n<p>Hoa hậu Ho&agrave;n vũ Thế giới 2016&nbsp;Iris Mittenaere từng tr&igrave;nh diễn ở show thời trang cao cấp m&ugrave;a thu 2017 của nh&agrave; mốt&nbsp;Jean Paul Gaultier như bao người mẫu b&igrave;nh thường kh&aacute;c - kh&ocirc;ng mở m&agrave;n cũng kh&ocirc;ng kết m&agrave;n.</p>\n\n<p>Những gương mặt vedette quen thuộc của nh&agrave; mốt quốc tế vẫn thường l&agrave; c&aacute;c ch&acirc;n d&agrave;i huyền thoại, người mẫu nổi tiếng gắn b&oacute; với thương hiệu (Alessandra Ambrosio, Natalia Vodianova, Natasha Poly, Coco Rocha...)&nbsp;hoặc thế hệ si&ecirc;u mẫu trẻ t&agrave;i năng như Cara Delevingne, Kendall Jenner, Gigi Hadid, Kai Gerber...</p>\n\n<p>Ở đ&oacute;, &quot;nữ ho&agrave;ng sắc đẹp&quot; v&agrave; &quot;nữ ho&agrave;ng s&agrave;n diễn&quot; l&agrave; hai phạm tr&ugrave; kh&aacute;c nhau.&nbsp;</p>\n\n<p><strong><a href=\"https://news.zing.vn/video-man-catwalk-cua-h-hen-nie-cung-hoang-thuy-va-mau-thuy-post836124.html\" target=\"_blank\">M&agrave;n catwalk của H&#39;Hen Ni&ecirc; c&ugrave;ng Ho&agrave;ng Th&ugrave;y v&agrave; M&acirc;u Thủy</a></strong>&nbsp;Ba người đẹp của Hoa hậu Ho&agrave;n vũ Việt Nam đảm nhận vai tr&ograve; mở m&agrave;n bộ sưu tập của NTK C&ocirc;ng Tr&iacute;.</p>\n\n<p>Phương Ly</p>\n', '1524534345', '', 44, 0),
(58, 'Khán giả vỗ tay tán thưởng khi thủ môn Bùi Tiến Dũng bước ra catwalk', '30421800_1247761918660399_376205616_n.jpg', 'Thủ môn Bùi Tiến Dũng lần đầu tiên đảm nhận vai trò người mẫu ở một chương trình lớn như Tuần lễ thời trang quốc tế Việt Nam mùa xuân hè 2018.', '<p>Thủ m&ocirc;n B&ugrave;i Tiến Dũng lần đầu ti&ecirc;n đảm nhận vai tr&ograve; người mẫu ở một chương tr&igrave;nh lớn như Tuần lễ thời trang quốc tế Việt Nam m&ugrave;a xu&acirc;n h&egrave; 2018.</p>\n\n<p>&nbsp;</p>\n\n<p><strong><a href=\"https://news.zing.vn/video-thu-mon-bui-tien-dung-lam-vedette-show-thoi-trang-post836561.html\" target=\"_blank\">Thủ m&ocirc;n B&ugrave;i Tiến Dũng l&agrave;m vedette show thời trang</a></strong>&nbsp;Đ&acirc;y l&agrave; lần đầu ti&ecirc;n ch&agrave;ng cầu thủ &quot;hot boy&quot; của đội tuyển U23 Việt Nam l&agrave;m người mẫu catwalk.</p>\n\n<p>Sau th&agrave;nh c&ocirc;ng của đội tuyển U23 Việt Nam, thủ m&ocirc;n B&ugrave;i Tiến Dũng l&agrave; một trong những c&aacute;i t&ecirc;n g&acirc;y ch&uacute; &yacute; nhất. Anh nhận được nhiều lời mời chụp h&igrave;nh quảng c&aacute;o, tham gia sự kiện. Tối 22/4, lần đầu ti&ecirc;n kh&aacute;n giả chứng kiến B&ugrave;i Tiến Dũng đảm nhận vai tr&ograve; người mẫu tr&ecirc;n s&agrave;n diễn Tuần lễ thời trang quốc tế Việt Nam 2018.</p>\n\n<p>Trong vai tr&ograve; vedette cho một thương hiệu thời trang nam, ch&agrave;ng thủ m&ocirc;n của đội tuyển U23 diện bộ suit xanh, kết hợp c&ugrave;ng gi&agrave;y thể thao trẻ trung. Sự xuất hiện bất ngờ của B&ugrave;i Tiến Dũng khiến kh&aacute;n giả th&iacute;ch th&uacute;, d&agrave;nh cho anh những tr&agrave;ng ph&aacute;o tay cổ vũ.</p>\n\n<p>C&oacute; kh&aacute;n giả n&oacute;i vui, giữa h&agrave;ng trăm người mẫu nổi tiếng xuất hiện, người d&agrave;nh được tiếng vỗ tay t&aacute;n thưởng to nhất, vang nhất lại l&agrave; thủ m&ocirc;n B&ugrave;i Tiến Dũng. Sức n&oacute;ng của U23 dường như vẫn c&ograve;n đ&acirc;y, ở một nơi dường như xa lạ với c&aacute;c cầu thủ l&agrave; s&agrave;n catwalk. Nhưng họ vẫn được đ&oacute;n nhận một c&aacute;ch nồng nhiệt v&agrave; gần gũi.</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Khan gia vo tay tan thuong khi thu mon Bui Tien Dung buoc ra catwalk hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/OpluOAA/2018_04_22/30421800_1247761918660399_376205616_n.jpg\" style=\"height:960px; width:640px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>Thủ m&ocirc;n B&ugrave;i Tiến Dũng lần đầu tr&igrave;nh diễn s&agrave;n catwalk.</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Do lần đầu tr&igrave;nh diễn, Tiến Dũng lộ r&otilde; vẻ căng thẳng tr&ecirc;n gương mặt, bước đi gượng gạo, kh&ocirc;ng đ&uacute;ng nhịp nhạc. Một kh&aacute;n giả b&igrave;nh luận tr&ecirc;n trang fanpage tuần lễ thời trang: &quot;Khi bạn đam m&ecirc; đ&aacute; b&oacute;ng nhưng lại bị bắt đi diễn thời trang, bước đi như hờn giận cả thế giới&quot;. Một bạn kh&aacute;c nhận x&eacute;t: &quot;Anh Dũng đi catwalk như đi chợ, nhưng thần th&aacute;i tốt. Quan trọng vẫn l&agrave; thần th&aacute;i&quot;.</p>\n\n<p>Tuy nhi&ecirc;n, theo nhiều người h&acirc;m mộ, Tiến Dũng tr&igrave;nh diễn gượng l&agrave; điều đương nhi&ecirc;n bởi kh&ocirc;ng phải ai cũng c&oacute; sẵn kỹ năng catwalk: &quot;Dũng l&agrave; thủ m&ocirc;n, c&oacute; học hay đi catwalk bao giờ đ&acirc;u. Đ&acirc;y l&agrave; được nh&agrave; thiết kế mời n&ecirc;n nhận lời th&ocirc;i. Sao lại ph&aacute;n x&eacute;t t&iacute;nh chuy&ecirc;n m&ocirc;n nhỉ, Dũng kh&ocirc;ng phải người mẫu?&quot;.</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Khan gia vo tay tan thuong khi thu mon Bui Tien Dung buoc ra catwalk hinh anh 2\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/OpluOAA/2018_04_22/30831563_1247763205326937_1968501185_n.jpg\" style=\"height:639px; width:960px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>Nhiều &yacute; kiến đ&aacute;nh gi&aacute; tr&aacute;i chiều quanh việc thủ m&ocirc;n B&ugrave;i Tiến Dũng tham gia tr&igrave;nh diễn với tư c&aacute;ch người mẫu ở tuần lễ thời trang quốc tế VN m&ugrave;a xu&acirc;n h&egrave; 2018.</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>B&ugrave;i Tiến Dũng sở hữu chiều cao l&yacute; tưởng 1,81 m c&ugrave;ng v&oacute;c d&aacute;ng kh&aacute; đẹp, gần ti&ecirc;u chuẩn của một người mẫu nam. Hồi đầu năm, nh&agrave; thiết kế Đỗ Mạnh Cường từng chia sẻ tr&ecirc;n trang c&aacute; nh&acirc;n muốn mời Tiến Dũng l&agrave;m vedette v&igrave; ấn tượng với chiều cao của cầu thủ n&agrave;y.&nbsp;</p>\n\n<p>Ly Nguyễn</p>\n\n<p>Ảnh: Nguyễn Th&agrave;nh</p>\n', '1524536842', '', 44, 0);
INSERT INTO `article` (`id_article`, `title`, `image`, `brief_content`, `content`, `created_date`, `modified_date`, `id_category`, `id_author`) VALUES
(59, 'Tạo hình \'Vườn sao băng 2018\' quê mùa, dùng lại nhạc phim cũ do F4 hát', 'mg21.jpg', 'Vừa tung ra đoạn trailer dài 30 giây, \"Vườn sao băng 2018\" đã vướng phải nhiều ý kiến chê bai của khán giả vì tạo hình không toát lên được gia thế giàu có của dàn nhân vật.', '<p>Vừa tung ra đoạn trailer d&agrave;i 30 gi&acirc;y, &quot;Vườn sao băng 2018&quot; đ&atilde; vướng phải nhiều &yacute; kiến ch&ecirc; bai của kh&aacute;n giả v&igrave; tạo h&igrave;nh kh&ocirc;ng to&aacute;t l&ecirc;n được gia thế gi&agrave;u c&oacute; của d&agrave;n nh&acirc;n vật.</p>\n\n<p>&nbsp;</p>\n\n<p>00:03</p>\n\n<p>&nbsp;</p>\n\n<p>00:30</p>\n\n<p><strong><a href=\"https://news.zing.vn/video-trailer-vuon-sao-bang-2018-post836904.html\" target=\"_blank\">Trailer Vườn sao băng 2018</a></strong>&nbsp;Trailer d&agrave;i 30 gi&acirc;y giới thiệu sơ qua phi&ecirc;n bản thực hiện v&agrave;o năm 2018 của bộ phim &quot;Vườn sao băng&quot;.</p>\n\n<p>Bộ phim&nbsp;<em>Vườn sao băng&nbsp;</em>phi&ecirc;n bản l&agrave;m lại của năm 2018&nbsp;đ&atilde; c&ocirc;ng bố đoạn trailer d&agrave;i 30 gi&acirc;y nhằm thăm d&ograve; &yacute; kiến kh&aacute;n giả. Đoạn trailer vẫn thu h&uacute;t được sự quan t&acirc;m của c&ocirc;ng ch&uacute;ng d&ugrave; chỉ xuất hiện tr&ecirc;n mạng Internet dưới dạng c&aacute;c video quay lại m&agrave;n h&igrave;nh k&eacute;m chất lượng.</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Tao hinh \'Vuon sao bang 2018\' que mua, dung lai nhac phim cu do F4 hat hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/unvjuas/2018_04_24/30264572_412935622466394_1538561069016940544_o.jpg\" style=\"height:647px; width:1024px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>D&agrave;n diễn vi&ecirc;n gồm F4 v&agrave; &quot;Cỏ dại&quot; Sam Th&aacute;i của&nbsp;<em>Vườn sao băng</em>&nbsp;phi&ecirc;n bản 2018.&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Sau khi trailer được c&ocirc;ng bố, kh&aacute;n giả tinh &yacute; nhận ra phi&ecirc;n bản 2018 vẫn sử dụng ca kh&uacute;c nhạc phim&nbsp;<em>Mưa sao băng</em>&nbsp;do F4 (d&agrave;n diễn vi&ecirc;n nam phi&ecirc;n bản năm 2000) tr&igrave;nh b&agrave;y. Ngay lập tức, cư d&acirc;n mạng đ&atilde; để lại h&agrave;ng loạt b&igrave;nh luận ủng hộ quyết định của nh&agrave; sản xuất. Nhiều &yacute; kiến tiết lộ ca kh&uacute;c chủ đề cũ khiến họ x&uacute;c động v&agrave; nhớ về k&yacute; ức xưa cũ của bản phim đầu ti&ecirc;n.</p>\n\n<p>B&ecirc;n cạnh lời khen ngợi d&agrave;nh cho phần nhạc phim, đoạn trailer 30 gi&acirc;y cũng thu về nhiều nhận x&eacute;t tr&aacute;i chiều về d&agrave;n diễn vi&ecirc;n trẻ cũng như sự đầu tư của &ecirc;-k&iacute;p sản xuất.</p>\n\n<p>Thẩm Nguyệt từng được nhận x&eacute;t mang vẻ đẹp của nữ ch&iacute;nh trong c&aacute;c bộ phim thanh xu&acirc;n, tương đối ph&ugrave; hợp với ngoại h&igrave;nh của &quot;Cỏ dại&quot; Sam Th&aacute;i. Tuy nhi&ecirc;n, c&aacute;c ph&acirc;n đoạn diễn xuất được tr&igrave;nh chiếu trong trailer khiến kh&aacute;n giả cho rằng c&ocirc; hơi &quot;hiền l&agrave;nh&quot; so với t&iacute;nh c&aacute;ch gốc của nh&acirc;n vật.&nbsp;</p>\n\n<table align=\"right\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Tao hinh \'Vuon sao bang 2018\' que mua, dung lai nhac phim cu do F4 hat hinh anh 2\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/unvjuas/2018_04_24/3934_1.jpg\" style=\"height:1024px; width:681px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>4 nam diễn vi&ecirc;n trẻ v&agrave;o vai F4 trong phi&ecirc;n bản mới từng được khen ngợi v&igrave; ngoại h&igrave;nh qu&aacute; đẹp.&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Trong 4 nam diễn vi&ecirc;n trẻ đảm nhận nh&acirc;n vật nh&oacute;m F4 của phi&ecirc;n bản mới, nam ch&iacute;nh Vương Hạc Đệ được cho l&agrave; thể hiện đ&uacute;ng thần th&aacute;i ngang t&agrave;n, ngạo nghễ đ&uacute;ng &quot;chuẩn&quot; nh&acirc;n vật Đạo Minh Tự. Ngoại h&igrave;nh điển trai v&agrave; tươi mới của Vương Hạc Đệ cũng khiến c&ocirc;ng ch&uacute;ng y&ecirc;u th&iacute;ch.</p>\n\n<p>Tr&aacute;i với Vương Hạc Đệ, Quan Hồng, người thủ vai Hoa Trạch Loại (vai diễn của Ch&acirc;u Du D&acirc;n), kh&ocirc;ng nhận được nhiều lời khen. Đa số &yacute; kiến người h&acirc;m mộ cho rằng Quan Hồng tuy đẹp trai nhưng tạo h&igrave;nh v&agrave; c&aacute;ch diễn của nam diễn vi&ecirc;n trẻ kh&ocirc;ng ph&ugrave; hợp với nh&acirc;n vật.&nbsp;</p>\n\n<p>Kh&ocirc;ng chỉ nhận &yacute; kiến tr&aacute;i chiều về d&agrave;n diễn vi&ecirc;n,&nbsp;<em>Vườn sao băng 2018</em>(t&ecirc;n kh&aacute;c l&agrave;&nbsp;<em>T&acirc;n Vườn sao băng</em>) c&ograve;n bị chỉ tr&iacute;ch v&igrave; phục trang &quot;k&eacute;m sang&quot; của F4. Xuất th&acirc;n l&agrave; con trai của c&aacute;c gia đ&igrave;nh gi&agrave;u c&oacute; bậc nhất, nhưng tạo h&igrave;nh của F4 vẫn chưa thể hiện được gia thế &quot;khủng&quot; của 4 ch&agrave;ng trai như nguy&ecirc;n t&aacute;c mi&ecirc;u tả.</p>\n\n<p>Tạo h&igrave;nh trang phục v&agrave; bối cảnh của&nbsp;<em>T&acirc;n Vườn sao băng</em>&nbsp;thậm ch&iacute; được cho l&agrave; kh&ocirc;ng sang trọng bằng phi&ecirc;n bản H&agrave;n Quốc đ&atilde; ph&aacute;t s&oacute;ng từ gần 10 năm trước. Ở một số cảnh, c&aacute;c nh&acirc;n vật c&ograve;n bị ch&ecirc; l&agrave; qu&ecirc; m&ugrave;a, lạc &quot;mốt&quot;.&nbsp;</p>\n\n<p>B&agrave; bầu S&agrave;i Tr&iacute; B&igrave;nh, người tạo n&ecirc;n phi&ecirc;n bản đầu ti&ecirc;n v&agrave; lăng x&ecirc; th&agrave;nh c&ocirc;ng nh&oacute;m F4, tiếp tục l&agrave; giữ vai tr&ograve; chỉ đạo sản xuất&nbsp;<em>T&acirc;n Vườn sao băng</em>. Đ&acirc;y cũng l&agrave; l&yacute; do c&ocirc;ng ch&uacute;ng đặt nhiều niềm tin v&agrave;o phi&ecirc;n bản remake năm 2018.&nbsp;</p>\n\n<p>Trước đ&oacute;, b&agrave; S&agrave;i Tr&iacute; B&igrave;nh từng tuy&ecirc;n bố ti&ecirc;u ch&iacute; tuyển chọn 4 th&agrave;nh vi&ecirc;n mới cho nh&oacute;m F4 l&agrave; đẹp trai, kh&ocirc;ng quan trọng khả năng diễn xuất. Tuy&ecirc;n bố của b&agrave; dẫn đến nhiều &yacute; kiến tranh c&atilde;i về mức độ th&agrave;nh c&ocirc;ng của&nbsp;<em>T&acirc;n Vườn sao băng.</em></p>\n\n<p>Tuy nhi&ecirc;n, kh&aacute;n giả vẫn b&agrave;y tỏ sự tr&ocirc;ng đợi v&agrave;o phi&ecirc;n bản 2018 v&agrave; cho rằng trailer 30 gi&acirc;y chưa đủ để nhận x&eacute;t về to&agrave;n bộ t&aacute;c phẩm. Bộ phim k&eacute;o d&agrave;i 48 tập v&agrave; sẽ được chỉnh sửa đề ph&ugrave; hợp với loại h&igrave;nh phim thanh xu&acirc;n vườn trường đang được y&ecirc;u th&iacute;ch thời gian gần đ&acirc;y.</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Tao hinh \'Vuon sao bang 2018\' que mua, dung lai nhac phim cu do F4 hat hinh anh 3\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/unvjuas/2018_04_24/4congtubofngayayvabaygiovertside.jpg\" style=\"height:500px; width:1773px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>F4 bản 2018 bị chỉ tr&iacute;ch v&igrave; tạo h&igrave;nh k&eacute;m xa hoa.&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p><em>Vườn sao băng</em>&nbsp;l&agrave; dự &aacute;n truyền h&igrave;nh chuyển thể từ bộ truyện tranh Nhật Bản nổi tiếng Hana Yori Dango (Con nh&agrave; gi&agrave;u). Nội dung phim xoay quanh mối quan hệ giữa c&ocirc; g&aacute;i nh&agrave; ngh&egrave;o Sam Th&aacute;i c&ugrave;ng nh&oacute;m F4 gồm 4 anh ch&agrave;ng đẹp trai, gi&agrave;u c&oacute; nhưng t&iacute;nh c&aacute;ch thường khiến người xung quanh kh&oacute; chịu.&nbsp;</p>\n\n<p>Ngo&agrave;i phi&ecirc;n bản đầu ti&ecirc;n v&agrave;o năm 2000 của Đ&agrave;i Loan, Hana Yori Dango từng được Nhật Bản, Trung Quốc v&agrave; H&agrave;n Quốc chuyển thể. Phi&ecirc;n bản H&agrave;n Quốc với t&ecirc;n gọi&nbsp;<em>Boys Over Flower</em>&nbsp;từng g&acirc;y &quot;sốt&quot; khắp ch&acirc;u &Aacute; v&agrave;o năm 2009 v&agrave; trở th&agrave;nh &quot;bệ ph&oacute;ng&quot; ng&ocirc;i sao của Lee Min Ho, Kim Bum, Kim Hyun Jung...</p>\n\n<p>Nghi&ecirc;m Ngọc</p>\n', '1524537023', '', 46, 0),
(60, 'Phim hoạt hình ‘Angry Birds’ ra mắt phần 2 trong mùa thu 2019', '016.jpg', 'Các nhà sản xuất tiếp tục đưa các chú “chim điên” lên màn ảnh vào năm tới, sau khi tập phim đầu tiên thu hơn 350 triệu USD hồi mùa hè cách đây hai năm.', '<p>C&aacute;c nh&agrave; sản xuất tiếp tục đưa c&aacute;c ch&uacute; &ldquo;chim đi&ecirc;n&rdquo; l&ecirc;n m&agrave;n ảnh v&agrave;o năm tới, sau khi tập phim đầu ti&ecirc;n thu hơn 350 triệu USD hồi m&ugrave;a h&egrave; c&aacute;ch đ&acirc;y hai năm.</p>\n\n<p>H&atilde;ng Sony Pictures v&agrave; Rovio Entertainment th&ocirc;ng b&aacute;o họ sẽ ph&aacute;t h&agrave;nh phần tiếp theo của&nbsp;<em>The Angry Birds Movie&nbsp;</em>từ 20/9/2019. Bộ phim được dựa tr&ecirc;n loạt tr&ograve; chơi di động ăn kh&aacute;ch&nbsp;<em>Angry Birds&nbsp;</em>của h&atilde;ng Rovio.</p>\n\n<p>Hai nh&agrave; đầu tư cũng đ&atilde; nhanh ch&oacute;ng k&yacute; hợp đồng mới với d&agrave;n diễn vi&ecirc;n lồng tiếng của phần trước. Theo đ&oacute;, Jason Sudeikis, Josh Gad, Bill Hader, Danny McBride v&agrave; Peter Dinklage sẽ c&ugrave;ng trở lại để g&oacute;p giọng cho tập phim mới.</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Phim hoat hinh ‘Angry Birds’ ra mat phan 2 trong mua thu 2019 hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/xbhunku/2018_04_23/016.jpg\" style=\"height:756px; width:1400px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>Phim hoạt h&igrave;nh&nbsp;<em>Angry Birds</em>&nbsp;ra tiếp phần hai trong m&ugrave;a thu 2019. Ảnh:&nbsp;<em>Sony</em>.</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Tuy nhi&ecirc;n, đạo diễn John Cohen của&nbsp;<em>The Angry Birds Movie&nbsp;</em>nay chỉ c&ograve;n giữ vai tr&ograve; nh&agrave; sản xuất. &Ocirc;ng nhường lại vị tr&iacute; chỉ đạo cho John Rice v&agrave; Thurop Van Orman ở phần tiếp theo.</p>\n\n<p>Hồi m&ugrave;a h&egrave; 2016,&nbsp;<em>The Angry Birds Movie&nbsp;</em>từng mang về cho nh&agrave; sản xuất khoảng 352 triệu USD tr&ecirc;n khắp to&agrave;n cầu. T&aacute;c phẩm hoạt h&igrave;nh đồng thời mở m&agrave;n ở vị tr&iacute; số một tại 50 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ m&agrave; n&oacute; c&oacute; mặt.</p>\n\n<p>Ra đời năm 2009,&nbsp;<em>Angry Birds&nbsp;</em>l&agrave; thương hiệu tr&ograve; chơi đ&igrave;nh đ&aacute;m do Rovio Entertainment s&aacute;ng tạo. Sau gần 10 năm, đ&atilde; c&oacute; rất nhiều sản phẩm ăn theo tựa game ăn kh&aacute;ch, v&agrave; gi&uacute;p c&ocirc;ng ty của Phần Lan vượt qua kh&ocirc;ng &iacute;t kh&oacute; khăn về mặt t&agrave;i ch&iacute;nh trong thời gian qua.</p>\n\n<p>Tuấn Lương</p>\n', '1524537174', '', 46, 0),
(61, 'Chi phí sản xuất ‘Avengers: Infinity War’ cao thứ 2 lịch sử Hollywood', 'infinity3.jpg', 'Chi phí sản xuất của bom tấn “Avengers: Infinity War” thuộc Vũ trụ Điện ảnh Marvel (MCU) lên đến 300 triệu USD, cao thứ hai trong lịch sử Hollywood.', '<p>Chi ph&iacute; sản xuất của bom tấn &ldquo;Avengers: Infinity War&rdquo; thuộc Vũ trụ Điện ảnh Marvel (MCU) l&ecirc;n đến 300 triệu USD, cao thứ hai trong lịch sử Hollywood.</p>\n\n<p><strong><a href=\"https://news.zing.vn/video-trailer-chinh-thuc-bom-tan-avengers-cuoc-chien-vo-cuc-post826749.html\" target=\"_blank\">Trailer ch&iacute;nh thức bom tấn &#39;Avengers: Cuộc chiến v&ocirc; cực&#39;</a></strong>&nbsp;Phần tiếp theo của loạt phim &quot;The Avengers&quot; v&agrave; thuộc Vũ trụ Điện ảnh Marvel (MCU), dự kiến khởi chiếu tại Việt Nam từ 25/4.</p>\n\n<p>Theo nguồn tin của b&aacute;o<em>&nbsp;Wall Street Journal</em>, mức đầu tư d&agrave;nh cho&nbsp;<em>Avengers: Infinity War</em>&nbsp;l&agrave; 300 triệu USD. Khoản tiền n&agrave;y tương đương với kinh ph&iacute; sản xuất của&nbsp;<em>Justice League</em>&nbsp;v&agrave;&nbsp;<em>Pirates of the Caribbean: At World&rsquo;s End</em>. Chỉ c&oacute; một bộ phim ti&ecirc;u tốn số tiền nhiều hơn 3 bom tấn n&agrave;y l&agrave;&nbsp;<em>Pirates of the Caribbean: On Stranger Tides</em>&nbsp;với 378,5 triệu USD.&nbsp;</p>\n\n<p>Trước đ&oacute;, dự &aacute;n ti&ecirc;u tốn nhiều tiền nhất của MCU l&agrave;&nbsp;<em>Avengers: Age of Ultron</em>với 279,8 triệu USD. Chi ph&iacute; của&nbsp;<em>Infinity War</em>&nbsp;bị dội l&ecirc;n cũng l&agrave; điều dễ hiểu bởi đ&acirc;y l&agrave; t&aacute;c phẩm đ&aacute;nh dấu 10 năm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển của MCU, tập trung cả hai nh&oacute;m si&ecirc;u anh h&ugrave;ng Avengers v&agrave; Guardians of the Galaxy.&nbsp;</p>\n\n<p>D&ugrave; vậy, c&oacute; lẽ h&atilde;ng Disney kh&ocirc;ng cần qu&aacute; lo lắng. Bởi theo Screen Rant, c&aacute;c khảo s&aacute;t đều cho thấy&nbsp;<em>Avengers:</em>&nbsp;<em>Infinity War</em>&nbsp;c&oacute; thể đạt doanh thu tối đa 255 triệu USD trong 3 ng&agrave;y c&ocirc;ng chiếu đầu ti&ecirc;n tại Bắc Mỹ, vượt qua kỷ lục 247,9 triệu USD của&nbsp;<em>Star Wars: The Force Awakens</em>.&nbsp;&nbsp;</p>\n\n<table align=\"center\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img alt=\"Chi phi san xuat ‘Avengers: Infinity War’ cao thu 2 lich su Hollywood hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/unvjuas/2018_04_21/infinity3.jpg\" style=\"height:972px; width:1614px\" /></p>\n			</td>\n		</tr>\n		<tr>\n			<td>&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p><br />\nMinh Phụng</p>\n', '1524537323', '', 46, 0),
(62, 'Ông Lê Văn Khoa xin thôi chức Phó chủ tịch UBND TP HCM', 'PCT-Le-Van-Khoa-3191-1524489486.jpg', 'Sau gần 2 năm rưỡi làm Phó chủ tịch UBND TP HCM phụ trách đô thị, giao thông, ông Lê Văn Khoa xin thôi chức vì lý do sức khỏe.', '<h2>Sau gần 2 năm rưỡi l&agrave;m Ph&oacute; chủ tịch UBND TP HCM phụ tr&aacute;ch đ&ocirc; thị, giao th&ocirc;ng, &ocirc;ng L&ecirc; Văn Khoa xin th&ocirc;i chức v&igrave; l&yacute; do sức khỏe.</h2>\n\n<p><a href=\"https://vnexpress.net/tin-tuc/thoi-su/tp-hcm-co-them-2-pho-chu-tich-3390535.html#ctr=related_news_click\">TP HCM c&oacute; th&ecirc;m 2 Ph&oacute; chủ tịch</a></p>\n\n<p>Chiều 23/4, &ocirc;ng L&ecirc; Văn Khoa cho biết đ&atilde; c&oacute; đơn xin th&ocirc;i giữ chức Ph&oacute; chủ tịch UBND TP HCM. &quot;Thời gian qua t&ocirc;i đ&atilde; hai lần gặp vấn đề về sức khỏe, kh&ocirc;ng thể đảm đương vị tr&iacute; n&agrave;y n&ecirc;n xin th&ocirc;i chức vụ&quot;, &ocirc;ng Khoa n&oacute;i.</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Phó chủ tịch UBND TP HCM Lê Văn Khoa. Ảnh: Ngọc Hậu\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/PCT-Le-Van-Khoa-3191-1524489486.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Ph&oacute; chủ tịch UBND TP HCM L&ecirc; Văn Khoa. Ảnh:&nbsp;<em>Ngọc Hậu.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Dự kiến ng&agrave;y mai (24/4), HĐND TP HCM kh&oacute;a IX (2016-2021) sẽ họp bất thường để bỏ phiếu miễn nhiệm chức vụ Ph&oacute; chủ tịch UBND th&agrave;nh phố đối với &ocirc;ng L&ecirc; Văn Khoa. Tuy nhi&ecirc;n, c&oacute; thể kỳ họp n&agrave;y chưa bầu người thay thế chức vụ của &ocirc;ng Khoa.</p>\n\n<p>&Ocirc;ng L&ecirc; Văn Khoa sinh năm 1961, qu&ecirc; Quảng Nam; cử nh&acirc;n luật, cử nh&acirc;n h&agrave;nh ch&iacute;nh, cử nh&acirc;n l&yacute; luận ch&iacute;nh trị.</p>\n\n<p>Tại kỳ họp thứ 20 HĐND TP HCM kh&oacute;a VIII v&agrave;o cuối năm 2015, &ocirc;ng Khoa được bầu l&agrave;m Ph&oacute; chủ tịch UBND th&agrave;nh phố. &Ocirc;ng được ph&acirc;n c&ocirc;ng phụ tr&aacute;ch mảng đ&ocirc; thị, giao th&ocirc;ng vận tải.</p>\n\n<p>&Ocirc;ng Khoa giữ cương vị n&agrave;y được gần 2 năm rưỡi. Hiện, mảng c&ocirc;ng việc của &ocirc;ng đ&atilde; được Ph&oacute; chủ tịch UBND th&agrave;nh phố Trần Vĩnh Tuyến phụ tr&aacute;ch.</p>\n\n<p>Trước khi đảm nhận vị tr&iacute; Ph&oacute; chủ tịch UBND th&agrave;nh phố, &ocirc;ng Khoa từng kinh qua c&aacute;c chức vụ Chủ tịch UBND quận 5; Ph&oacute; gi&aacute;m đốc, rồi Gi&aacute;m đốc Sở C&ocirc;ng Thương th&agrave;nh phố.</p>\n\n<p><img alt=\"Phó chủ tịch UBND TP HCM Lê Văn Khoa xin thôi chức \" src=\"https://iv.vnecdn.net/vnexpress/images/web/2018/04/23/pho-chu-tich-ubnd-tp-hcm-le-van-khoa-xin-thoi-chuc-1524493093_500x300.png\" /></p>\n\n<p>Video:&nbsp;<em>Ho&agrave;ng Kh&aacute;nh</em></p>\n', '1524537402', '', 1, 0),
(63, 'Bình cứu hỏa chung cư Hà Nội không hoạt động khi bị kiểm tra đột xuất', '31150337-1835447506475863-3294-1873-7446-1524495002.jpg', 'Tại chung cư M3-M4 số 91 Nguyễn Chí Thanh, một số cửa chống cháy cơ cấu tự đóng kém, đèn chỉ dẫn thoát nạn không đảm bảo...', '<h2>Tại chung cư M3-M4 số 91 Nguyễn Ch&iacute; Thanh, một số cửa chống ch&aacute;y cơ cấu tự đ&oacute;ng k&eacute;m, đ&egrave;n chỉ dẫn tho&aacute;t nạn kh&ocirc;ng đảm bảo...</h2>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Chuông báo trong khu chung cư M3-M4 bị hỏng, không thể hoạt động. Ảnh: Phương Sơn\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/chung-cu-m3-6624-1524489314.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Chu&ocirc;ng b&aacute;o trong khu chung cư M3-M4 bị hỏng. Ảnh:&nbsp;<em>Phương Sơn</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Ng&agrave;y 23/4, Bộ C&ocirc;ng an v&agrave; Bộ X&acirc;y Dựng bắt đầu mở đợt tổng kiểm tra nh&agrave; chung cư, nh&agrave; cao tầng, cơ sở tập trung đ&ocirc;ng người c&oacute; nguy cơ ch&aacute;y nổ tại 7 tỉnh, th&agrave;nh phố lớn.</p>\n\n<p>C&aacute;c tổ c&ocirc;ng t&aacute;c kiểm tra đột xuất nhiều chung cư ở trung t&acirc;m H&agrave; Nội. Tại khu nh&agrave; M3-M4-M5, địa chỉ số 91 Nguyễn Ch&iacute; Thanh (phường L&aacute;ng Hạ, Đống Đa), nơi c&oacute; 256 căn hộ với tr&ecirc;n 1.000 người d&acirc;n v&agrave; 20 căn hộ cho thu&ecirc; văn ph&ograve;ng, đo&agrave;n kiểm tra ph&aacute;t hiện h&agrave;ng loạt sai phạm về ph&ograve;ng ch&aacute;y chữa ch&aacute;y.</p>\n\n<p>Tại t&ograve;a M3-M4, khi kiểm tra hệ thống b&aacute;o ch&aacute;y tự động, b&igrave;nh cứu hỏa kh&ocirc;ng hoạt động. Một số cửa chống ch&aacute;y tại buồng thang bộ cơ cấu tự đ&oacute;ng k&eacute;m. Đ&egrave;n chỉ dẫn tho&aacute;t nạn, đ&egrave;n chiếu s&aacute;ng sự cố kh&ocirc;ng đảm bảo. Ban quản l&yacute; t&ograve;a nh&agrave; chưa tổ chức mua bảo hiểm ch&aacute;y, nổ tại t&ograve;a nh&agrave;&hellip;</p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Các bình chữa cháy chung chung cư M3-M4 không thể hoạt động. Ảnh: Tuấn Sơn\" src=\"https://i-vnexpress.vnecdn.net/2018/04/23/31150337-1835447506475863-3294-1873-7446-1524495002.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>C&aacute;c b&igrave;nh chữa ch&aacute;y chung&nbsp;cư M3-M4 kh&ocirc;ng thể hoạt động. Ảnh:<em>&nbsp;Tuấn Sơn</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Chung cư M5 kh&ocirc;ng c&oacute; đội ph&ograve;ng ch&aacute;y chữa ch&aacute;y cơ sở, việc ph&ograve;ng ch&aacute;y chữa ch&aacute;y tại chỗ chưa được quan t&acirc;m.</p>\n\n<p>Kết th&uacute;c kiểm tra tại chung cư M3-M4, Thứ trưởng C&ocirc;ng an B&ugrave;i Văn Th&agrave;nh cho rằng việc ph&ograve;ng ch&aacute;y chữa ch&aacute;y ở t&ograve;a nh&agrave; chưa đảm bảo, nếu c&oacute; sự cố nguy cơ thiệt hại rất nghi&ecirc;m trọng. &Ocirc;ng đề nghị chủ đầu tư khắc phục ngay v&agrave; đưa ra c&aacute;c giải ph&aacute;p để đảm bảo an to&agrave;n ph&ograve;ng ch&aacute;y chữa ch&aacute;y cho người d&acirc;n.</p>\n\n<p>Theo Bộ C&ocirc;ng an, to&agrave;n quốc hiện c&oacute; 4.100 chung cư, nh&agrave; cao tầng, ri&ecirc;ng 7 tỉnh th&agrave;nh phố lớn (gồm H&agrave; Nội, Hải Ph&ograve;ng, Đ&agrave; Nẵng, TP HCM, Cần Thơ, B&agrave; Rịa - Vũng T&agrave;u, Kh&aacute;nh H&ograve;a) c&oacute; tới 3.600 chung cư.</p>\n\n<p><strong>Phương Sơn</strong></p>\n', '1524537483', '', 1, 0),
(64, 'Hành tinh hấp thụ 99% ánh sáng Mặt Trời', 'Capture-1524536797_500x300.jpg', 'Hành tinh \"sao Mộc nóng\" WASP-104b tối đến mức hấp thụ gần như tất cả ánh sánh Mặt Trời chiếu đến.', '<h2>H&agrave;nh tinh &quot;sao Mộc n&oacute;ng&quot; WASP-104b tối đến mức hấp thụ gần như tất cả &aacute;nh s&aacute;nh Mặt Trời chiếu đến.</h2>\n\n<p><a href=\"https://vnexpress.net/infographics/khoa-hoc/hanh-tinh-giong-trai-dat-duoc-tri-tue-nhan-tao-cua-google-phat-hien-3686219.html#ctr=related_news_click\">H&agrave;nh tinh giống Tr&aacute;i Đất được tr&iacute; tuệ nh&acirc;n tạo của Google ph&aacute;t hiện</a></p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"WASP-104b là một trong những hành tinh tối nhất từng được phát hiện. Ảnh minh họa: NASA.\" src=\"https://i-vnexpress.vnecdn.net/2018/04/24/Capture-7425-1524536852.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>WASP-104b l&agrave; một trong những h&agrave;nh tinh tối nhất từng được ph&aacute;t hiện. Ảnh minh họa:&nbsp;<em>NASA.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>WASP-104b c&oacute; thể l&agrave; h&agrave;nh tinh tối nhất từng được ph&aacute;t hiện. H&agrave;nh tinh thuộc nh&oacute;m sao Mộc n&oacute;ng n&agrave;y được bao phủ bởi lớp sương m&ugrave; hấp thụ 97 - 99% &aacute;nh s&aacute;ng chiếu tới bề mặt. Nh&oacute;m nghi&ecirc;n cứu ở Đại học Keele tại Newscastle, Anh, kh&aacute;m ph&aacute; WASP-104b trong nghi&ecirc;n cứu xuất bản tr&ecirc;n tạp ch&iacute; arXiv h&ocirc;m 15/4, theo&nbsp;<em>Newsweek</em>.</p>\n\n<p>&quot;Trong mọi h&agrave;nh tinh tối m&agrave; t&ocirc;i c&oacute; thể t&igrave;m thấy trong c&aacute;c nghi&ecirc;n cứu, h&agrave;nh tinh n&agrave;y thuộc top năm&quot;, nh&agrave; nghi&ecirc;n cứu Teo Mocnik cho biết. &quot;T&ocirc;i nghĩ n&oacute; thuộc top ba&quot;.</p>\n\n<p>WASP-104b v&agrave; hai h&agrave;nh tinh cực tối kh&aacute;c l&agrave; TrES-2b v&agrave; HAT-P-7b phản chiếu &iacute;t &aacute;nh s&aacute;ng như nhau, do đ&oacute; rất kh&oacute; kết luận ch&iacute;nh x&aacute;c h&agrave;nh tinh n&agrave;o tối nhất. Ch&ecirc;nh lệch trong mức &aacute;nh s&aacute;ng phản chiếu nhỏ tới mức c&aacute;c nh&agrave; nghi&ecirc;n cứu kh&ocirc;ng thể đo r&otilde; r&agrave;ng, theo New Scientist.</p>\n\n<p>WASP-104b&nbsp; c&oacute; khối lượng tương đương sao Mộc v&agrave; quỹ đạo quay gần ng&ocirc;i sao chủ n&ecirc;n được xếp v&agrave;o nh&oacute;m &quot;sao Mộc n&oacute;ng&quot;. Phần lớn c&aacute;c h&agrave;nh tinh sao Mộc n&oacute;ng đều tối, chỉ phản chiếu tối đa khoảng 40% &aacute;nh s&aacute;ng chiếu đến bề mặt. Tuy nhi&ecirc;n, WASP-104b l&agrave; trường hợp đặc biệt.</p>\n\n<p>H&agrave;nh tinh ở gần ng&ocirc;i sao chủ tới mức chỉ mất 1,75 ng&agrave;y để ho&agrave;n th&agrave;nh một v&ograve;ng quỹ đạo. Khoảng c&aacute;ch gần l&agrave; yếu tố quan trọng tạo n&ecirc;n độ tối ấn tượng của WASP-104b, cho ph&eacute;p bức xạ từ sao chủ xuy&ecirc;n qua những đ&aacute;m m&acirc;y của h&agrave;nh tinh, giải ph&oacute;ng kali v&agrave; natri hấp thụ &aacute;nh s&aacute;ng sao.</p>\n\n<p>Nh&oacute;m nghi&ecirc;n cứu ch&uacute; &yacute; đến WASP-104b lần đầu ti&ecirc;n năm 2014&nbsp; trong dự &aacute;n T&igrave;m kiếm H&agrave;nh tinh G&oacute;c rộng v&agrave; k&iacute;nh viễn vọng vũ trụ Kepler sau đ&oacute; h&eacute; lộ khả năng hấp thụ &aacute;nh s&aacute;ng của n&oacute;. C&aacute;c nh&agrave; thi&ecirc;n văn học cũng quan s&aacute;t WASP-104b một c&aacute;ch gi&aacute;n tiếp khi n&oacute; đi qua ph&iacute;a trước v&agrave; che khuất một phần &aacute;nh s&aacute;ng của ng&ocirc;i sao chủ nhiều lần. Qu&aacute; tr&igrave;nh lướt qua n&agrave;y h&eacute; lộ th&ocirc;ng tin quan trọng về WASP-104b, bao gồm lượng &aacute;nh s&aacute;ng m&agrave; n&oacute; hấp thụ.</p>\n', '1524537583', '', 5, 0),
(65, 'SpaceX sắp phóng tên lửa mang vệ tinh hơn 6 tấn', 'SpaceX-trong-bai-1351-1520310580.jpg', 'Tên lửa Falcon 9 sẽ đưa vệ tinh quỹ đạo địa tĩnh lớn nhất SpaceX từng phóng lên không gian, đánh dấu lần phóng thứ 50 của hãng này.', '<h2>T&ecirc;n lửa Falcon 9 sẽ đưa vệ tinh quỹ đạo địa tĩnh lớn nhất SpaceX từng ph&oacute;ng l&ecirc;n kh&ocirc;ng gian, đ&aacute;nh dấu lần ph&oacute;ng thứ 50 của h&atilde;ng n&agrave;y.</h2>\n\n<p><a href=\"https://vnexpress.net/infographics/khoa-hoc/doi-thu-cua-spacex-che-tao-ten-lua-tai-su-dung-dong-co-day-3716160.html#ctr=related_news_click\">Đối thủ của SpaceX chế tạo t&ecirc;n lửa t&aacute;i sử dụng động cơ đẩy</a>&nbsp; / &nbsp;<a href=\"https://vnexpress.net/tin-tuc/khoa-hoc/he-thong-ten-lua-moi-cua-spacex-thay-the-falcon-heavy-3711079.html#ctr=related_news_click\">Hệ thống t&ecirc;n lửa mới của SpaceX thay thế Falcon Heavy</a></p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"SpaceX phóng nhiều tên lửa trong thời gian ngắn với tỷ lệ thành công rất cao. Ảnh: SwiftNary.\" src=\"https://i-vnexpress.vnecdn.net/2018/03/06/SpaceX-trong-bai-1351-1520310580.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>SpaceX ph&oacute;ng nhiều&nbsp;t&ecirc;n lửa&nbsp;chỉ trong 7 năm với tỷ lệ th&agrave;nh c&ocirc;ng rất cao. Ảnh:&nbsp;<em>SwiftNary.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>SpaceX sẽ ph&oacute;ng t&ecirc;n lửa Falcon 9 trong khung giờ 12h33 - 14h33 h&ocirc;m nay tại Florida, Mỹ,<em>&nbsp;Futurism</em>&nbsp;đưa tin. T&ecirc;n lửa n&agrave;y sẽ đưa Hispasat 30W-6, vệ tinh quỹ đạo địa tĩnh lớn nhất SpaceX từng ph&oacute;ng, bay l&ecirc;n kh&ocirc;ng gian. Vệ tinh Hispasat 30W-6 nặng hơn 6.092 kg, c&oacute; thể hoạt động khoảng 15 năm.</p>\n\n<p>Với lần ph&oacute;ng n&agrave;y, c&ocirc;ng ty viễn th&ocirc;ng T&acirc;y Ban Nha Hispasat c&oacute; thể mở rộng dịch vụ của m&igrave;nh ở ch&acirc;u &Acirc;u, ch&acirc;u Phi v&agrave; ch&acirc;u Mỹ. Đ&acirc;y cũng l&agrave; lần ph&oacute;ng t&ecirc;n lửa thứ 50 của SpaceX, đ&aacute;nh dấu một cột mốc đ&aacute;ng nhớ trong qu&aacute; tr&igrave;nh hoạt động của h&atilde;ng n&agrave;y.</p>\n\n<p>Đặc biệt hơn, SpaceX đạt mốc n&agrave;y chỉ trong v&ograve;ng 7 năm. Lần đầu ti&ecirc;n t&ecirc;n lửa Falcon 9 được ph&oacute;ng l&ecirc;n kh&ocirc;ng gian l&agrave; th&aacute;ng 6/2010. Trong khi đ&oacute;, t&ecirc;n lửa Atlas V ra đời từ sự hợp t&aacute;c giữa Lockheed Martin v&agrave; Boeing, mất đến hơn 9 năm để chạm mốc n&agrave;y.</p>\n\n<p>SpaceX kh&ocirc;ng chỉ ph&oacute;ng nhiều t&ecirc;n lửa trong thời gian ngắn m&agrave; kết quả của những lần ph&oacute;ng đều rất tốt. C&oacute; tổng số 48 lần ph&oacute;ng diễn ra thuận lợi, nghĩa l&agrave; tỷ lệ ph&oacute;ng th&agrave;nh c&ocirc;ng l&ecirc;n đến 96%.</p>\n\n<p><strong>Thu Thảo</strong></p>\n', '1524537681', '', 5, 0),
(66, 'Trung Quốc sẽ đưa cây và côn trùng lên Mặt Trăng năm 2018', '20160128-PCAMR-C-001-SCI-N-201-2681-6515-1515236133.jpg', 'Các nhà khoa học sẽ đưa hạt giống và trứng tằm tơ lên Mặt Trăng trong nhiệm vụ không gian Hằng Nga 4.', '<h2>C&aacute;c nh&agrave; khoa học sẽ đưa hạt giống v&agrave; trứng tằm tơ l&ecirc;n Mặt Trăng trong nhiệm vụ kh&ocirc;ng gian Hằng Nga 4.</h2>\n\n<p><a href=\"https://vnexpress.net/tin-tuc/khoa-hoc/tram-vu-tru-8-5-tan-cua-trung-quoc-se-dam-xuong-trai-dat-cuoi-thang-3-3694972.html#ctr=related_news_click\">Trạm vũ trụ 8,5 tấn của Trung Quốc sẽ đ&acirc;m xuống Tr&aacute;i Đất cuối th&aacute;ng 3</a>&nbsp; / &nbsp;<a href=\"https://vnexpress.net/infographics/khoa-hoc/tau-vu-tru-trung-quoc-sap-do-bo-mat-toi-cua-mat-trang-3693603.html#ctr=related_news_click\">T&agrave;u vũ trụ Trung Quốc sắp đổ bộ mặt tối của Mặt Trăng</a></p>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td><img alt=\"Trung Quốc sẽ nghiên cứu tác động của môi trường trên Mặt Trăng đến sinh vật Trái Đất. Ảnh: Ancient Code.\" src=\"https://i-vnexpress.vnecdn.net/2018/01/06/20160128-PCAMR-C-001-SCI-N-201-2681-6515-1515236133.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Trung Quốc sẽ nghi&ecirc;n cứu t&aacute;c động của m&ocirc;i trường tr&ecirc;n Mặt Trăng đến sinh vật Tr&aacute;i Đất. Ảnh:&nbsp;<em>Ancient Code.</em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Trung Quốc dự định thực hiện nhiệm vụ kh&ocirc;ng gian Hằng Nga 4 nhằm nghi&ecirc;n cứu địa chất v&agrave; t&igrave;m hiểu t&aacute;c động của lực hấp dẫn tr&ecirc;n Mặt Trăng đến c&ocirc;n tr&ugrave;ng v&agrave; thực vật từ Tr&aacute;i Đất trong năm nay,&nbsp;<em>Futurism</em>&nbsp;h&ocirc;m 5/1 đưa tin.</p>\n\n<p>Trước đ&oacute;, Trung Quốc đ&atilde; đưa hai t&agrave;u vũ trụ l&ecirc;n quỹ đạo Mặt Trăng v&agrave; một phương tiện th&aacute;m hiểm hạ c&aacute;nh xuống bề mặt nơi n&agrave;y theo chương tr&igrave;nh Kh&aacute;m ph&aacute; Mặt trăng Trung Quốc (CLEP), hay chương tr&igrave;nh Hằng Nga.</p>\n\n<p>Nhiệm vụ Hằng Nga 4 sẽ bắt đầu v&agrave;o th&aacute;ng 6 với sự kiện ph&oacute;ng t&ecirc;n lửa Long March 5 mang theo một t&agrave;u vũ trụ l&ecirc;n quỹ đạo Mặt Trăng. S&aacute;u th&aacute;ng sau khi con t&agrave;u đến vị tr&iacute; dự định tr&ecirc;n quỹ đạo, một phương tiện tiếp đất c&ugrave;ng xe th&aacute;m hiểm sẽ được ph&oacute;ng xuống bề mặt Mặt Trăng.</p>\n\n<p>Phương tiện tiếp đất n&agrave;y mang theo một th&ugrave;ng hợp kim nh&ocirc;m đựng hạt giống v&agrave; c&ocirc;n tr&ugrave;ng sống phục vụ nghi&ecirc;n cứu, gồm khoai t&acirc;y, hạt cải v&agrave; trứng tằm tơ, theo Zhang Yuanxun, chuy&ecirc;n gia thiết kế ch&iacute;nh của th&ugrave;ng chứa. Tằm tơ nở ra từ trứng sẽ sản sinh CO2. Trong khi đ&oacute;, khoai t&acirc;y v&agrave; hạt cải sẽ tạo ra oxy nhờ qu&aacute; tr&igrave;nh quang hợp.</p>\n\n<p>&quot;Ch&uacute;ng c&oacute; thể c&ugrave;ng nhau thiết lập một hệ sinh th&aacute;i đơn giản tr&ecirc;n Mặt Trăng&quot;, Yuanxum cho biết. Việc mang hạt giống v&agrave; c&ocirc;n tr&ugrave;ng l&ecirc;n Mặt Trăng c&oacute; thể gi&uacute;p x&aacute;c định tiềm năng con người sinh sống v&agrave; l&agrave;m việc tr&ecirc;n đ&oacute;.</p>\n\n<p>C&aacute;c chuy&ecirc;n gia hiện nay chưa r&otilde; liệu sinh vật Tr&aacute;i Đất c&oacute; thể ph&aacute;t triển tốt tr&ecirc;n Mặt Trăng, nơi lực hấp dẫn chỉ bằng khoảng 16% Tr&aacute;i Đất, hay kh&ocirc;ng. Điểm đến của nhiệm vụ Hằng Nga 4 l&agrave; v&ugrave;ng trũng cực Nam - Aitken, khu vực được nhiều người cho rằng c&oacute; thể l&agrave; địa điểm l&yacute; tưởng để x&acirc;y dựng căn cứ tr&ecirc;n Mặt Trăng.</p>\n\n<p>Lực hấp dẫn nhỏ g&acirc;y ra một số t&aacute;c động nghi&ecirc;m trọng đến sức khỏe con người. C&aacute;c phi h&agrave;nh gia th&aacute;m hiểm Mặt Trăng được trang bị dụng cụ bảo vệ để ngăn chặn những ảnh hưởng ti&ecirc;u cực của lực hấp dẫn tại đ&acirc;y. Tuy nhi&ecirc;n, việc sống ở một căn cứ tr&ecirc;n Mặt Trăng trong thời gian k&eacute;o d&agrave;i c&oacute; thể dẫn đến những vấn đề kh&ocirc;ng thể lường trước. &nbsp;</p>\n\n<p><strong>Thu Thảo</strong></p>\n', '1524537743', '', 5, 0),
(67, 'Cảnh báo mưa diện rộng ở Bắc Bộ và miền Trung', '1_25564.jpg', 'PLO) - Dự báo, hôm nay, Bắc Bộ (trong đó có tỉnh Phú Thọ) và các tỉnh bắc Trung Bộ có mưa rào và rải rác có dông; từ chiều và tối nay ở Bắc Bộ và Bắc Trung Bộ có mưa, mưa vừa, có nơi mưa to và dông nhiều nơi, sau đó vùng mưa dông có khả năng mở rộng...', '<p>(PLO) - Dự b&aacute;o, h&ocirc;m nay, Bắc Bộ (trong đ&oacute; c&oacute; tỉnh Ph&uacute; Thọ) v&agrave; c&aacute;c tỉnh bắc Trung Bộ c&oacute; mưa r&agrave;o v&agrave; rải r&aacute;c c&oacute; d&ocirc;ng; từ chiều v&agrave; tối nay ở Bắc Bộ v&agrave; Bắc Trung Bộ c&oacute; mưa, mưa vừa, c&oacute; nơi mưa to v&agrave; d&ocirc;ng nhiều nơi, sau đ&oacute; v&ugrave;ng mưa d&ocirc;ng c&oacute; khả năng mở rộng...</p>\n\n<p>Theo Trung t&acirc;m Dự b&aacute;o kh&iacute; tượng Thủy văn Trung ương, do ảnh hưởng của r&atilde;nh &aacute;p thấp bị n&eacute;n bởi khối kh&ocirc;ng kh&iacute; lạnh, h&ocirc;m nay, 24/4, Bắc Bộ (trong đ&oacute; c&oacute; tỉnh Ph&uacute; Thọ) v&agrave; c&aacute;c tỉnh bắc Trung Bộ c&oacute; mưa r&agrave;o v&agrave; rải r&aacute;c c&oacute; d&ocirc;ng; từ chiều v&agrave; tối nay ở Bắc Bộ v&agrave; Bắc Trung Bộ c&oacute; mưa, mưa vừa, c&oacute; nơi mưa to v&agrave; d&ocirc;ng nhiều nơi, sau đ&oacute; v&ugrave;ng mưa d&ocirc;ng c&oacute; khả năng mở rộng xuống khu vực Trung v&agrave; Nam Trung Bộ.</p>\n\n<p>&quot;Trong cơn d&ocirc;ng c&oacute; khả năng xuất hiện tố lốc, mưa đ&aacute; v&agrave; gi&oacute; giật mạnh&quot;, Trung t&acirc;m Dự b&aacute;o Kh&iacute; tượng Thủy văn Trung ương khuyến c&aacute;o.</p>\n\n<p><strong>Dự b&aacute;o thời tiết c&aacute;c khu vực ng&agrave;y v&agrave; đ&ecirc;m nay, 24/3:</strong></p>\n\n<p>Ph&iacute;a&nbsp;<em>T&acirc;y Bắc Bộ</em>: Ng&agrave;y m&acirc;y thay đổi, c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng v&agrave;i nơi; chiều tối v&agrave; đ&ecirc;m nhiều m&acirc;y, c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng rải r&aacute;c. Gi&oacute; nhẹ. Trong cơn d&ocirc;ng c&oacute; khả năng xảy ra tố, lốc, mưa đ&aacute; v&agrave; gi&oacute; giật mạnh. Độ ẩm từ 55-97%. Nhiệt độ thấp nhất 21-24 độ c, c&oacute; nơi dưới 20 độ C, cao nhất 30-33 độ C, ri&ecirc;ng khu T&acirc;y Bắc c&oacute; nơi tr&ecirc;n 34 độ C.</p>\n\n<p>Ph&iacute;a&nbsp;<em>Đ&ocirc;ng Bắc Bộ</em>: Nhiều m&acirc;y, ng&agrave;y c&oacute; mưa, mưa r&agrave;o v&agrave; d&ocirc;ng rải r&aacute;c; đ&ecirc;m c&oacute; mưa, c&oacute; nơi mưa vừa, mưa to v&agrave; rải r&aacute;c c&oacute; d&ocirc;ng. Gi&oacute; đ&ocirc;ng nam cấp 2-3. Trong cơn d&ocirc;ng c&oacute; khả năng xảy ra tố, lốc, mưa đ&aacute; v&agrave; gi&oacute; giật mạnh. Độ ẩm từ 56-98%. Nhiệt độ thấp nhất 22-25 độ C, cao nhất 26 - 29 độ C.</p>\n\n<p><em>Thanh H&oacute;a - Thừa Thi&ecirc;n Huế</em>: M&acirc;y thay đổi, c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng v&agrave;i nơi; ph&iacute;a Bắc từ chiều tối nhiều m&acirc;y, c&oacute; mưa, c&oacute; nơi mưa vừa, mưa to v&agrave; rải r&aacute;c c&oacute; d&ocirc;ng; ph&iacute;a Nam ng&agrave;y c&oacute; nơi c&oacute; nắng n&oacute;ng, chiều tối c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng rải r&aacute;c. Gi&oacute; nhẹ. Trong cơn d&ocirc;ng c&oacute; khả năng xảy ra tố, lốc, mưa đ&aacute; v&agrave; gi&oacute; giật mạnh. Độ ẩm từ 55-98%. Nhiệt độ thấp nhất 23-26 độ C, cao nhất 29-32 độ C; ph&iacute;a Nam từ 32 - 35 độ, c&oacute; nơi tr&ecirc;n 36 độ C.</p>\n\n<p><em>Đ&agrave; Nẵng đến B&igrave;nh Thuận</em>: M&acirc;y thay đổi, ng&agrave;y nắng, c&oacute; nơi c&oacute; nắng n&oacute;ng, chiều tối v&agrave; đ&ecirc;m c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng v&agrave;i nơi. Gi&oacute; nhẹ. Độ ẩm từ 50-96%. Nhiệt độ thấp nhất 23-26 độ C, cao nhất 33-36 độ C.</p>\n\n<p><em>T&acirc;y Nguy&ecirc;n</em>: M&acirc;y thay đổi, ng&agrave;y nắng, chiều tối c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng rải r&aacute;c, đ&ecirc;m c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng v&agrave;i nơi. Gi&oacute; nhẹ. Trong cơn d&ocirc;ng c&oacute; khả năng xảy ra tố, lốc, mưa đ&aacute; v&agrave; gi&oacute; giật mạnh. Độ ẩm từ 45-95%. Nhiệt độ thấp nhất 20-23 độ C, cao nhất 32 - 35 độ C.</p>\n\n<p><em>Nam Bộ</em>: M&acirc;y thay đổi, ng&agrave;y nắng, ri&ecirc;ng miền Đ&ocirc;ng c&oacute; nắng n&oacute;ng; chiều tối c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng rải r&aacute;c, đ&ecirc;m c&oacute; mưa r&agrave;o v&agrave; d&ocirc;ng v&agrave;i nơi. Gi&oacute; đ&ocirc;ng cấp 2-3. Trong cơn d&ocirc;ng c&oacute; khả năng xảy ra tố, lốc, mưa đ&aacute; v&agrave; gi&oacute; giật mạnh. Độ ẩm từ 48-95%. Nhiệt độ thấp nhất 24-27 độ C, cao nhất 33-36 độ C.</p>\n\n<p><em>H&agrave; Nội</em>: Nhiều m&acirc;y, ng&agrave;y c&oacute; mưa rải r&aacute;c; đ&ecirc;m c&oacute; mưa, c&oacute; nơi mưa vừa, mưa to v&agrave; rải r&aacute;c c&oacute; d&ocirc;ng. Gi&oacute; đ&ocirc;ng nam cấp 2-3. Trong cơn d&ocirc;ng c&oacute; khả năng xảy ra tố, lốc, mưa đ&aacute; v&agrave; gi&oacute; giật mạnh. Độ ẩm từ 65-98%. Nhiệt độ thấp nhất 22-25 độ C, cao nhất 26 - 29 độ C.</p>\n\n<p>P. Luật</p>\n', '1524537906', '', 3, 0),
(68, 'Cứu hộ thành công 13 ngư dân bị trôi dạt trên biển nhiều giờ', 'thanhhoa1.jpg', 'Sau nhiều giờ bị trôi dạt trên biển, 13 ngư dân Nghệ An trên tàu cá NA 90773 TS đã được tàu cứu nạn hàng hải SAR 411 cứu hộ thành công và đưa về cảng Nghi Sơn (tỉnh Thanh Hóa) an toàn vào đêm 23/4.', '<h2>Sau nhiều giờ bị tr&ocirc;i dạt tr&ecirc;n biển, 13 ngư d&acirc;n Nghệ An tr&ecirc;n t&agrave;u c&aacute; NA 90773 TS đ&atilde; được t&agrave;u cứu nạn h&agrave;ng hải SAR 411 cứu hộ th&agrave;nh c&ocirc;ng v&agrave; đưa về cảng Nghi Sơn (tỉnh Thanh H&oacute;a) an to&agrave;n v&agrave;o đ&ecirc;m 23/4.</h2>\n\n<table cellpadding=\"10\" style=\"width:150px\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img src=\"https://media.baotintuc.vn/2018/04/24/08/07/thanhhoa1.jpg\" /></p>\n\n			<p>T&agrave;u cứu nạn h&agrave;ng hải SAR 411 lai dắt t&agrave;u c&aacute; NA 90773 TS v&agrave;o bờ an to&agrave;n.&nbsp;</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Trước đ&oacute; Trung t&acirc;m Phối hợp t&igrave;m kiếm cứu nạn h&agrave;ng hải Việt Nam (Cục H&agrave;ng hải Việt Nam, Bộ Giao th&ocirc;ng Vận tải) tiếp nhận th&ocirc;ng tin t&agrave;u c&aacute; NA 90773 TS gặp s&oacute;ng to, gi&oacute; lớn khiến t&agrave;u bị hỏng m&aacute;y, mất khả năng hoạt động, nước tr&agrave;n v&agrave;o khoang m&aacute;y. Tr&ecirc;n t&agrave;u l&uacute;c n&agrave;y c&oacute; 13 ngư d&acirc;n; chủ t&agrave;u l&agrave; &ocirc;ng Phạm Văn Dũng, ở x&atilde; Quỳnh Long, huyện Quỳnh Lưu, tỉnh Nghệ An. Vị tr&iacute; t&agrave;u bị nạn c&aacute;ch đảo H&ograve;n Ngư (Nghệ An) khoảng 90 hải l&yacute; về ph&iacute;a Đ&ocirc;ng Bắc.&nbsp;<br />\n<br />\nTrung t&acirc;m Phối hợp t&igrave;m kiếm cứu nạn h&agrave;ng hải Việt Nam đ&atilde; trấn an tinh thần c&aacute;c ngư d&acirc;n, đồng thời hướng dẫn cho ngư d&acirc;n tr&ecirc;n t&agrave;u c&aacute;c biện ph&aacute;p khắc phục, ổn định t&agrave;u v&agrave; y&ecirc;u cầu Thuyền trưởng thường xuy&ecirc;n giữ li&ecirc;n lạc để cập nhật vị tr&iacute;, t&igrave;nh trạng t&agrave;u.&nbsp;</p>\n\n<table cellpadding=\"10\" style=\"width:150px\">\n	<tbody>\n		<tr>\n			<td>\n			<p><img src=\"https://media.baotintuc.vn/2018/04/24/08/22/ngu-dan.jpg\" /></p>\n\n			<p>13 ngư d&acirc;n được đưa v&agrave;o bờ an to&agrave;n. Ảnh: Trịnh Duy Hưng/TTXVN</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p>Sau nhiều giờ cố gắng khắc phục sự cố nhưng kh&ocirc;ng th&agrave;nh c&ocirc;ng, t&agrave;u bị nạn trong khu vực c&oacute; thời tiết xấu, s&oacute;ng lắc ngang mạnh, biển động mạnh cấp 5 -6, giật tr&ecirc;n cấp 7, nước tr&agrave;n v&agrave;o khoang t&agrave;u.&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>Trước t&igrave;nh huống khẩn cấp tr&ecirc;n, Trung t&acirc;m đ&atilde; điều động t&agrave;u cứu nạn h&agrave;ng hải SAR 411 đi cứu nạn khẩn cấp. Sau hơn 10 giờ vượt qua điều kiện khắc nghiệt của thời tiết tr&ecirc;n biển, đến 6 giờ ng&agrave;y 23/4, t&agrave;u cứu nạn h&agrave;ng hải SAR 411 đ&atilde; ph&aacute;t hiện vị tr&iacute; t&agrave;u c&aacute; NA 90773 TS bị nạn. Lực lượng cứu nạn đ&atilde; cử tổ c&ocirc;ng t&aacute;c sử dụng ca n&ocirc; tiếp cận t&agrave;u bị nạn, chăm s&oacute;c y tế cho 13 thuyền vi&ecirc;n, hỗ trợ chống ch&igrave;m t&agrave;u v&agrave; l&ecirc;n phương &aacute;n đưa t&agrave;u v&agrave; to&agrave;n bộ thuyền vi&ecirc;n về bờ.&nbsp;<br />\n<br />\nĐến khoảng 23 giờ ng&agrave;y 23/4, lực lượng cứu nạn h&agrave;ng hải đ&atilde; đưa to&agrave;n bộ 13 ngư d&acirc;n v&agrave; t&agrave;u NA 90773 TS về cảng Nghi Sơn an to&agrave;n, đồng thời tiến h&agrave;nh b&agrave;n giao c&aacute;c thuyền vi&ecirc;n cho c&aacute;c cơ quan chức năng địa phương.<br />\n&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><strong>Tin, ảnh: Trịnh Duy Hưng&nbsp;</strong><em>(TTXVN)</em></p>\n', '1524537964', '', 3, 0),
(69, 'Hút thuốc lá tại Nhật: 45 phút sau mới được phép sử dụng thang máy', 'thuoc-la.jpg', 'Ở Nhật Bản, nếu hút thuốc, bạn sẽ bị cấm sử dụng thang máy trong vòng 45 phút để tránh ảnh hưởng đến những người xung quanh.', '<p><strong>Ở Nhật Bản, nếu h&uacute;t thuốc, bạn sẽ bị cấm sử dụng thang m&aacute;y trong v&ograve;ng 45 ph&uacute;t để tr&aacute;nh ảnh hưởng đến những người xung quanh.</strong></p>\n\n<table cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>\n			<p><a href=\"http://media.doisongphapluat.com/thumb_x500x/589/2018/4/24/thuoc-la.jpg\"><img alt=\"Hút thuốc lá tại Nhật: 45 phút sau mới được phép sử dụng thang máy - Ảnh 1\" src=\"http://media.doisongphapluat.com/thumb_x500x/589/2018/4/24/thuoc-la.jpg\" /></a></p>\n			</td>\n		</tr>\n		<tr>\n			<td>\n			<h2><strong><em>Nếu h&uacute;t thuốc ở Nhật bạn sẽ bị cấm sử dụng thang m&aacute;y trong v&ograve;ng 45 ph&uacute;t. - Ảnh: Shutterstock.</em></strong></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Mới đ&acirc;y, th&agrave;nh phố Ikoma với 121.000 người của Nhật Bản ban h&agrave;nh ch&iacute;nh s&aacute;ch cấm những người h&uacute;t thuốc kh&ocirc;ng được ph&eacute;p sử dụng thang m&aacute;y trong &iacute;t nhất 45 ph&uacute;t để đảm bảo an to&agrave;n sức khỏe cho cộng đồng.</p>\n\n<p>Tất cả c&aacute;c nh&acirc;n vi&ecirc;n v&agrave; kh&aacute;ch ra v&agrave;o t&ograve;a nh&agrave; phải trải qua qu&aacute; tr&igrave;nh &quot;khử nhiễm&quot; hoặc đi thang bộ sau khi h&uacute;t thuốc.</p>\n\n<p>Ch&iacute;nh s&aacute;ch n&agrave;y đưa ra nhằm đảo bảo người lao động hay kh&aacute;ch tham quan đến với th&agrave;nh phố sau khi h&uacute;t thuốc sẽ c&oacute; đủ thời gian &quot;khử nhiễm&quot; l&agrave;m sạch c&aacute;c chất độc hại trước khi bước v&agrave;o thang m&aacute;y.</p>\n\n<p>&nbsp;</p>\n\n<p>Theo Daily Mail, mặc d&ugrave; chưa c&oacute; h&igrave;nh phạt thực tế đối với những người vi phạm nhưng quy định đ&atilde; được thực hiện nghi&ecirc;m t&uacute;c bắt đầu từ th&aacute;ng Tư.</p>\n\n<p>Tuy nhi&ecirc;n, năm ngo&aacute;i nh&agrave; chức tr&aacute;ch đ&atilde; cấm h&uacute;t thuốc l&aacute; gần ga t&agrave;u Ikoma, nếu cố t&igrave;nh sẽ bị phạt 188 USD.</p>\n\n<table cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>\n			<p><a href=\"http://media.doisongphapluat.com/thumb_x500x/589/2018/4/24/thuoc-la-2.jpg\"><img alt=\"Hút thuốc lá tại Nhật: 45 phút sau mới được phép sử dụng thang máy - Ảnh 2\" src=\"http://media.doisongphapluat.com/thumb_x500x/589/2018/4/24/thuoc-la-2.jpg\" /></a></p>\n			</td>\n		</tr>\n		<tr>\n			<td>\n			<h3><em><strong>Mỗi năm c&oacute; khoảng 15.000 người ở Nhật Bản chết v&igrave; c&aacute;c căn bệnh c&oacute; li&ecirc;n quan đến việc h&uacute;t thuốc thụ động. - Ảnh: Soha.vn.</strong></em></h3>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>Ch&iacute;nh s&aacute;ch n&agrave;y được đưa ra dựa tr&ecirc;n một nghi&ecirc;n cứu của gi&aacute;o sư Hiroshi Yamato tại Đại học Sức khỏe M&ocirc;i trường v&agrave; Nghề nghiệp.</p>\n\n<p>&Ocirc;ng đ&atilde; đo được h&agrave;m lượng hỗn hợp c&aacute;c hợp chất hữu cơ dễ bay hơi (TVOCs) trong hơi thở của những người h&uacute;t thuốc.</p>\n\n<p>Kết quả cho thấy phải mất 45 ph&uacute;t sau khi h&uacute;t thuốc để h&agrave;m lượng TVOCs trở lại b&igrave;nh thường v&agrave; trong thời gian đ&oacute; n&oacute; sẽ g&acirc;y hại cho sức khỏe trong một kh&ocirc;ng gian hạn chế.</p>\n\n<p>&quot;Một số người sẽ cảm thấy mệt mỏi khi tiếp x&uacute;c với hơi thở c&oacute; m&ugrave;i thuốc l&aacute; trong những kh&ocirc;ng gian nhỏ hẹp như thang m&aacute;y. Mọi người c&oacute; quyền tự do h&uacute;t thuốc nhưng điều đ&oacute; chỉ đ&uacute;ng khi việc l&agrave;m của họ kh&ocirc;ng g&acirc;y hại đến những người xung quanh,&quot; gi&aacute;o sư Hiroshi n&oacute;i với Asashi Shimbun.</p>\n\n<table cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>\n			<p><a href=\"http://media.doisongphapluat.com/thumb_x500x/589/2018/4/24/toa-thi-chinh.jpg\"><img alt=\"Hút thuốc lá tại Nhật: 45 phút sau mới được phép sử dụng thang máy - Ảnh 3\" src=\"http://media.doisongphapluat.com/thumb_x500x/589/2018/4/24/toa-thi-chinh.jpg\" /></a></p>\n			</td>\n		</tr>\n		<tr>\n			<td>\n			<p><em><strong>T&ograve;a thị ch&iacute;nh.- Ảnh: GENK.</strong></em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>B&ecirc;n trong những t&ograve;a nh&agrave; d&aacute;n nhiều tờ nội quy, n&oacute;i r&otilde;: &quot;Khi v&agrave;o nh&agrave; sau khi h&uacute;t thuốc b&ecirc;n ngo&agrave;i, người ta cho rằng phải mất 45 ph&uacute;t để c&aacute;c chất độc hại trong hơi thở của người đ&oacute; trở về mức trước khi h&uacute;t thuốc.&quot;</p>\n\n<p>Ngo&agrave;i ra, trong nội quy cũng c&oacute; lời khuy&ecirc;n: &quot;Sau khi h&uacute;t thuốc, bạn cần phải thở ra v&agrave; h&iacute;t v&agrave;o thật s&acirc;u trước khi trở lại b&ecirc;n trong&quot;.</p>\n\n<p>Giới chức th&agrave;nh phố cho biết ch&iacute;nh s&aacute;ch mới chỉ nhận được sự ủng hộ của một nửa cư d&acirc;n trong th&agrave;nh phố, nửa c&ograve;n lại phản đối v&igrave; cho rằng n&oacute; sẽ kh&ocirc;ng mang lại hiệu quả.</p>\n\n<p><strong>TRẦN LOAN</strong>&nbsp;<em>(T/h)</em></p>\n', '1524541851', '', 45, 0);
INSERT INTO `article` (`id_article`, `title`, `image`, `brief_content`, `content`, `created_date`, `modified_date`, `id_category`, `id_author`) VALUES
(71, 'Lợi và hại từ quả sung mà không phải ai cũng biết', 'sung_1_ZHPI.jpg', 'Không chỉ là loại quả ăn vặt, sung còn có tác dụng chữa bệnh rất hiệu quả. Tuy nhiên, sung cũng có thể gây ra những tác hại khôn lường nếu chúng ta chưa hiểu rõ về loại quả này.', '<h1>Lợi v&agrave; hại từ quả sung m&agrave; kh&ocirc;ng phải ai cũng biết</h1>\n\n<p>Chủ nhật, 22/04/2018 | 18:40 GMT+7</p>\n\n<ul>\n	<li>\n	<h2><a href=\"http://www.doisongphapluat.com/doi-song/bai-thuoc-cuu-tinh-cho-hang-ngan-benh-nhan-mac-benh-da-day-dai-trang-cua-luong-y-doan-duy-khanh-a210308.html\">B&agrave;i thuốc cứu tinh cho h&agrave;ng ng&agrave;n bệnh nh&acirc;n mắc bệnh dạ d&agrave;y, đại tr&agrave;ng của lương y Đo&agrave;n Duy Kh&aacute;nh</a></h2>\n	</li>\n</ul>\n\n<p><strong>Kh&ocirc;ng chỉ l&agrave; loại quả ăn vặt, sung c&ograve;n c&oacute; t&aacute;c dụng chữa bệnh rất hiệu quả. Tuy nhi&ecirc;n, sung cũng c&oacute; thể g&acirc;y ra những t&aacute;c hại kh&ocirc;n lường nếu ch&uacute;ng ta chưa hiểu r&otilde; về loại quả n&agrave;y.</strong></p>\n\n<p>Sung c&ograve;n c&oacute; một số t&ecirc;n kh&aacute;c như l&agrave; v&ocirc; hoa quả, thi&ecirc;n sinh tử, &aacute;nh nhật quả, văn ti&ecirc;n quả, phẩm ti&ecirc;n quả, n&atilde;i tương quả, mật quả.</p>\n\n<p>Qua nghi&ecirc;n cứu hiện đại, c&aacute;c chuy&ecirc;n gia đ&atilde; chỉ ra rằng quả sung c&oacute; chứa glucose, saccarose, quinic acid, shikimic acid, oxalic acid, citic acid, malic acid, auxin, c&aacute;c nguy&ecirc;n tố vi lượng như canxi, photpho, kali... v&agrave; một số vitamin như C rất c&oacute; lợi cho cơ thể.</p>\n\n<table cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>\n			<p><a href=\"http://media.doisongphapluat.com/thumb_x500x/589/2018/4/20/sung_1_ZHPI.jpg\"><img alt=\"Lợi và hại từ quả sung mà không phải ai cũng biết - Ảnh 1\" src=\"http://media.doisongphapluat.com/thumb_x500x/589/2018/4/20/sung_1_ZHPI.jpg\" /></a></p>\n			</td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em><strong>Ảnh minh họa: Internet.</strong></em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Tuy nhi&ecirc;n, b&ecirc;n cạnh những mặt c&oacute; lợi, mọi người khi ăn loại quả n&agrave;y nếu kh&ocirc;ng r&otilde; hết bản chất của n&oacute; lại c&oacute; thể g&acirc;y ra những t&aacute;c dụng phụ kh&ocirc;ng như mong muốn.</p>\n\n<p>B&agrave;i viết n&agrave;y sẽ gi&uacute;p c&aacute;c bạn c&oacute; thể biết được mặt lợi v&agrave; hại của tr&aacute;i sung để c&oacute; thể sử dụng loại quả n&agrave;y một c&aacute;ch hiệu quả nhất.</p>\n\n<p><strong>T&aacute;c dụng từ quả sung:</strong></p>\n\n<p><strong>Ngăn ngừa t&aacute;o b&oacute;n</strong></p>\n\n<p>Cứ trong 3gam sung th&igrave; c&oacute; tới 5 gam chất xơ v&agrave; prebiotic, c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch nhu động ruột, tạo điều kiện thuận lợi cho một số loại vi khuẩn c&oacute; lợi cho đường ruột ph&aacute;t triển. Do đ&oacute; tr&aacute;i sung gi&uacute;p c&aacute;c chức năng của ruột khỏe mạnh v&agrave; ngăn ngừa t&aacute;o b&oacute;n.</p>\n\n<p><strong>Giảm trọng lượng cơ thể</strong></p>\n\n<p>Trong tr&aacute;i sung c&oacute; chứa một lượng chất xơ kh&aacute; lớn rất tốt cho cơ thể của ch&uacute;ng ta. Đồng thời, c&aacute;c chất xơ trong quả sung c&ograve;n gi&uacute;p giảm trọng lượng v&agrave; n&oacute; l&agrave; loại quả thường được khuyến kh&iacute;ch cho những người b&eacute;o ph&igrave;.</p>\n\n<p><strong>Tăng cường v&agrave; ph&aacute;t triển xương</strong></p>\n\n<p>Trong quả sung chứa nhiều kali, mangan v&agrave; canxi, những kho&aacute;ng chất ảnh hưởng tốt đến mật độ xương. Kali c&oacute; t&aacute;c dụng chống lại sự b&agrave;i tiết canxi th&ocirc;ng qua nước tiểu. Những canxi n&agrave;y sẽ gi&uacute;p củng cố hệ xương ph&aacute;t triển v&ugrave;n vụt.</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>Theo c&aacute;c chuy&ecirc;n gia dinh dưỡng, bạn bị dị ứng với c&aacute;c sản phẩm từ sữa, bạn c&oacute; thể bổ sung canxi từ tr&aacute;i sung.</p>\n\n<p><strong>Hạ cholesterol</strong></p>\n\n<p>Những quả sung c&oacute; chứa Pectin ,một chất xơ h&ograve;a tan. Khi chất xơ n&agrave;y đi qua hệ ti&ecirc;u h&oacute;a gi&uacute;p hạ cholesterol trong m&aacute;u.</p>\n\n<p><strong>Ngăn ngừa ung thư v&agrave; bệnh tiểu đường</strong></p>\n\n<p>Kết quả nghi&ecirc;n cứu từ Đại học bang Colorado (Mỹ) cho thấy c&aacute;c dưỡng chất dồi d&agrave;o chứa trong quả sung như coumarin, pectin, beta-carotene, vitamin A, C, E, K, đồng, sắt, kẽm&hellip; c&oacute; khả năng l&agrave;m giảm cholesterol xấu trong m&aacute;u, hạn chế nguy cơ mắc bệnh tiểu đường, ung thư tuyến tiền liệt, ung thư v&uacute; v&agrave; ung thư ruột kết.</p>\n\n<p>Do đ&oacute;, n&ecirc;n th&ecirc;m quả sung v&agrave;o thực đơn ăn uống mỗi ng&agrave;y (c&oacute; thể uống nước sắc từ l&aacute; sung) để mang lại nhiều lợi &iacute;ch cho sức khỏe.</p>\n\n<p><strong>T&aacute;c dụng phụ của quả sung cần ch&uacute; &yacute; trước khi sử dụng:</strong></p>\n\n<p><strong>G&acirc;y hại cho m&aacute;u</strong></p>\n\n<p>Sung ch&iacute;n c&oacute; t&iacute;nh n&oacute;ng, c&oacute; thể g&acirc;y xuất huyết. Ăn nhiều sung c&oacute; thể g&acirc;y xuất huyết v&otilde;ng mạc, trực tr&agrave;ng hoặc chảy m&aacute;u nhẹ ở &acirc;m đạo. Đồng thời, ăn sung c&ograve;n g&acirc;y thiếu m&aacute;u. Trong trường hợp bị xuất huyết trực tr&agrave;ng hoặc &acirc;m đạo, n&ecirc;n dừng ăn sung cho đến khi ngừng chảy m&aacute;u.</p>\n\n<p><strong>Giảm đường huyết</strong></p>\n\n<p>Ăn sung l&agrave;m giảm lượng đường trong m&aacute;u, c&oacute; lợi cho bệnh nh&acirc;n bị tiểu đường. Tuy nhi&ecirc;n với những người c&oacute; lượng đường huyết thấp th&igrave; ăn nhiều sung rất c&oacute; hại. Ch&iacute;nh v&igrave; vậy, nếu cơ thể bạn c&oacute; lượng đường huyết thấp, n&ecirc;n tr&aacute;nh ăn sung.</p>\n\n<p><strong>C&aacute;c Oxalate c&oacute; hại</strong></p>\n\n<p>Trong sung chứa rất nhiều oxalate, c&oacute; thể g&acirc;y hại cho những người bị thận hoặc t&uacute;i mật. Ăn nhiều sung c&oacute; thể l&agrave;m t&igrave;nh trạng bệnh nghi&ecirc;m trọng hơn. Đồng thời, sung cũng c&oacute; thể ảnh hưởng đến l&aacute; l&aacute;ch - bộ phận trong cơ thể phụ tr&aacute;ch sản xuất tế b&agrave;o bạch cầu.</p>\n\n<p><strong>THANH LOAN</strong>&nbsp;<em>(T/h)</em></p>\n', '1524542008', '', 45, 0),
(72, 'Cách chọn và giữ hoa đào đẹp ngày Tết đón tài lộc vào nhà', 'hoa dao.jpg', 'Cắm cành hoa đào hay bày một cây đào trong nhà mỗi dịp Tết đến xuân về là phong tục truyền thống của người dân miền Bắc với hy vọng mang lại niềm vui và may mắn cho gia đình. Vì vậy, việc lựa chọn được một cành hay cây đào ưng ý hợp phong thủy và chăm sóc nó thế nào để giữ hoa tươi lâu ngày Tết là mối quan tâm của không ít người.', '<p><strong>Cắm c&agrave;nh hoa đ&agrave;o hay b&agrave;y một c&acirc;y đ&agrave;o trong nh&agrave; mỗi dịp Tết đến xu&acirc;n về l&agrave; phong tục truyền thống của người d&acirc;n miền Bắc với hy vọng mang lại niềm vui v&agrave; may mắn cho gia đ&igrave;nh. V&igrave; vậy, việc&nbsp;lựa chọn được một c&agrave;nh hay c&acirc;y đ&agrave;o ưng &yacute; hợp phong thủy v&agrave; chăm s&oacute;c n&oacute; thế n&agrave;o để giữ hoa tươi l&acirc;u ng&agrave;y Tết l&agrave; mối quan t&acirc;m của kh&ocirc;ng &iacute;t người.&nbsp;</strong></p>\n\n<p><strong>C&aacute;ch chọn đ&agrave;o</strong></p>\n\n<p><em><strong>Chọn đ&agrave;o c&agrave;nh&nbsp;</strong></em></p>\n\n<table cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>\n			<p><a href=\"http://media.doisongphapluat.com/thumb_x500x/563/2017/12/27/hoa%20dao%201.jpg\"><img alt=\"Cách chọn và giữ hoa đào đẹp ngày Tết đón tài lộc vào nhà  - Ảnh 1\" src=\"http://media.doisongphapluat.com/thumb_x500x/563/2017/12/27/hoa%20dao%201.jpg\" /></a></p>\n			</td>\n		</tr>\n		<tr>\n			<td>&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Người chọn đ&agrave;o c&agrave;nh thường l&agrave; do kh&ocirc;ng gian nh&agrave; nhỏ hoặc chỉ cần b&agrave;y tr&ecirc;n b&agrave;n thờ, b&agrave;n ph&ograve;ng kh&aacute;ch. Tuy nhi&ecirc;n, chọn c&agrave;nh đ&agrave;o to nhỏ t&ugrave;y theo kh&ocirc;ng gian diện t&iacute;ch nh&agrave;.</p>\n\n<p>Điều quan trọng nhất l&agrave; t&aacute;n đ&agrave;o phải tr&ograve;n, c&aacute;c nh&aacute;nh ph&acirc;n bố đều. Kh&ocirc;ng n&ecirc;n chọn c&agrave;nh c&oacute; t&aacute;n lệch v&agrave; c&aacute;c nh&aacute;nh đ&acirc;m l&ecirc;n kh&ocirc;ng c&ugrave;ng bắt đầu từ một điểm tr&ecirc;n th&acirc;n gốc. Bạn n&ecirc;n t&igrave;m mua loại c&agrave;nh c&oacute; dăm nhỏ, v&uacute;t thẳng ra ngo&agrave;i t&aacute;n, nụ rải đều từ đầu tới cuối.</p>\n\n<p>B&ecirc;n cạnh đ&oacute;, bạn n&ecirc;n chọn c&agrave;nh đ&agrave;o c&oacute; nhiều dăm (l&agrave; những c&agrave;nh đ&agrave;o nhỏ nhất), c&oacute; nhiều hoa, nụ mập v&agrave; ph&acirc;n bố đều. Hoa đ&agrave;o nở c&oacute; c&aacute;nh k&eacute;p, d&agrave;y, tươi m&agrave;u. Th&acirc;n đ&agrave;o c&oacute; thể x&ugrave; x&igrave; nhưng chắc khỏe.</p>\n\n<p>Thời điểm chọn mua hoa đ&agrave;o tốt nhất l&agrave; trước Tết&nbsp;3-5 ng&agrave;y để hoa nở rộ đ&uacute;ng Tết, tr&aacute;nh mua qu&aacute; sớm hoặc qu&aacute; muộn v&igrave; khi mua c&oacute; thể đẹp nhưng đến Tết hoa đ&agrave;o c&oacute; thể kh&ocirc;ng nở hoặc đ&atilde; t&agrave;n.</p>\n\n<p><em><strong>Chọn đ&agrave;o c&acirc;y</strong></em></p>\n\n<table cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>\n			<p><a href=\"http://media.doisongphapluat.com/thumb_x500x/563/2017/12/27/cay%20dao.jpg\"><img alt=\"Cách chọn và giữ hoa đào đẹp ngày Tết đón tài lộc vào nhà  - Ảnh 2\" src=\"http://media.doisongphapluat.com/thumb_x500x/563/2017/12/27/cay%20dao.jpg\" /></a></p>\n			</td>\n		</tr>\n		<tr>\n			<td>&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Người tinh tế khi chọn đ&agrave;o thế sẽ ch&uacute; &yacute; đ&agrave;o phải c&oacute; đủ bộ tứ qu&yacute;: Hoa, nụ, lộc v&agrave; quả, bởi đ&oacute; l&agrave; biểu tượng cho sự đề huề, ấm no của gia đ&igrave;nh.</p>\n\n<p>Với đ&agrave;o c&acirc;y, ngo&agrave;i c&aacute;c ti&ecirc;u ch&iacute; về hoa, nụ, dăm, như đ&agrave;o c&agrave;nh th&igrave; cần th&ecirc;m một v&agrave;i ti&ecirc;u ch&iacute; kh&aacute;c như c&oacute; dăm nhỏ, ngắn, c&aacute;c nh&aacute;nh ch&iacute;nh của c&acirc;y tạo n&ecirc;n d&aacute;ng c&acirc;y xuất ph&aacute;t từ một điểm v&agrave; được uốn tạo d&aacute;ng đẹp, c&acirc;n đối.</p>\n\n<p>Đ&agrave;o c&acirc;y thường nở hoa chậm hơn so với đ&agrave;o c&agrave;nh n&ecirc;n bạn phải chọn c&acirc;y c&oacute; nụ to, hoa nở nhiều v&agrave;o l&uacute;c cận Tết. Nếu mua những c&acirc;y c&oacute; &iacute;t hoa khi c&aacute;ch ng&agrave;y Tết v&agrave;i ng&agrave;y th&igrave; hoa đ&agrave;o sẽ kh&ocirc;ng kịp nở v&agrave; k&eacute;m sắc v&agrave;o ng&agrave;y Tết.</p>\n\n<p><strong>C&aacute;ch giữ hoa đ&agrave;o tươi l&acirc;u ng&agrave;y Tết</strong></p>\n\n<p><em><strong>Đối với đ&agrave;o c&agrave;nh</strong></em></p>\n\n<p>Bạn cần phải rửa sạch lọ v&agrave; d&ugrave;ng nước sạch để cắm hoa. C&aacute;ch 2-3 ng&agrave;y, bạn n&ecirc;n thay nước trong b&igrave;nh cắm một lần v&agrave; rửa sạch phần c&agrave;nh đ&agrave;o nằm ở dưới nước. Bạn cũng c&oacute; thể thả v&agrave;o lọ v&agrave;i vi&ecirc;n thuốc vitamin B1 để c&oacute; dinh dưỡng gi&uacute;p hoa đ&agrave;o tươi l&acirc;u hơn.</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p><em><strong>Đối với c&acirc;y đ&agrave;o</strong></em></p>\n\n<p>Đ&agrave;o l&agrave; c&acirc;y kh&ocirc;ng ưa ẩm n&ecirc;n bạn cần tưới nước sạch thường xuy&ecirc;n nhưng tuyệt đối kh&ocirc;ng được tưới qu&aacute; nhiều nếu kh&ocirc;ng c&acirc;y đ&agrave;o sẽ bị thối rễ. Đồng thời bạn cần giữ cho c&acirc;y được sạch, m&aacute;t để hoa được tươi l&acirc;u.</p>\n\n<p><strong>B&iacute; quyết để hoa đ&agrave;o nở đẹp suốt dịp Tết</strong></p>\n\n<table cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>\n			<p><a href=\"http://media.doisongphapluat.com/thumb_x500x/563/2017/12/27/hoa%20dao.jpg\"><img alt=\"Cách chọn và giữ hoa đào đẹp ngày Tết đón tài lộc vào nhà  - Ảnh 3\" src=\"http://media.doisongphapluat.com/thumb_x500x/563/2017/12/27/hoa%20dao.jpg\" /></a></p>\n			</td>\n		</tr>\n		<tr>\n			<td>&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Chọn được một c&agrave;nh, c&acirc;y đ&agrave;o ưng &yacute; rồi th&igrave; c&aacute;ch chăm s&oacute;c n&oacute; ra sao để nở hoa đ&uacute;ng trong suốt dịp Tết&nbsp;cũng rất quan trọng.&nbsp;</p>\n\n<p>Đ&agrave;o c&agrave;nh mua về, phải đốt gốc, đốt c&agrave;nh hay nh&uacute;ng ngay v&agrave;o chậu nước n&oacute;ng gi&agrave; 70-80 độ C để c&aacute;c chất dinh dưỡng dự trữ nu&ocirc;i hoa trong c&agrave;nh kh&ocirc;ng thẩm thấu được ra ngo&agrave;i.</p>\n\n<p>Nếu hoa đ&agrave;o nở sớm v&agrave; qu&aacute; nhanh, ch&uacute;ng ta c&oacute; thể<strong>&nbsp;</strong>d&ugrave;ng dao cứa một v&ograve;ng quanh th&acirc;n&nbsp;c&aacute;ch gốc 1 gang tay. C&aacute;ch n&agrave;y hạn chế được c&agrave;nh đ&agrave;o lấy dinh dưỡng từ gốc để nu&ocirc;i hoa l&agrave;m chậm qu&aacute; tr&igrave;nh nở hoa. Bạn cũng c&oacute; thể kết hợp với việc cho sỏi v&agrave;o trong b&igrave;nh v&agrave; d&ugrave;ng nước lạnh để cắm hoặc tưới để đạt hiệu quả tốt hơn.</p>\n\n<p>Nếu hoa đ&agrave;o nở muộn, bạn h&atilde;y&nbsp;đắp quanh gốc đ&agrave;o một nắm v&ocirc;i. Đối với đ&agrave;o c&agrave;nh, c&oacute; thể&nbsp;cắm trong nước n&oacute;ng ấm, đổi nước li&ecirc;n tục khi nguội. Chỉ sau một đ&ecirc;m, hoa đ&agrave;o sẽ nở tưng bừng đ&oacute;n Tết c&ugrave;ng gia đ&igrave;nh bạn.</p>\n\n<p><strong>C&aacute;ch cắm hoa đ&agrave;o ng&agrave;y Tết hợp phong thủy, đem lại may mắn</strong></p>\n\n<p>Tết đến, m&agrave;u hồng phai hay thắm của hoa đ&agrave;o kh&ocirc;ng thể thiếu trong mỗi gia đ&igrave;nh miền Bắc. Cắm hoa, chăm hoa v&agrave; hướng đặt b&igrave;nh đ&uacute;ng phong thủy sẽ mang c&aacute;t lợi cho cả 365 ng&agrave;y.</p>\n\n<table cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>\n			<p><a href=\"http://media.doisongphapluat.com/thumb_x500x/563/2017/12/27/dao.jpg\"><img alt=\"Cách chọn và giữ hoa đào đẹp ngày Tết đón tài lộc vào nhà  - Ảnh 4\" src=\"http://media.doisongphapluat.com/thumb_x500x/563/2017/12/27/dao.jpg\" /></a></p>\n			</td>\n		</tr>\n		<tr>\n			<td>&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Nếu l&agrave; năm T&yacute;, Th&igrave;n, Th&acirc;n n&ecirc;n đặt b&igrave;nh đ&agrave;o theo hướng T&acirc;y</p>\n\n<p>Nếu l&agrave; năm Ngọ, Tuất, Dần n&ecirc;n đặt b&igrave;nh đ&agrave;o theo hướng Đ&ocirc;ng</p>\n\n<p>Nếu l&agrave; năm Dậu, Sửu, Tỵ n&ecirc;n đặt b&igrave;nh đ&agrave;o theo hướng Nam</p>\n\n<p>Nếu l&agrave; năm M&atilde;o, M&ugrave;i, Hợi, n&ecirc;n đặt b&igrave;nh đ&agrave;o theo hướng Bắc.</p>\n\n<p>Cần phải vứt hoa đi trước khi hoa h&eacute;o ngay trong ch&iacute;nh nh&agrave; bạn đầu năm mới. Trong phong thủy, nếu đầu năm m&agrave; chứng kiến hoa h&eacute;o, trong nh&agrave; c&oacute; sự chia c&aacute;ch hoặc tranh chấp.</p>\n\n<p>M&agrave;u sắc của b&igrave;nh cắm đ&agrave;o cũng n&ecirc;n theo phong thủy. Nếu đặt b&igrave;nh về hướng Bắc n&ecirc;n chọn m&agrave;u xanh da trời, m&agrave;u đen. Nếu đặt b&igrave;nh về hướng Đ&ocirc;ng Nam hay Đ&ocirc;ng n&ecirc;n chọn b&igrave;nh m&agrave;u xanh ngọc, xanh l&aacute; c&acirc;y. Nếu đặt b&igrave;nh ph&iacute;a Nam n&ecirc;n chọn b&igrave;nh m&agrave;u đỏ hoặc t&iacute;m. Nếu đặt b&igrave;nh hướng T&acirc;y, T&acirc;y Bắc n&ecirc;n chọn b&igrave;nh m&agrave;u v&agrave;ng hoặc trắng. T&acirc;y Nam v&agrave; Đ&ocirc;ng Bắc n&ecirc;n sử dụng b&igrave;nh m&agrave;u v&agrave;ng n&acirc;u.</p>\n\n<p><strong>Hằng Thanh</strong>&nbsp;<em>(T/h)</em></p>\n', '1524542087', '', 45, 0),
(73, 'Phim ngắn Việt Nam thu hút cả thế giới', '072739-24.jpg', 'Mới đây, hãng Reuters đã chính thức gửi lời đề nghị phát hành bộ phim ngắn Vietnam from above II - Bản hòa tấu không trung trên hệ thống của hãng.', '<h1>Phim ngắn Việt Nam thu h&uacute;t cả thế giới</h1>\n\n<p>25/09/2017 - 10:17 (GMT+7)</p>\n\n<h2>Mới đ&acirc;y, h&atilde;ng Reuters đ&atilde; ch&iacute;nh thức gửi lời đề nghị ph&aacute;t h&agrave;nh bộ phim ngắn Vietnam from above II - Bản h&ograve;a tấu kh&ocirc;ng trung tr&ecirc;n hệ thống của h&atilde;ng.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"24\" src=\"http://cdn.baogiaothong.vn/files/Baogiay/2017/09/25/072739-24.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p><em><strong>Một cảnh trong bộ phim ngắn Vietnam from above II của nhiếp ảnh gia L&ecirc; Thế Thắng</strong></em></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>T&aacute;c giả bộ phim l&agrave; nhiếp ảnh gia L&ecirc; Thế Thắng cho hay, mọi thỏa thuận, trao đổi, chuyển giao nội dung với Reuters đ&atilde; xong, chỉ đợi thay đổi nhạc nền cho ph&ugrave; hợp. Đ&acirc;y l&agrave; th&agrave;nh tựu quan trọng, bởi Reuters l&agrave; h&atilde;ng th&ocirc;ng tấn nổi tiếng c&oacute; tầm bao phủ rộng khắp tr&ecirc;n to&agrave;n thế giới.</p>\n\n<p>Vietnam from above II - Bản h&ograve;a tấu kh&ocirc;ng trung l&agrave; bộ phim ngắn 8 ph&uacute;t của nhiếp ảnh gia L&ecirc; Thế Thắng. Được thực hiện bằng phương tiện flycam. Bộ phim đem tới những g&oacute;c nh&igrave;n từ tr&ecirc;n cao xuống v&ocirc; c&ugrave;ng độc đ&aacute;o, giới thiệu cảnh sắc 31 tỉnh, th&agrave;nh Việt Nam theo c&aacute;ch chưa bao giờ c&oacute;. Ra mắt hồi trung tuần th&aacute;ng 8, tới nay, bộ phim đ&atilde; đạt hơn 325.000 lượt xem, 4.600 lượt chia sẻ tr&ecirc;n mạng x&atilde; hội.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/Nh%E1%BA%ADt-Minh-tag/\"><strong>Nhật Minh</strong></a></p>\n', '1524542389', '', 46, 0),
(74, 'Điều gì thôi thúc Triều Tiên tuyên bố ngừng thử hạt nhân?', '071357-33.jpg', 'Cuối tuần qua, Triều Tiên đã công bố một chiến lược mới cho đất nước, trong đó tạm đóng băng các cuộc thử nghiệm...', '<h2>Cuối tuần qua, Triều Ti&ecirc;n đ&atilde; c&ocirc;ng bố một chiến lược mới cho đất nước, trong đ&oacute; tạm đ&oacute;ng băng c&aacute;c cuộc thử nghiệm...</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"33\" src=\"http://cdn.baogiaothong.vn/files/Baogiay/2018/04/23/071357-33.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em><strong>&Ocirc;ng Kim Jong-un tuy&ecirc;n bố chương tr&igrave;nh t&ecirc;n lửa v&agrave; hạt nh&acirc;n của Triều Ti&ecirc;n đ&atilde; ho&agrave;n th&agrave;nh sứ mệnh</strong></em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Cuối tuần qua, Triều Ti&ecirc;n đ&atilde; c&ocirc;ng bố một chiến lược mới cho đất nước, trong đ&oacute; tạm đ&oacute;ng băng c&aacute;c cuộc thử nghiệm t&ecirc;n lửa v&agrave; vũ kh&iacute; hạt nh&acirc;n để theo đuổi tăng trưởng kinh tế v&agrave; h&ograve;a b&igrave;nh tr&ecirc;n b&aacute;n đảo ở Đ&ocirc;ng Bắc &Aacute;.</p>\n\n<p><strong>Ủng hộ v&agrave; ho&agrave;i nghi</strong></p>\n\n<p>Việc nh&agrave; l&atilde;nh đạo Triều Ti&ecirc;n Kim Jong-un tuy&ecirc;n bố đất nước của &ocirc;ng chấm dứt c&aacute;c hoạt động thử nghiệm vũ kh&iacute; hạt nh&acirc;n từ ng&agrave;y 21/4 l&agrave; một t&iacute;n hiệu nhượng bộ đ&aacute;ng kể so với c&aacute;c tuy&ecirc;n bố cứng rắn trước đ&acirc;y của nước n&agrave;y.</p>\n\n<p>Chiến lược mới của Triều Ti&ecirc;n cũng nhấn mạnh v&agrave;o việc tập trung x&acirc;y dựng một nền kinh tế - x&atilde; hội chủ nghĩa mạnh mẽ, đồng thời tăng cường đối thoại với c&aacute;c nước l&aacute;ng giềng v&agrave; cộng đồng quốc tế để bảo vệ h&ograve;a b&igrave;nh v&agrave; ổn định của b&aacute;n đảo Triều Ti&ecirc;n v&agrave; thế giới.</p>\n\n<p>Ngay lập tức, tuy&ecirc;n bố n&agrave;y của B&igrave;nh Nhưỡng đ&atilde; sớm nhận được sự ủng hộ của hai nh&agrave; l&atilde;nh đạo Mỹ v&agrave; H&agrave;n Quốc.</p>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"640x-1\" src=\"http://cdn.baogiaothong.vn/files/hoi.lq/2018/04/24/094146-640x-1.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Qu&acirc;n đội Triều Ti&ecirc;n</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Hai nh&agrave; l&atilde;nh đạo cho rằng, đ&acirc;y l&agrave; một bước tiến lớn đối với việc phi hạt nh&acirc;n h&oacute;a b&aacute;n đảo Triều Ti&ecirc;n, g&oacute;p phần tạo m&ocirc;i trường rất t&iacute;ch cực dọn đường cho cuộc gặp trong tuần n&agrave;y giữa nh&agrave; l&atilde;nh đạo Triều Ti&ecirc;n Kim Jong-un với Tổng thống H&agrave;n Quốc Moon Jae-in v&agrave; mở ra triển vọng tươi s&aacute;ng cho cuộc gặp thượng đỉnh Mỹ - Triều v&agrave;o th&aacute;ng 6 tới. Nhưng, với thực tế về mặt kỹ thuật hai quốc gia H&agrave;n - Triều vẫn c&ograve;n trong t&igrave;nh trạng chiến tranh khiến nhiều người d&acirc;n ở Seoul tỏ ra ho&agrave;i nghi về sự ch&acirc;n th&agrave;nh của B&igrave;nh Nhưỡng.</p>\n\n<p>Một cuộc thăm d&ograve; &yacute; kiến được tờ Bưu điện Hoa Nam buổi s&aacute;ng (SCMP) đăng tải cho thấy, người H&agrave;n Quốc đ&atilde; sống nhiều thập ni&ecirc;n trong mối đe dọa chiến tranh với người h&agrave;ng x&oacute;m th&ugrave; địch v&agrave; bị c&ocirc; lập.</p>\n\n<p>Hội nghị thượng đỉnh trong tuần n&agrave;y m&agrave; Tổng thống H&agrave;n Quốc Moon Jae-in đang th&uacute;c đẩy với B&igrave;nh Nhưỡng kh&ocirc;ng phải l&agrave; lần đầu. Người d&acirc;n Seoul từng chứng kiến ​​một v&agrave;i sự th&uacute;c đẩy trước đ&oacute; cho sự h&ograve;a giải l&agrave;m dấy l&ecirc;n hy vọng h&ograve;a b&igrave;nh nhưng đều kh&ocirc;ng th&agrave;nh c&ocirc;ng.</p>\n\n<p>V&igrave; thế, người d&acirc;n xứ sở kim chi ng&agrave;y c&agrave;ng thờ ơ với mối đe dọa chiến tranh v&agrave; xoay quanh họ l&agrave; c&aacute;c vấn đề như c&ocirc;ng ăn việc l&agrave;m v&agrave; &aacute;p lực đi k&egrave;m với sự ph&aacute;t triển nhanh ch&oacute;ng của H&agrave;n Quốc kể từ những năm 1950.</p>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"north-korean-female-officers-Cropped-2\" src=\"http://cdn.baogiaothong.vn/files/hoi.lq/2018/04/24/094208-north-korean-female-officers-cropped-2.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Nh&agrave; l&atilde;nh đạo Triều Ti&ecirc;n Kim Jong Un v&agrave; qu&acirc;n đội</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Một số chuy&ecirc;n gia cũng tỏ ra ho&agrave;i nghi về động th&aacute;i mới n&agrave;y của Triều Ti&ecirc;n, nhất l&agrave; khi n&oacute; được đưa ra v&agrave;o thời điểm chỉ v&agrave;i ng&agrave;y trước hội nghị thượng đỉnh li&ecirc;n Triều v&agrave; cuộc gặp được l&ecirc;n kế hoạch giữa &ocirc;ng Trump v&agrave; &ocirc;ng Kim Jong-un.</p>\n\n<p>Cựu ph&acirc;n t&iacute;ch vi&ecirc;n về Triều Ti&ecirc;n của CIA Sue Mi Terry n&oacute;i tr&ecirc;n CNN: &ldquo;Tuy&ecirc;n bố tr&ecirc;n l&agrave; rất quan trọng nhưng Triều Ti&ecirc;n kh&ocirc;ng bao giờ l&agrave;m g&igrave; m&agrave; kh&ocirc;ng c&oacute; l&yacute; do. V&igrave; vậy, c&oacute; thể họ đang t&igrave;m kiếm điều g&igrave; đ&oacute; từ ph&iacute;a Mỹ&rdquo;.</p>\n\n<p><strong>Chương tr&igrave;nh&nbsp;hạt nh&acirc;n đ&atilde;&nbsp;ho&agrave;n th&agrave;nh sứ mệnh</strong></p>\n\n<p>Chiến lược mới m&agrave; đảng Lao động Triều Ti&ecirc;n (WPK) c&ocirc;ng bố gồm 6 luận điểm, trong đ&oacute; n&ecirc;u r&otilde; B&igrave;nh Nhưỡng đ&atilde; th&agrave;nh c&ocirc;ng trong việc thu nhỏ đầu đạn hạt nh&acirc;n v&agrave; ph&aacute;t triển bệ ph&oacute;ng t&ecirc;n lửa. C&aacute;c chuy&ecirc;n gia nhận định, đ&acirc;y l&agrave; một phần l&yacute; do Triều Ti&ecirc;n quyết định ngừng thử nghiệm hạt nh&acirc;n v&agrave; t&ecirc;n lửa.</p>\n\n<p>Bởi, trong nhiều thập ni&ecirc;n qua, Triều Ti&ecirc;n lu&ocirc;n khẳng định, sứ mệnh ph&aacute;t triển chương tr&igrave;nh hạt nh&acirc;n v&agrave; t&ecirc;n lửa l&agrave; v&igrave; mục đ&iacute;ch ph&ograve;ng vệ mọi mối đe dọa từ b&ecirc;n ngo&agrave;i. Chương tr&igrave;nh g&acirc;y tranh c&atilde;i n&agrave;y cũng đ&atilde; trở th&agrave;nh &ldquo;chi&ecirc;u b&agrave;i mặc cả&rdquo; chiến lược trong mọi cuộc thương lượng với Mỹ v&agrave; H&agrave;n Quốc.</p>\n\n<p>C&aacute;c vụ ph&oacute;ng t&ecirc;n lửa của Triều Ti&ecirc;n thời gian gần đ&acirc;y đ&atilde; cho thấy sự cải thiện đ&aacute;ng kể về kỹ thuật khi t&ecirc;n lửa bay được ng&agrave;y c&agrave;ng xa v&agrave; hiện đ&atilde; c&oacute; thể đạt đến tầm bắn 10.400km.</p>\n\n<p>Vụ thử hạt nh&acirc;n th&aacute;ng 9/2016 của B&igrave;nh Nhưỡng cho thấy, nước n&agrave;y đ&atilde; c&oacute; thể sản xuất c&aacute;c vũ kh&iacute; hạt nh&acirc;n thu nhỏ, nhẹ hơn với sức tấn c&ocirc;ng mạnh hơn v&agrave; c&oacute; thể lắp đặt được tr&ecirc;n những t&ecirc;n lửa đạn đạo.</p>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"canadian-soldiers\" src=\"http://cdn.baogiaothong.vn/files/hoi.lq/2018/04/24/094239-canadian-soldiers.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2>&nbsp;</h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>C&ograve;n vụ thử hạt nh&acirc;n mới nhất v&agrave;o th&aacute;ng 9/2017 đ&aacute;nh dấu th&agrave;nh c&ocirc;ng bom nhiệt hạch (bom H) của Triều Ti&ecirc;n cho thấy, nước n&agrave;y c&oacute; thể chế tạo bom H thay thế lượng chất nổ của t&ecirc;n lửa xuy&ecirc;n lục địa (ICBM).</p>\n\n<p>Giới chuy&ecirc;n gia cho rằng, rất c&oacute; thể nh&agrave; l&atilde;nh đạo Kim Jong-un đ&atilde; thấy h&agrave;i l&ograve;ng với tiến bộ của chương tr&igrave;nh hạt nh&acirc;n n&ecirc;n kh&ocirc;ng cần phải thử th&ecirc;m nữa.</p>\n\n<p>Hoặc c&oacute; thể, chương tr&igrave;nh vũ kh&iacute; của Triều Ti&ecirc;n đ&atilde; chuyển từ giai đoạn &ldquo;thử nghiệm&rdquo; sang &ldquo;chế tạo&rdquo; v&agrave; nước n&agrave;y đang &ldquo;dự trữ vũ kh&iacute; v&agrave; t&ecirc;n lửa&rdquo;, Jeffrey Lewis, chuy&ecirc;n gia h&agrave;ng đầu về t&ecirc;n lửa Triều Ti&ecirc;n nhận định.</p>\n\n<p>Nh&agrave; ph&acirc;n t&iacute;ch Catherine Dill tại Viện Nghi&ecirc;n cứu quốc tế Middlebury ở Monterey (Mỹ) khẳng định: &ldquo;Cam kết dừng c&aacute;c vụ thử hạt nh&acirc;n v&agrave; t&ecirc;n lửa kh&ocirc;ng đồng nghĩa với việc Triều Ti&ecirc;n ph&aacute; bỏ to&agrave;n bộ chương tr&igrave;nh n&agrave;y. T&iacute;n hiệu n&ecirc;u tr&ecirc;n cho thấy, Triều Ti&ecirc;n đ&atilde; đạt được độ ch&iacute;n muồi trong c&ocirc;ng nghệ v&agrave; t&ecirc;n lửa&rdquo;.&nbsp;</p>\n\n<p>Th&ugrave;y Dương</p>\n', '1524542685', '', 2, 0),
(75, 'Quan hệ Nga – Anh đang xuống mức thấp nhất', '234434-1192737.jpg', 'Đại sứ quán Nga tại Anh bày tỏ sự hối tiếc khi quan hệ hai nước trở nên căng thẳng từ sau vụ Skripal.', '<p>24/04/2018 - 06:57 (GMT+7)</p>\n\n<h2>Đại sứ qu&aacute;n Nga tại Anh b&agrave;y tỏ sự hối tiếc khi quan hệ hai nước trở n&ecirc;n căng thẳng từ sau vụ Skripal.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"1192737\" src=\"http://cdn.baogiaothong.vn/files/trang.nguyenthu/2018/04/23/234434-1192737.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Đại sứ qu&aacute;n Nga tại Anh</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Đại sứ qu&aacute;n Nga n&oacute;i rằng quan hệ song phương giữa hai b&ecirc;n đ&atilde; đến mức thấp nhất kể từ khi Ch&iacute;nh phủ Anh đổ lỗi cho Nga v&igrave; vụ đầu độc cựu điệp vi&ecirc;n Sergei Skripal; đồng thời b&agrave;y tỏ sự hối tiếc v&igrave; Quốc hội Anh đang bị cuốn v&agrave;o một chiến dịch m&agrave; c&oacute; thể l&agrave;m mối quan hệ hai nước c&agrave;ng trở n&ecirc;n tồi tệ hơn.</p>\n\n<p>Theo đ&oacute;, Đại sứ qu&aacute;n đ&atilde; được nghe qua th&ocirc;ng tin về việc c&aacute;c nghị Anh sẽ th&agrave;nh lập một nh&oacute;m nhằm đối ph&oacute; với &quot;những đe doạ, tấn c&ocirc;ng&quot; từ Nga đối với Anh. Nh&oacute;m n&agrave;y sẽ do Chủ tịch Ủy ban Ngoại giao Tom Tugendhat đứng đầu v&agrave; c&oacute; sự tham gia của c&aacute;c th&agrave;nh vi&ecirc;n quốc hội.</p>\n\n<p>Ph&iacute;a Nga b&agrave;y tỏ sự tiếc nuối khi thay v&igrave; t&igrave;m kiếm c&aacute;ch để kh&ocirc;i phục quan hệ Nga &ndash; Anh, Quốc hội nước n&agrave;ylại cho ph&eacute;p m&igrave;nh bị l&ocirc;i k&eacute;o v&agrave;o một chiến dịch mới chống Nga do những th&agrave;nh vi&ecirc;n bảo thủ đưa ra.</p>\n\n<p>Những căng thẳng giữa Nga v&agrave; Anh bắt đầu b&ugrave;ng nổ kể từ sau&nbsp;<a href=\"http://www.baogiaothong.vn/nga-nghi-ngo-tinh-xac-thuc-cua-tuyen-bo-thay-mat-cho-yulia-skripal-d251672.html\">vụ đầu độc cựu điệp vi&ecirc;n Skripal</a>&nbsp;hồi th&aacute;ng 3. Ch&iacute;nh quyền Anh ngay lập tức c&aacute;o buộc Nga c&oacute; li&ecirc;n quan v&agrave;o vụ việc d&ugrave; chưa c&oacute; bằng chứng. Ph&iacute;a Nga cũng đưa ra những đ&aacute;p trả gay gắt, l&agrave;m tổn hại đến quan hệ hai nước</p>\n\n<p><a href=\"http://www.baogiaothong.vn/Thu-Trang-(Theo-TASS)-tag/\"><strong>Thu Trang (Theo TASS)</strong></a></p>\n', '1524542772', '', 2, 0),
(76, 'Tổng thư kí LHQ: \"Chiến tranh Lạnh đã trở lại\"', '234434-1192737.jpg', 'Theo ông Antonio Guterres, Chiến tranh Lạnh giai đoạn hiện nay khác với quá khứ rất nhiều.', '<p>23/04/2018 - 12:13 (GMT+7)</p>\n\n<h2>Theo &ocirc;ng Antonio Guterres, Chiến tranh Lạnh giai đoạn hiện nay kh&aacute;c với qu&aacute; khứ rất nhiều.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"4579167\" src=\"http://cdn.baogiaothong.vn/files/trang.nguyenthu/2018/04/23/092220-4579167.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Tổng thư k&yacute; LHQ Antonio Guterres</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Trong một cuộc phỏng vấn với k&ecirc;nh truyền h&igrave;nh SVT của Thuỵ Điển, Tổng thư k&yacute; LHQ Antonio Guterres cho biết &ocirc;ng tin rằng hiện tại thế giới đang chứng kiến một cuộc Chiến tranh Lạnh kh&aacute;c.</p>\n\n<p>Tuy nhi&ecirc;n, &ocirc;ng thấy rằng c&oacute; một số sự kh&aacute;c biệt giữa cuộc chiến n&agrave;y trong qu&aacute; khứ v&agrave; với những g&igrave; ch&uacute;ng ta đang chứng kiến b&acirc;y giờ.</p>\n\n<p>Thứ nhất, &ocirc;ng cho rằng cuộc chiến lần n&agrave;y kh&ocirc;ng c&ograve;n hai khối r&otilde; r&agrave;ng như trước.</p>\n\n<p>&quot;Mỹ v&agrave; Nga kh&ocirc;ng kiểm so&aacute;t tất cả mọi người như họ đ&atilde; từng l&agrave;m&rdquo;, &ocirc;ng n&oacute;i. &quot;Ch&uacute;ng ta c&oacute; một số quốc gia rất t&iacute;ch cực trong khu vực như Thổ Nhĩ Kỳ, Iran, Arab Saudi v&agrave; những nước kh&aacute;c. R&otilde; r&agrave;ng l&agrave; kh&ocirc;ng c&oacute; hai khối đồng nhất được kiểm so&aacute;t bởi quốc gia đứng đầu n&agrave;o đ&oacute;.&quot;</p>\n\n<p>Điều thứ hai m&agrave; &ocirc;ng n&ecirc;u ra l&agrave; c&aacute;c cơ chế đối thoại, đ&agrave;m ph&aacute;n khi xuất hiện những nguy cơ đối đầu hoặc xung đột c&oacute; khả năng kh&ocirc;ng thể kiểm so&aacute;t.</p>\n\n<p>Theo &ocirc;ng cơ chế n&agrave;y ng&agrave;y nay kh&ocirc;ng c&ograve;n tồn tại. V&agrave; đ&oacute; l&agrave; l&yacute; do khiến t&igrave;nh h&igrave;nh hiện tại trở n&ecirc;n nguy hiểm.</p>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"syria-12-1523672656412456736409\" src=\"http://cdn.baogiaothong.vn/files/trang.nguyenthu/2018/04/19/104301-syria-12-1523672656412456736409.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Cuộc tấn c&ocirc;ng v&agrave;o Syria h&ocirc;m 14/4 khiến quan hệ c&aacute;c nước trở n&ecirc;n căng thẳng</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Nhưng &ocirc;ng Guterres b&agrave;y tỏ rằng &ocirc;ng cảm thấy hạnh ph&uacute;c v&igrave; những căng thẳng n&agrave;y đang &ldquo;nguội&rdquo; dần đi.</p>\n\n<p>&quot;T&ocirc;i hy vọng rằng c&oacute; thể t&igrave;m thấy một sự đồng thuận trong tương lai để những t&igrave;nh huống như tuần trước kh&ocirc;ng c&ograve;n lặp lại.&quot;</p>\n\n<p>Điều m&agrave; Tổng thư k&iacute; LHQ muốn n&oacute;i tới ch&iacute;nh l&agrave; vụ tấn c&ocirc;ng v&agrave;o Syria của&nbsp;<a href=\"http://www.baogiaothong.vn/sau-cuoc-khong-kich-tong-thong-phap-lai-muon-xay-dung-mot-syria-moi-d253216.html\">li&ecirc;n minh Anh, Ph&aacute;p, Mỹ</a>&nbsp;v&agrave;o ng&agrave;y 14/4 vừa qua.</p>\n\n<p>Cuộc tấn c&ocirc;ng đ&atilde; l&agrave;m dấy l&ecirc;n lo ngại rằng Thế chiến thứ 3 c&oacute; thể xảy ra nếu c&aacute;c b&ecirc;n tham gia tiếp tục c&oacute; &yacute; định tấn c&ocirc;ng hay đ&aacute;p trả.</p>\n\n<p>Tuy nhi&ecirc;n, c&oacute; vẻ như t&igrave;nh h&igrave;nh đ&atilde; ổn định trở lại khi c&aacute;c nước đang nỗ lực x&uacute;c tiến những cuộc đối thoại trong tương lai.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/Thu-Trang-(Theo-TASS)-tag/\"><strong>Thu Trang (Theo TASS</strong></a></p>\n', '1524542863', '', 2, 0),
(77, 'Tổng thống Trump đang \"đặt cược\" vào Triều Tiên', '162618-bnye234_trumpq_gr_20180409205950.jpg', 'Ông Trump cho rằng cuộc gặp với ông Kim Jong-un vẫn có thể không thành công.', '<p>23/04/2018 - 07:25 (GMT+7)</p>\n\n<h2>&Ocirc;ng Trump cho rằng cuộc gặp với &ocirc;ng Kim Jong-un vẫn c&oacute; thể kh&ocirc;ng th&agrave;nh c&ocirc;ng.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"BNYE234_TRUMPQ_GR_20180409205950\" src=\"http://cdn.baogiaothong.vn/files/trang.nguyenthu/2018/04/14/162618-bnye234_trumpq_gr_20180409205950.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Tổng thống Mỹ Donald Trump</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Trước cuộc gặp với l&atilde;nh đạo Triều Ti&ecirc;n Kim Jong-un, Tổng thống Mỹ Donald Trump nhận x&eacute;t rằng những nỗ lực để giải quyết căng thẳng giữa hai b&ecirc;n vẫn c&oacute; thể kh&ocirc;ng đạt được kết quả.</p>\n\n<p>&Ocirc;ng đ&atilde; thừa nhận tr&ecirc;n trang Twitter của m&igrave;nh rằng: &ldquo;C&oacute; thể mọi thứ sẽ th&agrave;nh c&ocirc;ng, c&oacute; thể kh&ocirc;ng&rdquo; khi n&oacute;i về cuộc gặp được mong chờ nhất hiện nay.</p>\n\n<p>Cuộc họp sắp tới sẽ đem đến cho Mỹ cơ hội đề nghị th&aacute;o bỏ chương tr&igrave;nh hạt nh&acirc;n của Triều Ti&ecirc;n.</p>\n\n<p>Trước đ&oacute;, &ocirc;ng Trump đ&atilde; b&agrave;y tỏ nếu cuộc gặp n&agrave;y đạt được nhưng mục ti&ecirc;u hai bến hướng tơi, n&oacute; sẽ mang lại&nbsp;<a href=\"http://www.baogiaothong.vn/trump-hop-voi-ong-kim-se-la-thanh-cong-mang-tinh-toan-cau-d252752.html\">&quot;th&agrave;nh c&ocirc;ng mang t&iacute;nh to&agrave;n cầu&rdquo;</a>.</p>\n\n<p>Về ph&iacute;a Triều Ti&ecirc;n, nước n&agrave;y cũng vừa đưa ra tuy&ecirc;n bố sẽ dừng ngay lập tức việc thử t&ecirc;n lửa v&agrave; hạt nh&acirc;n. Động th&aacute;i n&agrave;y đ&atilde; nhận được sự hoan ngh&ecirc;nh của nhiều nước tr&ecirc;n thế giới.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/Thu-Trang-tag/\"><strong>Thu Trang</strong></a></p>\n', '1524542939', '', 2, 0),
(78, 'TT Pháp nói với ông Putin: Cuộc tấn công vào Syria là hợp pháp', '220458-1192736.jpg', 'Tổng thống Pháp cũng bày tỏ sẵn sàng cho một cuộc đối thoại tại Syria.', '<h2>Tổng thống Ph&aacute;p cũng b&agrave;y tỏ sẵn s&agrave;ng cho một cuộc đối thoại tại Syria.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"1192736\" src=\"http://cdn.baogiaothong.vn/files/trang.nguyenthu/2018/04/23/220458-1192736.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Tổng thống Ph&aacute;p Emmanuel Macron</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Tổng thống Ph&aacute;p Emmanuel Macron đ&atilde; c&oacute; cuộc điện đ&agrave;m với người đống cấp nước Nga &ocirc;ng Vladimir Putin v&agrave;o h&ocirc;m thứ Hai.</p>\n\n<p>Trong cuộc tr&ograve; chuyện, &ocirc;ng Macron tiếp tục khẳng định cuộc tấn c&ocirc;ng bằng t&ecirc;n lửa v&agrave;o Syria do li&ecirc;n qu&acirc;n Anh, Ph&aacute;p, Mỹ tiến h&agrave;nh l&agrave; hợp ph&aacute;p. Theo &ocirc;ng, h&agrave;nh động đ&oacute; l&agrave; để đảm bảo rằng luật ph&aacute;p quốc tế được thực thi.</p>\n\n<p>Tổng thống Ph&aacute;p cũng nhấn mạnh rằng mục ti&ecirc;u của phương T&acirc;y l&agrave; ph&aacute; vỡ bất kỳ kế hoạch sản xuất v&agrave; cất giữ vũ kh&iacute; h&aacute;o học n&agrave;o.</p>\n\n<p>V&igrave; vậy, việc Damascus b&iacute; mật duy tr&igrave; chương tr&igrave;nh n&agrave;y l&agrave; tr&aacute;i với cam kết của Syria với Tổ chức Cấm vũ kh&iacute; ho&aacute; học (OPCW).</p>\n\n<p>&quot;Cần phải nỗ lực hết m&igrave;nh để chấm dứt chương tr&igrave;nh h&oacute;a học của Syria v&agrave; sự đ&agrave;n &aacute;p đối với người d&acirc;n của m&igrave;nh&quot;, &ocirc;ng Macron khẳng định.</p>\n\n<p>Tuy nhi&ecirc;n, nh&agrave; l&atilde;nh đạo Ph&aacute;p cũng cho biết Paris sẵn s&agrave;ng nỗ lực t&iacute;ch cực để x&acirc;y dựng đối thoại về h&ograve;a b&igrave;nh ở Syria, v&agrave; đặc biệt sẵn l&ograve;ng hợp t&aacute;c với Moscow.</p>\n\n<p>Cuộc tấn c&ocirc;ng v&agrave;o Syria h&ocirc;m 14/4 do 3 nước phương T&acirc;y,&nbsp;<a href=\"http://www.baogiaothong.vn/phap-phai-dung-ke-hoach-du-phong-trong-cuoc-khong-kich-syria-d252831.html\">trong đ&oacute; c&oacute; Ph&aacute;p</a>, tiến h&agrave;nh sau khi c&aacute;o buộc ch&iacute;nh quyền Assad c&oacute; sử dụng vũ kh&iacute; ho&aacute; học. Mặc d&ugrave; chưa c&oacute; bằng chứng x&aacute;c thực nhưng cuộc tấn c&ocirc;ng vẫn được tiến h&agrave;nh.</p>\n\n<p>Cho đến nay, mặc d&ugrave; chịu nhiều chỉ tr&iacute;ch về quyết định của m&igrave;nh, nhưng &ocirc;ng Macron vẫn lu&ocirc;n khẳng định m&igrave;nh đ&atilde; l&agrave;m đ&uacute;ng để bảo vệ h&agrave;o b&igrave;nh của Syria v&agrave; thế giới.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/Thu-Trang-(Theo-TASS)-tag/\"><strong>Thu Trang (Theo TASS)</strong></a></p>\n', '1524543010', '', 2, 0),
(79, 'OnePlus 6 sẽ ra mắt vào tháng 5, có giá từ 604 USD', '072023-3-15228133815691670417362.jpg', 'Nhiều nguồn tin cho rằng OnePlus sẽ chính thức trình làng dòng sản phẩm mới của mình vào tháng 5 tới.', '<p>24/04/2018 - 07:55 (GMT+7)</p>\n\n<h2>Nhiều nguồn tin cho rằng OnePlus sẽ ch&iacute;nh thức tr&igrave;nh l&agrave;ng d&ograve;ng sản phẩm mới của m&igrave;nh v&agrave;o th&aacute;ng 5 tới.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"3-15228133815691670417362\" src=\"http://cdn.baogiaothong.vn/files/huyentrang.nguyen/2018/04/24/072023-3-15228133815691670417362.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>OnePlus 6 đ&atilde; lộ diện cấu h&igrave;nh, thiết kế với phi&ecirc;n bản nắp lưng gỗ độc đ&aacute;o.</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Hiện tại, c&aacute;c th&ocirc;ng tin về thiết kế cũng như cấu h&igrave;nh của OnePlus 6 gần như đ&atilde; lộ diện ho&agrave;n to&agrave;n. Những g&igrave; người d&ugrave;ng quan t&acirc;m chỉ c&ograve;n l&agrave; thời điểm ra mắt v&agrave; gi&aacute; b&aacute;n của m&aacute;y.</p>\n\n<p>Trước đ&oacute;, nhiều nguồn tin cho rằng OnePlus sẽ ch&iacute;nh thức tr&igrave;nh l&agrave;ng d&ograve;ng sản phẩm mới của m&igrave;nh v&agrave;o th&aacute;ng 5 tới để kịp &ldquo;ăn theo&rdquo; bom tấn Avengers: Infinity War với những phi&ecirc;n bản giới hạn đặc biệt. V&agrave; mới đ&acirc;y, tờ MoneyControl đ&atilde; bất ngờ h&eacute; lộ OnePlus 6 sẽ ra mắt v&agrave;o ng&agrave;y 18/5 tại thị trường Ấn Độ với mức gi&aacute; 604 USD (13.7 triệu đồng).</p>\n\n<p>Về th&ocirc;ng số kỹ thuật, chắc chắn m&aacute;y sẽ sử dụng vi xử l&yacute; Snapdragon 845 của Qualcomm, d&ugrave;ng RAM l&ecirc;n đến 8 GB. OnePlus cũng tuy&ecirc;n bố sẽ mang đến một trải nghiệm &quot;nhanh như bay&quot; tr&ecirc;n chiếc điện thoại n&agrave;y. M&aacute;y sẽ c&oacute; m&agrave;n h&igrave;nh tr&agrave;n viền với phần notch như iPhone X, diện t&iacute;ch hiển thị chiếm 90% mặt trước.</p>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"1_2385064\" src=\"http://cdn.baogiaothong.vn/files/huyentrang.nguyen/2018/04/24/072102-1_2385064.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>H&igrave;nh ảnh r&ograve; rỉ được cho l&agrave; OnePlus 6. Ảnh: Android Authority.</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Đ&aacute;ng ch&uacute; &yacute;, 604 USD mới chỉ l&agrave; mức gi&aacute; d&agrave;nh cho phi&ecirc;n bản OnePlus 6 rẻ nhất với lượng RAM v&agrave; ROM kh&aacute; &ldquo;khi&ecirc;m tốn&rdquo;. Trong khi đ&oacute;, để sở hữu cấu h&igrave;nh mạnh nhất gồm 8GB RAM v&agrave; 256GB ROM, chắc chắn người d&ugrave;ng to&agrave;n cầu (bao gồm cả Ấn Độ) sẽ phải bỏ ra một khoản tiền cao hơn rất nhiều.</p>\n\n<p>Hiện tại, OnePlus đ&atilde; bắt đầu chiến dịch quảng b&aacute; cho model mới của m&igrave;nh trong c&aacute;c rạp chiếu phim tại Ấn Độ. Ngo&agrave;i ra, h&atilde;ng cũng đ&atilde; hợp t&aacute;c với Marvel của Disney để sản xuất những phi&ecirc;n bản OnePlus 6 giới hạn theo chủ đề Avengers: Infinity War, dự kiến ra mắt tại một số nước ch&acirc;u &Acirc;u trong thời gian tới.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/Huy%E1%BB%81n-Trang-(TH)-tag/\"><strong>Huyền Trang (TH</strong></a></p>\n', '1524543068', '', 5, 0),
(80, 'Apple sẽ loại bỏ thứ này trên iPhone SE 2?', '164215-photo-1-15242735291831113261780.jpg', 'iPhone SE 2 được dự đoán sẽ không mang thiết kế của iPhone X và Apple sẽ loại bỏ thứ này trên thân nó.', '<p>21/04/2018 - 17:15 (GMT+7)</p>\n\n<h2>iPhone SE 2 được dự đo&aacute;n sẽ kh&ocirc;ng mang thiết kế của iPhone X v&agrave; Apple sẽ loại bỏ thứ n&agrave;y tr&ecirc;n th&acirc;n n&oacute;.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"photo-1-15242735291831113261780\" src=\"http://cdn.baogiaothong.vn/files/nam.pham/2018/04/21/164215-photo-1-15242735291831113261780.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><strong><em>Ảnh chụp được cho l&agrave; khung của iPhone SE 2.</em></strong></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Theo&nbsp;Global Sources Mobile Electronics, tới đ&acirc;y nhiều khả năng<a href=\"http://www.baogiaothong.vn/iphone-2018-co-the-se-ho-tro-ban-2-sim-d252678.html\">&nbsp;iPhone SE 2&nbsp;</a>ra mắt sẽ kh&ocirc;ng mang thiết kế của iPhone X. N&oacute; vẫn sẽ được trang bị Touch ID v&agrave; &quot;buồn b&atilde;&quot; hơn l&agrave; m&agrave;n h&igrave;nh 4 inch kh&ocirc;ng hề thay đổi.</p>\n\n<p>Mặc d&ugrave; vậy, phi&ecirc;n bản đặc biệt của SE 2 sẽ theo bước ch&acirc;n c&aacute;c phi&ecirc;n bản mới ra mắt gần đ&acirc;y của Apple khi loại bỏ ho&agrave;n to&agrave;n jack cắm tai nghe 3.5 mm, cho ph&eacute;p Apple giảm k&iacute;ch thước smartphone xuống mức thấp hơn.</p>\n\n<p>Ngo&agrave;i ra, iPhone SE 2 cũng c&oacute; thể chuyển đổi cấu tr&uacute;c nh&ocirc;m của bản tiền nhiệm để chuyển sang sử dụng k&iacute;nh mới hơn, điều n&agrave;y c&oacute; thể dẫn đến khả năng hỗ trợ sạc kh&ocirc;ng d&acirc;y. Về sức mạnh, m&aacute;y sẽ được vận h&agrave;nh bởi chip xử l&yacute; A10 Fusion m&agrave; c&ocirc;ng ty đ&atilde; ra mắt trong d&ograve;ng iPhone 7.&nbsp;</p>\n\n<p>Hiện ph&iacute;a Apple vẫn chưa x&aacute;c nhận bất cứ th&ocirc;ng tin n&agrave;o li&ecirc;n quan tới sản phẩm iPhone SE 2.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/H.-Nam-tag/\"><strong>H. Nam</strong></a></p>\n', '1524543134', '', 5, 0),
(81, 'Apple \"tung\" tin vui cho những người dùng Macbook Pro 13 inch', '165426-mac_1200x675_800x450_800x450.jpg', 'Apple mới đây đã khởi động lại chương trình thay thế pin miễn phí cho các máy Macbook Pro 13 inch (không Touch Bar).', '<p>21/04/2018 - 18:04 (GMT+7)</p>\n\n<h2>Apple mới đ&acirc;y đ&atilde; khởi động lại chương tr&igrave;nh thay thế pin miễn ph&iacute; cho c&aacute;c m&aacute;y Macbook Pro 13 inch (kh&ocirc;ng Touch Bar).</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"mac_1200x675_800x450_800x450\" src=\"http://cdn.baogiaothong.vn/files/nam.pham/2018/04/21/165426-mac_1200x675_800x450_800x450.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Apple mới đ&acirc;y đ&atilde; khởi động lại chương tr&igrave;nh thay thế pin miễn ph&iacute; cho c&aacute;c m&aacute;y Macbook Pro 13 inch (kh&ocirc;ng Touch Bar).</em></h2>\n\n			<h2><em><strong>&nbsp;</strong></em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Theo DailyMail, Apple mới đ&acirc;y đ&atilde; cho khởi động lại chương tr&igrave;nh&nbsp;thay thế miễn ph&iacute; pin của c&aacute;c&nbsp;<a href=\"http://www.baogiaothong.vn/tin-vui-cho-cac-tin-do-thich-dung-macbook-air-cua-apple-d247301.html\">m&aacute;y Macbook Pro</a>&nbsp;13 inch (kh&ocirc;ng Touch Bar) bị lỗi từ ph&iacute;a nh&agrave; sản xuất.&nbsp;Những m&aacute;y nằm trong diện được thay thế được sản xuất trong giai đoạn từ th&aacute;ng 10/2016 đến th&aacute;ng 10/2017 (đ&atilde; được b&aacute;n ra tr&ecirc;n to&agrave;n cầu).</p>\n\n<p>Apple cũng khẳng định sẽ thay thế pin cho Macbook Pro trong thời gian 5 năm kể từ khi b&aacute;n ra (tới khoảng năm 2022). Apple cho biết việc thay thế sẽ kh&ocirc;ng ảnh hưởng tới chế độ bảo h&agrave;nh của m&aacute;y.</p>\n\n<p>Để kiểm tra xem m&aacute;y của m&igrave;nh c&oacute; nằm trong diện được thay thế pin miễn ph&iacute; hay kh&ocirc;ng, người d&ugrave;ng c&oacute; thể kiểm tra m&atilde; sản phẩm (Serial Number) tr&ecirc;n trang web của Apple (https://www.apple.com/support/13inch-macbookpro-battery-replacement/).</p>\n\n<p>Nếu đủ điều kiện hợp lệ, người d&ugrave;ng c&oacute; thể đem m&aacute;y tới c&aacute;c trung t&acirc;m dịch vụ ủy quyền của Apple (Apple Authorized Service Provider) hoặc c&aacute;c cửa h&agrave;ng b&aacute;n lẻ Apple Store. Ngo&agrave;i ra, m&aacute;y cũng c&oacute; thể được gửi qua bưu điện tới trung t&acirc;m sửa chữa của Apple.&nbsp;</p>\n\n<p>Điểm đặc biệt l&agrave; những người đủ điều kiện thay đ&atilde; từng mất tiền để thay thế, sửa chữa loại pin n&agrave;y sẽ được Apple ho&agrave;n trả lại tiền.&nbsp;Trước đ&oacute;, nhiều người d&ugrave;ng Macbook Pro 13 inch (kh&ocirc;ng c&oacute; Touch Bar) đ&atilde; gặp phải t&igrave;nh trạng pin bị phồng do lỗi của nh&agrave; sản xuất.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/H.-Nam-tag/\"><strong>H. Nam</strong></a></p>\n', '1524543186', '', 5, 0);
INSERT INTO `article` (`id_article`, `title`, `image`, `brief_content`, `content`, `created_date`, `modified_date`, `id_category`, `id_author`) VALUES
(82, 'Buýt trợ giá đi ngoại thành Hà Nội dần hút khách', '051220-4.jpg', 'Chiều 21/4, trực tiếp trải nghiệm trên tuyến buýt trợ giá số 108 lộ trình BX Thường Tín - Minh Tân (Phú Xuyên) mới được khai trương đầu tháng 2/2018, ghi nhận của PV Báo Giao thông, lượng hành khách khá đông.', '<p>24/04/2018 - 07:25 (GMT+7)</p>\n\n<p>&nbsp;</p>\n\n<h2>Chiều 21/4, trực tiếp trải nghiệm tr&ecirc;n tuyến bu&yacute;t trợ gi&aacute; số 108 lộ tr&igrave;nh BX Thường T&iacute;n - Minh T&acirc;n (Ph&uacute; Xuy&ecirc;n) mới được khai trương đầu th&aacute;ng 2/2018, ghi nhận của PV B&aacute;o Giao th&ocirc;ng, lượng h&agrave;nh kh&aacute;ch kh&aacute; đ&ocirc;ng.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"4\" src=\"http://cdn.baogiaothong.vn/files/Baogiay/2018/04/24/051220-4.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em><strong>Xe bu&yacute;t kh&ocirc;ng trợ gi&aacute; số 212 BX Thường T&iacute;n - Minh T&acirc;n (Ph&uacute; Xuy&ecirc;n) c&oacute; &ldquo;ngoại h&igrave;nh&rdquo; kh&ocirc;ng kh&aacute;c xe kh&aacute;ch nhưng gi&aacute; th&agrave;nh rẻ hơn nhiều</strong></em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Xe được thiết kế nhỏ gọn&nbsp;với&nbsp;29 chỗ, tr&ecirc;n&nbsp;chuyến xe xuất ph&aacute;t l&uacute;c 15h, h&agrave;nh kh&aacute;ch đ&atilde; k&iacute;n chỗ. D&ugrave; vậy, PV&nbsp;vẫn cảm nhận sự sạch sẽ, m&aacute;t mẻ do tr&ecirc;n xe c&oacute; lắp điều h&ograve;a v&agrave; được phụ xe qu&eacute;t dọn trước mỗi chuyến đi.&nbsp;Tr&ecirc;n&nbsp;xe&nbsp;c&ograve;n c&oacute;&nbsp;bảng nội quy&nbsp;rất&nbsp;cụ thể&nbsp;v&agrave; ni&ecirc;m yết&nbsp;số điện thoại đường d&acirc;y n&oacute;ng để&nbsp;h&agrave;nh kh&aacute;ch&nbsp;phản &aacute;nh.</p>\n\n<p>Phụ xe Nguyễn Xu&acirc;n Hiếu chia sẻ,&nbsp;tuyến 108 c&oacute; cự ly tr&ecirc;n 31km với 10 xe, gi&aacute; v&eacute; 9.000 đồng/h&agrave;nh kh&aacute;ch/lượt. Xe hoạt động từ 4h25 s&aacute;ng đến 21h h&agrave;ng ng&agrave;y.&nbsp;&ldquo;Từ khi hoạt động đến nay, lượng kh&aacute;ch đi xe cũng nhiều hơn mỗi ng&agrave;y. Th&ocirc;ng thường, mỗi chuyến c&oacute; khoảng 30 - 40 kh&aacute;ch&rdquo;, anh Hiếu th&ocirc;ng tin.</p>\n\n<table>\n	<tbody>\n		<tr>\n			<td>\n			<p><strong>Tuyến bu&yacute;t số 108 BX Thường T&iacute;n - Minh T&acirc;n (Ph&uacute; Xuy&ecirc;n) c&oacute; lộ tr&igrave;nh chiều đi: BX Thường T&iacute;n - QL1 - tỉnh lộ 428 - Minh T&acirc;n (b&atilde;i đỗ xe bu&yacute;t x&atilde; Minh T&acirc;n); Chiều về: Minh T&acirc;n - Tỉnh lộ 428 - QL1 - BX Thường T&iacute;n. &nbsp;</strong></p>\n\n			<p><strong>Tuyến bu&yacute;t 212 BX Mỹ Đ&igrave;nh - Quế V&otilde; c&oacute; lộ tr&igrave;nh: BX Mỹ Đ&igrave;nh - Phạm H&ugrave;ng - Phạm Văn Đồng - cầu Thăng Long - V&otilde; Văn Kiệt - QL18 - KCN Y&ecirc;n Phong (Đường YP3, YP2) - QL18 - QL1 - cầu vượt Bồ Sơn - Nguyễn Tr&atilde;i - L&yacute; Th&aacute;i Tổ - Trần Hưng Đạo - cầu vượt Đại Ph&uacute;c - QL18 - BX Quế V&otilde; v&agrave; ngược lại.</strong></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Anh Trần Văn Lộc ở Tri Thủy, Ph&uacute; Xuy&ecirc;n chia sẻ: &ldquo;T&ocirc;i&nbsp;l&agrave;m ở BX Gi&aacute;p B&aacute;t, trước đ&acirc;y thường phải d&ugrave;ng xe m&aacute;y đi l&agrave;m. Từ ng&agrave;y c&oacute; tuyến bu&yacute;t 108,&nbsp;t&ocirc;i&nbsp;thường đi v&eacute; th&aacute;ng, 2 tuyến bu&yacute;t l&agrave; đến chỗ l&agrave;m, rất tiện lợi m&agrave; cũng kh&ocirc;ng lo muộn giờ v&igrave; xe&nbsp;bu&yacute;t được quản l&yacute; nghi&ecirc;m ngặt về giờ giấc&rdquo;.</p>\n\n<p>Trong khi đ&oacute;, tuyến bu&yacute;t 212 kh&ocirc;ng trợ gi&aacute; c&oacute; lộ tr&igrave;nh BX Mỹ Đ&igrave;nh đi Quế V&otilde; (Bắc Ninh) khai trương cuối th&aacute;ng 3 cũng đang dần lấy được nhiều thiện cảm của người d&acirc;n.</p>\n\n<p>Xe c&oacute; sức chứa 42 người, như một xe kh&aacute;ch chất lượng cao với kh&ocirc;ng gian xe tho&aacute;ng đ&atilde;ng, c&oacute; hệ thống wifi tốc độ cao phục vụ h&agrave;nh kh&aacute;ch. Theo bạn Đinh Mạnh Tuyến (sinh vi&ecirc;n ĐH H&agrave; Nội, nh&agrave; ở gần BX Bắc Ninh): &ldquo;L&acirc;u rồi mới cảm nhận m&igrave;nh l&agrave; &ldquo;thượng đế&rdquo;, đồng tiền bỏ ra xứng đ&aacute;ng. Nh&acirc;n vi&ecirc;n rất lịch thiệp, kh&aacute;ch xuống giữa chặng đều được tận t&igrave;nh hướng dẫn&rdquo;.</p>\n\n<p>Trao đổi với&nbsp;B&aacute;o Giao th&ocirc;ng, &ocirc;ng Nguyễn Ho&agrave;ng Hải, Gi&aacute;m đốc Trung t&acirc;m Quản l&yacute; v&agrave; điều h&agrave;nh giao th&ocirc;ng đ&ocirc; thị H&agrave; Nội cho biết, tuyến 108, 212 l&agrave; hai trong số 14 tuyến bu&yacute;t H&agrave; Nội đ&atilde; khai trương trong năm nay. Mục ti&ecirc;u của H&agrave; Nội&nbsp;l&agrave;&nbsp;mở rộng v&ugrave;ng phục vụ xe bu&yacute;t c&oacute; trợ gi&aacute; để đ&aacute;p ứng tốt nhất nhu cầu đi lại của nh&acirc;n d&acirc;n.&nbsp;</p>\n\n<p>N&oacute;i về tuyến bu&yacute;t kế cận số 212 BX&nbsp;Mỹ Đ&igrave;nh - BX Quế V&otilde;, &ocirc;ng Hải cho rằng, đ&acirc;y l&agrave; tuyến bu&yacute;t kế cận đầu ti&ecirc;n khai th&aacute;c phục vụ nhu cầu đi lại của nh&acirc;n d&acirc;n theo hướng từ BX Mỹ Đ&igrave;nh qua cầu Thăng Long dọc theo QL18 kết nối KCN Y&ecirc;n Phong với KCN Quế V&otilde;, qua TP Bắc Ninh đến BX Quế V&otilde; v&agrave; ngược lại. Đối với tuyến xe bu&yacute;t kh&ocirc;ng trợ gi&aacute; n&agrave;y, c&oacute; 20 xe bu&yacute;t mới 100%, nh&atilde;n hiệu Samco, sức chở 42 h&agrave;nh kh&aacute;ch, sản xuất mới năm 2018 theo ti&ecirc;u chuẩn kh&iacute; thải Euro IV. Xe tr&ecirc;n tuyến hoạt động li&ecirc;n tục trong ng&agrave;y từ 5h - 19h với tần suất 15 - 20 ph&uacute;t/chuyến. Gi&aacute; v&eacute; được t&iacute;nh tuỳ chặng với c&aacute;c mức 10.000 - 30.000 - 35.000 (theo chặng) v&agrave; to&agrave;n tuyến: 40.000 đồng/lượt.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/L%C3%AA-T%C6%B0%C6%A1i-Nam-Kh%C3%A1nh-tag/\"><strong>L&ecirc; Tươi - Nam Kh&aacute;nh</strong></a></p>\n', '1524543239', '', 5, 0),
(83, 'Báo Giao thông phát hành ấn phẩm đặc biệt', '053304-5.jpg', 'Báo Giao thông phát hành ấn phẩm đặc biệt với chủ đề “Kỳ vọng người dẫn đầu”.', '<p>24/04/2018 - 06:39 (GMT+7)</p>\n\n<h2>B&aacute;o Giao th&ocirc;ng ph&aacute;t h&agrave;nh ấn phẩm đặc biệt với chủ đề &ldquo;Kỳ vọng người dẫn đầu&rdquo;.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"5\" src=\"http://cdn.baogiaothong.vn/files/Baogiay/2018/04/24/053304-5.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em><strong>B&aacute;o Giao th&ocirc;ng ph&aacute;t h&agrave;nh ấn phẩm đặc biệt với chủ đề &ldquo;Kỳ vọng người dẫn đầu&rdquo;.</strong></em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Nh&acirc;n dịp Giỗ Tổ H&ugrave;ng Vương (10/3 &acirc;m lịch), kỷ niệm 43 năm Giải ph&oacute;ng miền Nam thống nhất đất nước (30/4/1975 - 30/4/2018) v&agrave; Quốc tế Lao động 1/5, B&aacute;o Giao th&ocirc;ng ph&aacute;t h&agrave;nh ấn phẩm đặc biệt với chủ đề &ldquo;Kỳ vọng người dẫn đầu&rdquo;.</p>\n\n<p>43 năm sau ng&agrave;y giải ph&oacute;ng v&agrave; hơn 30 năm thực hiện đổi mới ph&aacute;t triển c&ugrave;ng đất nước, TP.HCM lu&ocirc;n thể hiện được vị tr&iacute; của trung t&acirc;m kinh tế khi đ&oacute;ng g&oacute;p hơn 22% GDP v&agrave; khoảng 28% tổng thu ng&acirc;n s&aacute;ch Quốc gia.&nbsp;Bằng Nghị quyết 54 ng&agrave;y 24/11/2017, Quốc hội đ&atilde; cho ph&eacute;p TP.HCM thực hiện th&iacute; điểm cơ chế, ch&iacute;nh s&aacute;ch đặc th&ugrave;. Đ&oacute; được xem như nguồn sung lực mới, mở ra cơ hội đột ph&aacute; ph&aacute;t triển cho trung t&acirc;m kinh tế lớn nhất nước.</p>\n\n<p>Trong số b&aacute;o đặc biệt n&agrave;y c&oacute; nhiều b&agrave;i viết đ&aacute;ng ch&uacute; &yacute; như: Những đột ph&aacute; đầu ti&ecirc;n của TP.HCM sau khi c&oacute; cơ chế đặc th&ugrave;; H&igrave;nh h&agrave;i đ&ocirc; thị th&ocirc;ng minh đầu ti&ecirc;n của cả nước; Đột ph&aacute; đầu tư hạ tầng giao th&ocirc;ng nhờ cơ chế mới.</p>\n\n<p>Ngo&agrave;i ra, số b&aacute;o c&ograve;n c&oacute; nhiều b&agrave;i viết chia sẻ hồi ức một thời h&agrave;o h&ugrave;ng của những cựu chiến binh như: Anh h&ugrave;ng l&aacute;i xe chở 1.000 tấn vũ kh&iacute; vượt Trường Sơn; K&yacute; ức ng&agrave;y giải ph&oacute;ng của cựu binh tự chặt tay bị thương chiến đấu tiếp...</p>\n\n<p>B&ecirc;n cạnh đ&oacute;, bạn đọc sẽ cảm nhận được những c&acirc;u chuyện cảm động qua c&aacute;c b&agrave;i viết: Kỳ c&ocirc;ng đưa hoa ban qu&yacute; Điện Bi&ecirc;n về Thủ đ&ocirc;; Lớp học tiếng Anh của ch&agrave;ng trai khuyết tật người T&agrave;y; Cảm động &ldquo;đội qu&acirc;n&rdquo; m&aacute;u hiếm cứu người&hellip; v&agrave; c&aacute;c trang thơ, truyện ngắn, văn h&oacute;a thể thao, quốc tế.</p>\n\n<p>B&aacute;o Giao th&ocirc;ng&nbsp;số đặc biệt gộp c&aacute;c số: 67, 68, 69, 70 v&agrave; Cuối tuần 17, ra thứ tư ng&agrave;y 24/4/2018. B&aacute;o in 4 m&agrave;u, gi&aacute; b&igrave;a 22.300 đồng.</p>\n\n<p>Trong thời gian ph&aacute;t h&agrave;nh số b&aacute;o đặc biệt, tin tức thời sự trong v&agrave; ngo&agrave;i ng&agrave;nh sẽ được cập nhật tr&ecirc;n B&aacute;o Giao th&ocirc;ng điện tử tại địa chỉ www.baogiaothong.vn.</p>\n\n<p>B&aacute;o Giao th&ocirc;ng sẽ tiếp tục phục vụ độc giả với số b&aacute;o 71 ra thứ tư ng&agrave;y 2/5/2018.&nbsp;</p>\n\n<p><a href=\"http://www.baogiaothong.vn/BGT-tag/\"><strong>BGT</strong></a></p>\n', '1524543378', '', 7, 0),
(84, 'Nẫng 40 tấn thép tại công trường dự án nghìn tỷ', '105227-untitled.jpg', '11 đối tượng đã bị bắt, khởi tố do liên quan đến vụ mất trộm 40 tấn thép tại công trường dự án xây dựng trang trại chăn nuôi lợn công nghệ cao tại Nghệ An.', '<p>24/04/2018 - 11:07 (GMT+7)</p>\n\n<p>&nbsp;</p>\n\n<h2>11 đối tượng đ&atilde; bị bắt, khởi tố do li&ecirc;n quan đến vụ mất trộm 40 tấn th&eacute;p tại c&ocirc;ng trường dự &aacute;n x&acirc;y dựng trang trại chăn nu&ocirc;i lợn c&ocirc;ng nghệ cao tại Nghệ An.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"Untitled\" src=\"http://cdn.baogiaothong.vn/files/thanh.ngo/2018/04/24/105227-untitled.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>15 tấn th&eacute;p tang vật của vụ trộm đ&atilde; được c&ocirc;ng an thu giữ&nbsp;<br />\n			(ảnh minh họa)</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Chiều ng&agrave;y 23/4, Đại t&aacute; Nguyễn Mạnh H&ugrave;ng &ndash; Ph&oacute; gi&aacute;m đốc C&ocirc;ng an tỉnh Nghệ An đ&atilde; thay mặt ban gi&aacute;m đốc, trao thưởng n&oacute;ng số tiền 5 triệu đồng cho Ban Chuy&ecirc;n &aacute;n 318T c&ocirc;ng an huyện Quỳ Hợp. Ban chuy&ecirc;n &aacute;n lập th&agrave;nh t&iacute;ch khi kh&aacute;m ph&aacute; th&agrave;nh c&ocirc;ng vụ &ldquo;Trộm cắp sắt th&eacute;p tại Trang trại chăn nu&ocirc;i lợn c&ocirc;ng nghệ cao của C&ocirc;ng ty Masan Nutri Farm&rdquo; tại x&atilde; Hạ Sơn, huyện Quỳ Hợp. Đ&acirc;y được xem l&agrave; vụ trộm cắp lớn nhất từ trước tới nay, xảy ra ở c&ocirc;ng trường x&acirc;y dựng.</p>\n\n<p>Theo cơ quan điều tra, v&agrave;o th&aacute;ng 3/2018, khu vực x&acirc;y dựng Trang trại chăn nu&ocirc;i lợn của C&ocirc;ng ty Masan Nutri Farm (tại x&oacute;m Cồn Sơn, x&atilde; Hạ Sơn, huyện Quỳ Hợp) c&oacute; hiện tượng mất cắp vật tư x&acirc;y dựng. Qu&aacute; tr&igrave;nh điều tra x&aacute;c minh v&agrave; l&agrave;m việc với c&aacute;c b&ecirc;n li&ecirc;n quan, C&ocirc;ng an huyện Quỳ Hợp bước đầu x&aacute;c định số lượng vật tư x&acirc;y dựng (th&eacute;p) bị mất l&ecirc;n đến 40 tấn, tổng trị gi&aacute; khoảng 1,2 tỷ đồng.</p>\n\n<p>Nhận thấy đ&acirc;y l&agrave; vụ &aacute;n rất nghi&ecirc;m trọng, c&oacute; t&iacute;nh chất phức tạp, li&ecirc;n quan đến một trong số dự &aacute;n lớn đang được đầu tư ở tỉnh Nghệ An, với tổng mức đầu tư ban đầu l&ecirc;n đến hơn 1.000 tỷ đồng; số t&agrave;i sản bị mất với số lượng lớn. V&igrave; vậy, ng&agrave;y 18/3/2018 Cơ quan CSĐT C&ocirc;ng an huyện Quỳ Hợp đ&atilde; x&aacute;c lập chuy&ecirc;n &aacute;n 318T để đấu tranh l&agrave;m r&otilde;.</p>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"31138129_2057556474463561_6838566204227977216_n\" src=\"http://cdn.baogiaothong.vn/files/thanh.ngo/2018/04/24/105225-31138129_2057556474463561_6838566204227977216_n.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>L&atilde;nh đạo C&ocirc;ng an tỉnh Nghệ An trao thưởng n&oacute;ng cho ban chuy&ecirc;n &aacute;n</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Sau thời gian điều tra, thu thập t&agrave;i liệu, chứng cứ, ng&agrave;y 03/04/2018, Cơ quan CSĐT C&ocirc;ng an huyện Quỳ Hợp đ&atilde; x&aacute;c định 3 nh&oacute;m đối tượng chuy&ecirc;n Trộm cắp t&agrave;i sản v&agrave; 1 nh&oacute;m đối tượng chuy&ecirc;n ti&ecirc;u thụ t&agrave;i sản do phạm tội m&agrave; c&oacute;. Đồng thời, thực hiện bắt giữ 11 đối tượng li&ecirc;n quan, thu giữ 2 &ocirc; t&ocirc;, hơn 15 tấn th&eacute;p tang vật (c&oacute; trị gi&aacute; khoảng 450 triệu đồng) v&agrave; c&aacute;c vật chứng kh&aacute;c.</p>\n\n<p>Tại cơ quan điều tra, c&aacute;c đối tượng đều khai nhận h&agrave;nh vi phạm tội của bản th&acirc;n v&agrave; đồng bọn. Sau khi tập hợp đủ c&aacute;c t&agrave;i liệu, tang vật chứng minh h&agrave;nh vi phạm tội, Cơ quan CSĐT C&ocirc;ng an huyện Quỳ Hợp đ&atilde; tiến h&agrave;nh khởi tố điều tra 4 vụ &aacute;n, 11 bị can về c&aacute;c tội danh &ldquo;Trộm cắp t&agrave;i sản&rdquo; v&agrave; &ldquo;Ti&ecirc;u thụ t&agrave;i sản do người kh&aacute;c phạm tội m&agrave; c&oacute;&rdquo;, đồng thời, &aacute;p dụng biện ph&aacute;p ngăn chặn &ldquo;tạm giam&rdquo; đối với 11 bị can.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/V%C4%83n-Thanh-tag/\"><strong>Văn Thanh</strong></a></p>\n', '1524543428', '', 7, 0),
(85, 'Khói thuốc bám trên quần áo cũng có thể phá hủy nội tạng', '063747-thuocla1.jpg', 'Thưa bác sĩ, em vừa nghe thông tin rằng khói thuốc lá bám trên quần áo cũng có thể phá hủy nội tạng.', '<p>24/04/2018 - 06:34 (GMT+7)</p>\n\n<p>&nbsp;</p>\n\n<h2>Thưa b&aacute;c sĩ, em vừa nghe th&ocirc;ng tin rằng kh&oacute;i thuốc l&aacute; b&aacute;m tr&ecirc;n quần &aacute;o cũng c&oacute; thể ph&aacute; hủy nội tạng.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"thuocla1\" src=\"http://cdn.baogiaothong.vn/files/loan.le/2018/04/24/063747-thuocla1.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em>Kh&oacute;i thuốc l&aacute; đang trở n&ecirc;n nguy hại hơn bao giờ hết, khi c&aacute;c h&oacute;a chất c&ograve;n b&aacute;m tr&ecirc;n vật dụng xung quanh cũng c&oacute; thể g&acirc;y hại cho sức khỏe.</em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p><strong>Hỏi:</strong>&nbsp;Thưa b&aacute;c sĩ, em vừa nghe th&ocirc;ng tin rằng kh&oacute;i thuốc l&aacute; b&aacute;m tr&ecirc;n quần &aacute;o cũng c&oacute; thể ph&aacute; hủy nội tạng. Vậy thực hư th&ocirc;ng tin n&agrave;y c&oacute; ch&iacute;nh x&aacute;c?</p>\n\n<p><strong>Nguyễn Quỳnh An</strong>&nbsp;(Nam Từ Li&ecirc;m, H&agrave; Nội)</p>\n\n<p><strong>Trả lời:</strong></p>\n\n<p>Th&ocirc;ng tin bạn tiếp nhận l&agrave; ch&iacute;nh x&aacute;c. Kh&oacute;i thuốc l&aacute; đang trở n&ecirc;n nguy hại hơn bao giờ hết, khi c&aacute;c h&oacute;a chất c&ograve;n b&aacute;m tr&ecirc;n vật dụng xung quanh cũng c&oacute; thể g&acirc;y hại cho sức khỏe.</p>\n\n<p>T&aacute;c hại của thuốc l&aacute; đến sức khỏe&nbsp;đ&atilde; được khoa học chứng minh từ l&acirc;u. V&agrave;o giữa thập ni&ecirc;n 1980, ch&uacute;ng ta c&ograve;n biết th&ecirc;m rằng việc h&uacute;t thuốc thụ động (do h&iacute;t phải kh&oacute;i từ người h&uacute;t thuốc) cũng g&acirc;y t&aacute;c hại rất lớn, thậm ch&iacute; l&agrave; lớn hơn h&uacute;t chủ động.</p>\n\n<p>Tưởng chỉ c&oacute; vậy, thế nhưng h&oacute;a ra việc h&uacute;t thuốc thụ động c&ograve;n một dạng kh&aacute;c nữa. Thế giới gọi đ&oacute; l&agrave; third-hand smoke (THS - tạm dịch: kh&oacute;i lần 3), nhằm &aacute;m chỉ dư lượng kh&oacute;i thuốc c&ograve;n b&aacute;m tr&ecirc;n những đồ xung quanh: r&egrave;m cửa, thảm, nội thất, quần &aacute;o...</p>\n\n<p>V&agrave; nay, một nghi&ecirc;n cứu mới đ&atilde; chỉ ra rằng dư lượng kh&oacute;i ấy ho&agrave;n to&agrave;n c&oacute; thể tiếp tục l&agrave;m hại con người. Theo đ&oacute;, kể cả khi kh&oacute;i thuốc đ&atilde; tan, lượng h&oacute;a chất c&ograve;n tồn đọng - như nitrosamine - sẽ tiếp tục l&agrave; một vấn đề nan giải. Nếu như được t&iacute;ch tụ qua thời gian, ch&uacute;ng ho&agrave;n to&agrave;n c&oacute; thể g&acirc;y ra một loại&nbsp;ung thư&nbsp;ho&agrave;n to&agrave;n mới.</p>\n\n<p>Theo &ocirc;ng Manuela Martins-Green, nh&agrave; nghi&ecirc;n cứu từ ĐH California (Mỹ), họ đ&atilde; thử nghiệm với chuột. Cụ thể, sau khi cho c&aacute;c vật liệu ngập trong kh&oacute;i thuốc để lưu lại THS, nh&oacute;m nghi&ecirc;n cứu đặt ch&uacute;ng v&agrave;o trong chuồng nu&ocirc;i. Trong v&ograve;ng 6 th&aacute;ng, c&aacute;c chuy&ecirc;n gia thực hiện x&eacute;t nghiệm n&atilde;o, gan v&agrave; huyết thanh của chuột cho thấy c&oacute; sự thay đổi về hormone, khả năng kh&aacute;ng insulin, hệ ti&ecirc;u h&oacute;a, v&agrave; c&aacute;c dấu hiệu tổn thương hệ thần kinh, nội tạng.</p>\n\n<p>Trưởng ph&ograve;ng Y tế&nbsp;dự ph&ograve;ng v&agrave; c&aacute;c chương tr&igrave;nh y tế (Cục Y tế GTVT)</p>\n\n<p><a href=\"http://www.baogiaothong.vn/BS.-%C4%90%E1%BA%B7ng-Qu%E1%BB%91c-Kh%C3%A1nh-tag/\"><strong>BS. Đặng Quốc Kh&aacute;nh</strong></a></p>\n', '1524543525', '1524543794', 45, 0),
(86, 'Barca vô địch Champions League, Roma có kế hoạch đặc biệt đối phó Salah', '103240-u19-barca.JPG', 'Barca vô địch Champions League, AS Roma có kế hoạch đặc biệt đối phó Salah là những tin chính đáng chú ý sáng 24/4.', '<p>24/04/2018 - 10:38 (GMT+7)</p>\n\n<h2>Barca v&ocirc; địch Champions League, AS Roma c&oacute; kế hoạch đặc biệt đối ph&oacute; Salah l&agrave; những tin ch&iacute;nh đ&aacute;ng ch&uacute; &yacute; s&aacute;ng 24/4.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"u19-barca\" src=\"http://cdn.baogiaothong.vn/files/doai.tran/2018/04/24/103240-u19-barca.JPG\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><strong>U19 Barca v&ocirc; địch UEFA Youth League</strong></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p><strong>Barca v&ocirc; địch Champions League</strong></p>\n\n<p>Đ&ecirc;m qua, U19 Barca đ&atilde; đ&aacute;nh bại U19 Chelsea 3-0 trong trận chung kết U19&nbsp;<strong><a href=\"http://www.baogiaothong.vn/the-thao.html\" target=\"_blank\">Champions League</a></strong>&nbsp;(UEFA Youth League) để l&ecirc;n ng&ocirc;i v&ocirc; địch giải đấu n&agrave;y.</p>\n\n<p><strong>Strootman cảnh b&aacute;o Salah</strong></p>\n\n<p>Tiền vệ Kevin Strootman cho biết AS Roma đ&atilde; c&oacute; một chiến thuật đặc biệt để đối ph&oacute; với Mohamed Salah v&agrave; Liverpool ở b&aacute;n kết Champions League đ&ecirc;m nay. &ldquo;Đặc sản ph&ograve;ng ngự Italy, đ&oacute; l&agrave; những g&igrave; t&ocirc;i hy vọng l&uacute;c n&agrave;y&quot;, Kevin Strootman n&oacute;i.</p>\n\n<p><strong>Klopp tin Liverpool sẽ lột x&aacute;c khi đại chiến Roma</strong></p>\n\n<p>HLV Klopp tin đội b&oacute;ng của &ocirc;ng sẽ kh&ocirc;ng bị ảnh hưởng bởi trận h&ograve;a 2-2 với West Brom tại Ngoại hạng Anh cuối tuần qua. Nh&agrave; cầm qu&acirc;n người Đức tin c&aacute;c học tr&ograve; sẽ l&agrave;m tốt hơn v&agrave; lột x&aacute;c khi đại chiến Roma.</p>\n\n<p><strong>Salah y&ecirc;u Roma, nhưng sẽ chơi hết m&igrave;nh v&igrave; Liverpool</strong></p>\n\n<p>Salah tiết lộ anh vẫn n&oacute;i chuyện với đồng đội cũ ở AS Roma h&agrave;ng ng&agrave;y v&agrave; d&agrave;nh nhiều t&igrave;nh cảm cho đội b&oacute;ng n&agrave;y. Tuy nhi&ecirc;n anh sẽ thi đấu hết m&igrave;nh để c&ugrave;ng Liverpool v&ocirc; địch Champions League.</p>\n\n<p><strong>HLV Barca c&oacute; thể ra đi</strong></p>\n\n<p>Theo tờ AS, HLV Valverde c&oacute; thể ra đi sau m&ugrave;a giải n&agrave;y sau khi bất đồng với ban l&atilde;nh đạo Barca sau trận thua AS Roma ở tứ kết Champions League. Trước trận chung kết C&uacute;p Nh&agrave; vua với Sevilla, một th&agrave;nh vi&ecirc;n BLĐ Barca đ&atilde; tiết lộ th&ocirc;ng tin với b&aacute;o ch&iacute; rằng cựu HLV Bilbao sẽ bị sa thải nếu đội nh&agrave; thua trận.</p>\n\n<p><strong>Mikel Arteta được tiến cử l&agrave;m HLV của Arsenal</strong></p>\n\n<p>Gi&aacute;m đốc điều h&agrave;nh của Arsenal, Ivan Gazidis &nbsp;đ&aacute;nh gi&aacute; cao Mikel Arteta, cựu đội trưởng Ph&aacute;o thủ cho vị tr&iacute; HLV trưởng thay Arsene Wenger trong khi GĐTT của đội b&oacute;ng n&agrave;y lại chọn Luis Enrique.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/V.T-tag/\"><strong>V.T</strong></a></p>\n', '1524543611', '1524543771', 4, 0),
(87, 'Châu Âu kêu gọi tẩy chay World Cup tại Nga', '064735-24.jpg', 'Tờ Express đưa tin, các thành viên nghị viện châu Âu (MEP) kêu gọi chính phủ các nước châu Âu tẩy chay World Cup 2018 tổ chức tại Nga để phản đối những hành động của chính quyền Moscow.', '<p>24/04/2018 - 09:49 (GMT+7)</p>\n\n<h2>Tờ Express đưa tin, c&aacute;c th&agrave;nh vi&ecirc;n nghị viện ch&acirc;u &Acirc;u (MEP) k&ecirc;u gọi ch&iacute;nh phủ c&aacute;c nước ch&acirc;u &Acirc;u tẩy chay World Cup 2018 tổ chức tại Nga để phản đối những h&agrave;nh động của ch&iacute;nh quyền Moscow.</h2>\n\n<table>\n	<tbody>\n		<tr>\n			<td><img alt=\"24\" src=\"http://cdn.baogiaothong.vn/files/Baogiay/2018/04/24/064735-24.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<h2><em><strong>Ch&acirc;u &Acirc;u k&ecirc;u gọi tẩy chay World Cup tại Nga</strong></em></h2>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Trong l&aacute; thư, c&aacute;c nghị sĩ cho rằng, việc đầu độc điệp vi&ecirc;n ở Salisbury; việc n&eacute;m bom v&agrave;o trường học, bệnh viện v&agrave; c&aacute;c khu d&acirc;n cư ở Syria đều c&oacute; b&agrave;n tay của Nga. B&ecirc;n cạnh đ&oacute;, MEP c&ograve;n l&ecirc;n &aacute;n cuộc chiến Nga ph&aacute;t động nhằm v&agrave;o Ukraine v&agrave; nhiều động th&aacute;i kh&aacute;c g&acirc;y bất ổn EU.</p>\n\n<p>Được biết, Thủ tướng Anh Theresa May đ&atilde; ra lệnh cấm tất cả c&aacute;c quan chức Anh tới dự lễ khai mạc World Cup 2018. C&aacute;c quan chức của Iceland, Australia, Nhật Bản, Đan Mạch, Ba Lan v&agrave; Thụy Điển cũng tẩy chay World Cup năm nay. Theo lịch, World Cup 2018 diễn ra ở Nga từ 14/6 đến 15/7.</p>\n\n<p><a href=\"http://www.baogiaothong.vn/Thanh-Huy%E1%BB%81n-tag/\"><strong>Thanh Huyền</strong></a></p>\n', '1524543676', '', 4, 0);

